// NPM IMPORTS
import assert from 'assert'
import { format } from 'util'
import _ from 'lodash'

// COMMON IMPORTS
import T      from '../../../node_modules/devapt-core-common/dist/js/utils/types'
import Stream from '../../../node_modules/devapt-core-common/dist/js/messaging/stream'

// BROWSER IMPORTS
import BindingStream          from './binding_stream'
import BindingServiceTimeline from './binding_service_timeline'
import BindingService         from './binding_service'


const context = 'browser/base/binding_Loader'



/**
 * @file UI component binding loader class.
 * 
 * @author Luc BORIES
 * 
 * @license Apache-2.0
 */
export default class BindingLoader
{
	/**
	 * Creates an instance of BindingLoader.
	 * 
	 * @returns {nothing}
	 */
	constructor()
	{
	}



	/**
	 * Normalize an array of objects selectors in an array of objects.
	 * @static
	 * 
	 * @param {RuntimeBase} arg_runtime - client runtime.
	 * @param {Component} arg_component - component instance.
	 * @param {array} arg_selectors - selectors strings array.
	 * @param {array|string} arg_dom_types - selected items types strings array or single string (default "dom").
	 * 
	 * @returns {array} - objects|strings array.
	 */
	static normalize_objects(arg_runtime, arg_component, arg_selectors, arg_types = 'dom')
	{
		arg_component.enter_group('normalize_objects')

		if ( ! T.isArray(arg_selectors) )
		{
			arg_component.leave_group('normalize_objects:bad selectors array')
			return []
		}

		const objects = []
		arg_selectors.forEach(
			(selector, index)=>{
				if ( ! T.isString(selector) )
				{
					console.warn('normalize_objects:component=%s:bad selector type=%s', arg_component.get_name(), typeof selector)
					return
				}

				// DEBUG
				// console.log(context + ':normalize_objects:component=%s:binding target dom selector=%s', arg_component.get_name(), dom_selector)

				const object_type = T.isString(arg_types) ? arg_types : ( T.isArray(arg_types) && arg_types.length > index ? arg_types[index] : arg_types[arg_types.length - 1])

				if (object_type == 'view')
				{
					if (selector == 'this')
					{
						arg_component.debug('normalize_objects:view:this is found')
						objects.push( arg_component)
						return
					}

					arg_component.debug('normalize_objects:view:' + selector)
					const target_object = arg_runtime._ui.get(selector)
					if (target_object)
					{
						objects.push(target_object)
						return
					}

					console.warn(context + ':normalize_objects:component=%s:bad view selector=%s', arg_component.get_name(), selector)
					return
				}

				if (object_type == 'jquery')
				{
					if (selector == 'this')
					{
						arg_component.debug('normalize_objects:jquery:this is found')
						objects.push( $( arg_component.get_dom_id() ) )
						return
					}
					
					arg_component.debug('normalize_objects:jquery:' + selector)
					const jqo = $(selector)
					if (jqo && jqo.length > 0)
					{
						objects.push(jqo)
						return
					}

					console.warn(context + ':normalize_objects:component=%s:bad jquery selector=%s', arg_component.get_name(), selector, jqo)
					return
				}

				if (object_type == 'dom')
				{
					if (selector == 'this')
					{
						arg_component.debug('normalize_objects:dom:this is found')
						objects.push( arg_component.get_dom_element() )
						return
					}

					arg_component.debug('normalize_objects:dom:' + selector)
					const element = document.getElementById(selector)
					if (element)
					{
						objects.push(element)
						return
					}

					const selection = document.querySelector(selector)
					if (selection)
					{
						objects.push(selection)
						return
					}

					console.warn(context + ':normalize_objects:component=%s:bad dom selector=%s', arg_component.get_name(), selector)
					return
				}

				if (object_type == 'delegate')
				{
					objects.push(selector)
					return
				}

				console.warn(context + ':normalize_objects:component=%s:bad selector type=%s for selector=%s', arg_component.get_name(), object_type, selector)
			}
		)
		
		arg_component.leave_group('normalize_objects')
		return objects
	}
	
	

	/**
	 * Load and apply a component binding configuration.
	 * 
	 * @param {string} arg_id - binding identifier.
	 * @param {RuntimeBase} arg_runtime - client runtime.
	 * @param {Component} arg_component - component instance.
	 * @param {Immutable.Map|undefined} arg_binding_cfg - component binding configuration.
	 * 
	 * @returns {BindingStream|array} 
	 */
	static load(arg_id, arg_runtime, arg_component, arg_binding_cfg)
	{
		// console.info(context + ':load:loading binding for component ' + arg_component.get_name(), arg_binding_cfg)
		
		// CHECK BINDING CONFIGURATION
		if (! T.isObject(arg_binding_cfg) )
		{
			return
		}
		
		// GET CONFIGURATION ATTRIBUTES
		const type = ('type' in arg_binding_cfg) ? arg_binding_cfg['type'] : undefined
		const state_path = ('state_path' in arg_binding_cfg) ? arg_binding_cfg['state_path'] : ['bindings_values', arg_id]
		let xform = ('transform' in arg_binding_cfg) ? arg_binding_cfg['transform'] : undefined
		const options = ('options' in arg_binding_cfg) ? arg_binding_cfg['options'] : undefined
		
		const source_svc_name   = ('service' in arg_binding_cfg) ? arg_binding_cfg['service'] : undefined
		const source_svc_method = ('method' in arg_binding_cfg) ? arg_binding_cfg['method'] : undefined
		const source_timeline   = ('timeline' in arg_binding_cfg) ? arg_binding_cfg['timeline'] : undefined
		const source_stream     = ('source_stream' in arg_binding_cfg) ? arg_binding_cfg['source_stream'] : undefined
		// const source_event      = ('event' in arg_binding_cfg) ? arg_binding_cfg['event'] : undefined
		const source_dom_event = ('dom_event' in arg_binding_cfg) ? arg_binding_cfg['dom_event'] : undefined
		
		// SOURCES
		const source_type = ('source_type' in arg_binding_cfg) ? arg_binding_cfg['source_type'] : undefined
		const source_types = ('source_types' in arg_binding_cfg) ? arg_binding_cfg['source_types'] : (source_type ? [source_type] : undefined)

		const source_selector = ('source_selector' in arg_binding_cfg) ? arg_binding_cfg['source_selector'] : undefined
		const source_selectors = ('source_selectors' in arg_binding_cfg) ? arg_binding_cfg['source_selectors'] : (source_selector ? [source_selector] : undefined)

		// TARGETS
		const target_type = ('target_type' in arg_binding_cfg) ? arg_binding_cfg['target_type'] : undefined
		const target_types = ('target_types' in arg_binding_cfg) ? arg_binding_cfg['target_types'] : (target_type ? [target_type] : undefined)

		const target_selector = ('target_selector' in arg_binding_cfg) ? arg_binding_cfg['target_selector'] : undefined
		const target_selectors = ('target_selectors' in arg_binding_cfg) ? arg_binding_cfg['target_selectors'] : (target_selector ? [target_selector] : undefined)

		// METHOD
		const target_method  = ('target_method' in arg_binding_cfg)  ? arg_binding_cfg['target_method']  : undefined
		const target_methods = ('target_methods' in arg_binding_cfg) ? arg_binding_cfg['target_methods'] : undefined
		
		// NORMALIZE SOURCES
		// console.log(context + ':load:component=%s:binding type=%s:source_selectors&source_types=', arg_component.get_name(), type, source_selectors, source_types)
		const sources = source_selectors ? this.normalize_objects(arg_runtime, arg_component, source_selectors, source_types) : undefined

		// NORMALIZE TARGETS
		// console.log(context + ':load:component=%s:binding type=%s:target_selectors&target_types=', arg_component.get_name(), type, target_selectors, target_types)
		const targets = target_selectors ? this.normalize_objects(arg_runtime, arg_component, target_selectors, target_types) : undefined

		// GET STARTING VALUE
		let starting_value = undefined
		if ( T.isArray(state_path) && state_path.length > 0 )
		{
			starting_value = arg_component.get_state_value(state_path, undefined)
			if (! starting_value)
			{
				starting_value = arg_runtime.get_state_store().get_state().getIn(state_path, undefined)
				if (starting_value && starting_value.toJS)
				{
					starting_value = starting_value.toJS()
				}
			}
		}


		// BIND SOURCES AND TARGETS
		console.log(context + ':load:component=%s:binding type=%s', arg_component.get_name(), type)
		switch(type)
		{
			case 'timeline': {
				assert( T.isString(source_timeline),       context + format(':load:component=%s:bad timeline name=%s',            arg_component.get_name(), source_timeline) )
				assert( T.isString(source_svc_name),       context + format(':load:component=%s:bad service name=%s,timeline=%s', arg_component.get_name(), source_timeline, source_svc_name) )
				assert( T.isString(source_svc_method),     context + format(':load:component=%s:bad service name=%s,timeline=%s', arg_component.get_name(), source_timeline, source_svc_method) )
				assert( T.isArray(targets) && targets.length > 0, context + format(':load:component=%s,timeline=%s:bad targets',  arg_component.get_name(), source_timeline, source_svc_method) )
				assert( T.isString(target_method),  context + format(':load:component=%s,timeline=%s:bad target method=%s',       arg_component.get_name(), source_timeline, target_method) )
				
				return new BindingServiceTimeline(arg_id, arg_runtime, arg_component)
					.set_state_path(state_path)
					.set_starting_value(starting_value)
					.set_source_service_name(source_svc_name)
					.set_source_service_method(source_svc_method)
					.set_source_timeline_name(source_timeline)
					.set_targets_instances_array(targets)
					.set_target_method_name(target_method)
					.set_options(options)
					.build()
			}

			case 'service': {
				assert( T.isString(source_svc_name),       context + format(':load:component=%s:bad service name=%s',  arg_component.get_name(), source_svc_name) )
				assert( T.isString(source_svc_method),     context + format(':load:component=%s:bad method name=%s',   arg_component.get_name(), source_svc_method) )
				assert( T.isArray(targets) && targets.length > 0, context + format(':load:component=%s:bad targets',   arg_component.get_name()) )
				assert( T.isString(target_method),  context + format(':load:component=%s:bad target method=%s',        arg_component.get_name(), target_method) )

				return new BindingService(arg_id, arg_runtime, arg_component)
					.set_state_path(state_path)
					.set_starting_value(starting_value)
					.set_source_service_name(source_svc_name)
					.set_source_service_method(source_svc_method)
					.set_source_transformation(xform)
					.set_targets_instances_array(targets)
					.set_target_method_name(target_method)
					.set_options(options)
					.build()
			}

			case 'emitter_jquery': {
				assert( T.isArray(sources),         context + format(':load:component=%s:bad sources array=%s',       arg_component.get_name()) )
				assert( T.isArray(targets) && targets.length > 0, context + format(':load:component=%s:bad targets',  arg_component.get_name()) )
				assert( T.isString(source_dom_event),  context + format(':load:component=%s:bad source event=%s',     arg_component.get_name(), source_dom_event) )
				assert( T.isString(target_method),  context + format(':load:component=%s:bad target method=%s',       arg_component.get_name(), target_method) )
				
				const streams = []
				sources.forEach(
					(jqo)=>{
						const stream = new Stream( jqo.asEventStream(source_dom_event) )
						streams.push(stream)
					}
				)
				
				console.log(context + ':load:component=%s:binding type=%s:event=%s:sources=', arg_component.get_name(), type, source_dom_event, sources)
				
				if (!xform)
				{
					xform = (event)=>{
						const method_cfg = T.isObject(options) ? options.method : undefined
						const operands   = T.isObject(method_cfg) ? method_cfg.operands : undefined
						
						console.log(context + ':load:component=%s:binding type=%s:event=%s:on handler:data=', arg_component.get_name(), type, source_dom_event, operands)
						
						return {
							is_event_handler:true,
							component_name:undefined,
							event_name:source_dom_event,
							dom_selector:undefined,
							target:event.target,
							data:operands
						}
					}
				}

				return new BindingStream(arg_id, arg_runtime, arg_component)
					.set_stream(streams)
					.set_state_path(state_path)
					.set_source_transformation(xform)
					.set_targets_instances_array(targets)
					.set_target_method_name(target_method)
					.set_options(options)
					.build()
			}

			case 'emitter_dom': {
				assert( T.isArray(sources), context + format(':load:component=%s:bad sources array=%s',               arg_component.get_name()) )
				assert( T.isArray(targets) && targets.length > 0, context + format(':load:component=%s:bad targets',  arg_component.get_name()) )
				assert( T.isString(source_dom_event),  context + format(':load:component=%s:bad source event=%s',     arg_component.get_name(), source_dom_event) )
				assert( T.isString(target_method) || T.isNotEmptyArray(target_methods),  context + format(':load:component=%s:bad target method=%s:methods=%s', arg_component.get_name(), target_method, target_methods) )
				
				const data = options && options.method && options.method.operands ? options.method.operands : undefined
				const trace_enabled = false

				if ( T.isString(target_method) )
				{
					const streams = []
					sources.forEach(
						(arg_dom_selector)=>{
							const stream = new Stream()
							const handler = (component, dom_event_name, arg_selector, dom_event, dom_event_target, arg_data)=>{
								const data = {
									is_event_handler:true,
									component_name:component.get_name(),
									event_name:dom_event_name,
									dom_selector:arg_selector,
									target:dom_event_target,
									data:arg_data
								}
								
								console.log(context + ':load:component=%s:binding type=%s:event=%s:on handler:data=', arg_component.get_name(), type, source_dom_event, data)

								stream.push(data)
							}

							console.log(context + ':load:component=%s:binding type=%s:event=%s:selector=%s', arg_component.get_name(), type, source_dom_event, arg_dom_selector)
							
							arg_component.on_dom_event(source_dom_event, arg_dom_selector, handler, data, trace_enabled)
							streams.push(stream)
						}
					)
					
					// console.log(context + ':load:component=%s:binding type=%s:event=%s:sources=', arg_component.get_name(), type, source_dom_event, sources)
					
					return new BindingStream(arg_id, arg_runtime, arg_component)
						.set_stream(streams)
						.set_state_path(state_path)
						.set_source_transformation(xform)
						.set_targets_instances_array(targets)
						.set_target_method_name(target_method)
						.set_options(options)
						.build()
				}

				if ( T.isNotEmptyArray(target_methods) )
				{
					const binding_streams = []
					sources.forEach(
						(arg_dom_selector)=>{
							console.log(context + ':load:component=%s:target_methods binding type=%s:event=%s:selector=%s:methods=', arg_component.get_name(), type, source_dom_event, arg_dom_selector, target_methods)
							
							const stream = new Stream()
							const handler = (component, dom_event_name, arg_selector, dom_event, dom_event_target, arg_data)=>{
								const data = {
									is_event_handler:true,
									component_name:component.get_name(),
									event_name:dom_event_name,
									dom_selector:arg_selector,
									target:dom_event_target,
									data:arg_data
								}
								
								console.log(context + ':load:component=%s:target_methods binding type=%s:event=%s:on handler:data=', arg_component.get_name(), type, source_dom_event, data)

								stream.push(data)
							}

							arg_component.on_dom_event(source_dom_event, arg_dom_selector, handler, data, trace_enabled)
							
							_.forEach(targets,
								(target, index)=>{
									const method = index < target_methods.length ? target_methods[index] : target_methods[target_methods.length - 1]
									let method_name = undefined
									let method_operands = undefined
									if ( T.isObject(method) )
									{
										method_name = method.method
										method_operands = method.operands
									}
									else if ( T.isString(method) )
									{
										method_name = method
										if (data)
										{
											method_operands = {
												method:{
													operands:data
												}
											}
										}
									} else {
										console.warn(context + ':load:component=%s:error bad method:binding type=%s:event=%s:selector=%s:methods=%s', arg_component.get_name(), type, source_dom_event, arg_dom_selector, target_methods)
										return
									}

									console.log(context + ':load:component=%s:target_methods binding type=%s:method_name=%s:method_operands=', arg_component.get_name(), type, method_name, method_operands)

									const binding_stream = new BindingStream(arg_id, arg_runtime, arg_component)
										.set_stream(stream)
										.set_state_path(state_path)
										.set_source_transformation(xform)
										.set_targets_instances_array([target])
										.set_target_method_name(method_name)
										.set_options(method_operands)
										.build()
									binding_streams.push(binding_stream)
								}
							)
						}
					)

					return binding_streams
				}

				break
			}

			case 'stream': {
				assert( T.isObject(source_stream) && source_stream.is_stream, context + format(':load:component=%s:bad source stream', arg_component.get_name()) )
				assert( T.isArray(targets) && targets.length > 0, context + format(':load:component=%s:bad targets',  arg_component.get_name()) )
				assert( T.isString(target_method) || T.isNotEmptyArray(target_methods),  context + format(':load:component=%s:bad target method=%s',       arg_component.get_name(), target_method) )
				
				console.log(context + ':load:component=%s:target_methods binding type=%s:method_name=%s:method_operands=', arg_component.get_name(), type, target_method, options)
				
				return new BindingStream(arg_id, arg_runtime, arg_component)
					.set_stream(source_stream)
					.set_state_path(state_path)
					.set_source_transformation(xform)
					.set_targets_instances_array(targets)
					.set_target_method_name(target_methods ? target_methods : target_method)
					.set_options(options)
					.build()
			}

			default:{
				console.warn(context + ':load:component=%s:binding type=%s:type not found', arg_component.get_name(), type)
			}
		}
	}
}

// NPM IMPORTS
import assert from 'assert'
import { format } from 'util'

// COMMON IMPORTS
import T      from '../../../node_modules/devapt-core-common/dist/js/utils/types'
import Stream from '../../../node_modules/devapt-core-common/dist/js/messaging/stream'

// BROWSER IMPORTS
import BindingStream from './binding_stream'


const context = 'browser/base/binding_service'



/**
 * @file UI binding class for service stream.
 * 
 * @author Luc BORIES
 * 
 * @license Apache-2.0
 */
export default class BindingService extends BindingStream
{
	/**
	 * Creates an instance of Binding.
	 * @extends Bindable
	 *
	 * @param {string} arg_id - binding identifier.
	 * @param {RuntimeBase} arg_runtime - client runtime.
	 * @param {Component} arg_component - component instance.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_id, arg_runtime, arg_component)
	{
		super(arg_id, arg_runtime, arg_component)

		this.is_binding_service = true
	}


	
	/**
	 * Build binding.
	 * 
	 * @returns {Promise} 
	 */
	build()
	{
		this._component.enter_group('build')

		// console.log(context + ':build:loading binding for component=% and service=%s', this._component.get_name(), this._source_svc_name)
		
		assert( T.isString(this._source_svc_name),       context + format(':build:component=%s:bad service name=%s',                  this._component.get_name(), this._source_svc_name) )
		assert( T.isString(this._source_svc_method),     context + format(':build:component=%s:service=%s:bad service method=%s',     this._component.get_name(), this._source_svc_name, this._source_svc_method) )
		assert( T.isArray(this._targets) && this._targets.length > 0, context + format(':build:component=%s:service=%s:bad targets',  this._component.get_name(), this._source_svc_name) )
		assert( T.isString(this._target_method),         context + format(':build:component=%s:service=%s,bad target method=%s',      this._component.get_name(), this._source_svc_name, this._target_method) )
				
		const promise = this.bind_svc()
		
		// if ( T.isArray(this._state_path) && this._state_path.length > 0 )
		// {
		// 	const opds = { method:{ operands:[this._state_path]}}
		// 	this.set_targets_instances_array([this._component])
		// 	this.set_target_method_name('dispatch_update_state_value_action')
		// 	this.set_options(opds)
		// 	this._unsubscribe_state_update = this.bind_svc()
		// }

		this._component.leave_group('build:async')
		return promise
	}
	
	
	
	/**
	 * Bind a service stream event on object method.
	 * 
	 * @returns {Promise}
	 */
	bind_svc()
	{
		this._component.enter_group('bind_svc')

		console.info(context + ':bind_svc:loading binding for component ' + this._component.get_name())
		
		const promise = this._runtime.register_service(this._source_svc_name).then(
			(svc) => {
				this._component.enter_group('bind_svc - service found')
				
				assert( (this._source_svc_method in svc) && T.isFunction(svc[this._source_svc_method]), context + ':bind_svc - service found:bad bound method function')
				console.info(context + ':bind_svc:service found for component ' + this._component.get_name() + ' and service ' + this._source_svc_name)

				if (this._source_svc_method == 'post')
				{
					svc.subscribe() // TODO ?????
				}
				
				const method_cfg = T.isObject(this._options) ? this._options.method  : undefined
				const stream = new Stream( svc[this._source_svc_method](method_cfg) )

				if (this._source_svc_name == 'resources')
				{
					// DEBUG
					stream.get_transformed_stream().onValue(
						(values) => {
							console.log(context + ':bind_svc:resources stream:%s:values=', this._component.get_name(), values)
						}
					)
				}
				this.set_stream(stream)
				super.build()

				this._component.leave_group('bind_svc - service found')
			}
		)

		this._component.leave_group('bind_svc:async')
		return promise
	}
}

// NPM IMPORTS
import assert from 'assert'
import { format } from 'util'

// COMMON IMPORTS
import T             from '../../../node_modules/devapt-core-common/dist/js/utils/types'
import { transform } from '../../../node_modules/devapt-core-common/dist/js/utils/transform'
import Stream        from '../../../node_modules/devapt-core-common/dist/js/messaging/stream'

// BROWSER IMPORTS
import BindingStream from './binding_stream'


const context = 'browser/base/binding_service_timeline'



/**
 * @file UI binding class for service based timeline.
 * 
 * @author Luc BORIES
 * 
 * @license Apache-2.0
 */
export default class BindingServiceTimeline extends BindingStream
{
	/**
	 * Creates an instance of Binding.
	 * @extends Bindable
	 *
	 * @param {string} arg_id - binding identifier.
	 * @param {RuntimeBase} arg_runtime - client runtime.
	 * @param {Component} arg_component - component instance.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_id, arg_runtime, arg_component)
	{
		super(arg_id, arg_runtime, arg_component)

		this.is_binding_service_timeline = true
	}

	
	/**
	 * Build binding.
	 * 
	 * @returns {Promise} 
	 */
	build()
	{
		this._component.enter_group('build')

		console.info(context + ':build:loading binding for component ' + this._component.get_name(), this._stream)
		
		assert( T.isString(this._source_timeline),       context + format(':build:component=%s:bad timeline name=%s',            this._component.get_name(), this._source_timeline) )
		assert( T.isString(this._source_svc_name),       context + format(':build:component=%s:bad service name=%s,timeline=%s', this._component.get_name(), this._source_timeline, this._source_svc_name) )
		assert( T.isString(this._source_svc_method),     context + format(':build:component=%s:bad service name=%s,timeline=%s', this._component.get_name(), this._source_timeline, this._source_svc_method) )
		assert( T.isArray(this._targets) && this._targets.length > 0, context + format(':build:component=%s,timeline=%s:bad targets',  this._component.get_name(), this._source_timeline, this._source_svc_method) )
		assert( T.isString(this._target_method),         context + format(':build:component=%s,timeline=%s:bad target method=%s', this._component.get_name(), this._source_timeline, this._target_method) )
		
		let promise = this.bind_timeline()
		promise = promise.then(
			(unsubscribe)=>{
				this._unsubscribe = unsubscribe

				if ( T.isArray(this._state_path) && this._state_path.length > 0 )
				{
					const opds = { method:{ operands:[this._state_path]}}
					this.set_targets_instances_array([this._component])
					this.set_target_method_name('dispatch_update_state_value_action')
					this.set_options(opds)
					return this.bind_timeline().then(
						(unsubscribe)=>{
							this._unsubscribe_state_update = unsubscribe
						}
					)
				}
			}
		)
		
		this._component.leave_group('build:async')
		return promise
	}
	
	
	
	/**
	 * Bind a service timeline stream event on object method.
	 * 
	 * @returns {Promise}
	 */
	bind_timeline()
	{
		// console.info(context + ':bind_timeline:loading binding for component ' + this._component.get_name(), this._source_timeline)
		
		return this._runtime.register_service(this._source_svc_name).then(
			(svc) => {
				// console.log(context + ':bind_timeline:svc', svc)
				assert( (this._source_svc_method in svc) && T.isFunction(svc[this._source_svc_method]), context + ':bind_timeline:bad bound method function')
				
				if (this._source_svc_method == 'post')
				{
					svc.subscribe() // TODO ???????
				}
				
				const method_cfg = T.isObject(this._options) ? this._options.method : undefined
				const operands   = T.isObject(method_cfg) ? method_cfg.operands : undefined
				const format_cfg = T.isObject(this._options) ? this._options.format  : undefined

				assert( this._source_timeline in svc[this._source_svc_method].timelines, context + ':bind_timeline:timeline name [' + this._source_timeline + '] not found')
				assert( T.isObject( svc[this._source_svc_method].timelines[this._source_timeline] ), context + ':bind_timeline:bad timeline object')
				
				const stream = svc[this._source_svc_method].timelines[this._source_timeline].stream
				// stream.subscribe( (datas) => {console.log(context + ':bind_timeline:timeline=%s datas=', arg_timeline_name, datas) } )
				
				const unsubscribes = []
				this._targets.forEach(
					(target, index)=>{
						// const method = this._target_method
						// console.info(context + ':bind_timeline:component=%s timeline=%s target at %s method=%s', this._component.get_name(), this._source_timeline, index, method)
						
						const values_xform = {
							"result_type":"single",
							"fields":[
								{
									"name":'value at ' + index,
									"path":'value'
								}
							]
						}
						
						const datas_xform = (arg_stream_record) => {
							if (arg_stream_record.datas)
							{
								// console.log(context + ':bind_stream:datas', arg_stream_record.datas)
								return arg_stream_record.datas
							}
							return arg_stream_record
						}

						let extracted_stream = stream.map(datas_xform).map( transform(values_xform) )
						if (this._options && T.isBoolean(this._options.dispatch) && this._options.dispatch)
						{
							const at_index_xform = (arg_stream_record) => {
								if ( T.isArray(arg_stream_record) && arg_stream_record.length > index )
								{
									return arg_stream_record[index]
								}
								return undefined
							}

							extracted_stream = extracted_stream.map(at_index_xform)
						}

						// INIT STARTING VALUE
						this._stream = new Stream( extracted_stream.startWith(this._starting_value) )
						
						// DEBUG
						// this._stream.get_source_stream().onValue(
						// 	(extracted_value) => {
						// 		console.log(context + ':bind_timeline:timeline=%s method=%s stream value=', this._source_timeline, method, extracted_value)
						// 	}
						// )

						const unbind = this.bind_stream(this._stream, this._source_xform, target, this._target_method, operands, format_cfg, this._starting_value)
						unsubscribes.push(unbind)

						// if ( T.isArray(this._state_path) && this._state_path.length > 0 )
						// {
						// 	const opds = { method:{ operands:[this._state_path]}}
						// 	this._unsubscribe_state_update = this.bind_stream(this._stream, this._source_xform, [this._component], 'dispatch_update_state_value_action', opds)
						// }
					}
				)

				return ()=>{
					unsubscribes.forEach(
						(unsubscribe)=>{
							unsubscribe()
						}
					)
				}
			}
		)
	}
}

// NPM IMPORTS
import assert from 'assert'
import { format } from 'util'

// COMMON IMPORTS
import T             from '../../../node_modules/devapt-core-common/dist/js/utils/types'
import uid           from '../../../node_modules/devapt-core-common/dist/js/utils/uid.js'
import { transform } from '../../../node_modules/devapt-core-common/dist/js/utils/transform'


const context = 'browser/base/binding_stream'



/**
 * @file UI component binding class.
 * 
 * @author Luc BORIES
 * 
 * @license Apache-2.0
 */
export default class BindingStream
{
	/**
	 * Creates an instance of Binding for stream.
	 *
	 * @param {string} arg_id - binding identifier.
	 * @param {RuntimeBase} arg_runtime - client runtime.
	 * @param {Component} arg_component - component instance.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_id, arg_runtime, arg_component)
	{
		this.is_binding_stream = true

		this._id = arg_id ? arg_id : 'binding_' + uid()
		this._runtime = arg_runtime
		this._component = arg_component

		this._unsubscribe = undefined
		this._unsubscribe_state_update = undefined

		this._stream = undefined
		this._state_path = undefined
		this._starting_value = undefined
		this._source_svc_name = undefined
		this._source_svc_method = undefined
		this._source_xform = undefined
		this._source_timeline = undefined
		this._targets = undefined
		this._target_method = undefined
		this._options = undefined
	}


	
	set_stream(arg_stream)
	{
		this._stream = arg_stream
		return this
	}
	
	set_state_path(arg_state_path)
	{
		this._state_path = arg_state_path
		return this
	}
	
	set_starting_value(arg_starting_value)
	{
		this._starting_value = arg_starting_value
		return this
	}

	set_source_service_name(arg_source_svc_name)
	{
		this._source_svc_name = arg_source_svc_name
		return this
	}

	set_source_service_method(arg_source_svc_method)
	{
		this._source_svc_method = arg_source_svc_method
		return this
	}

	set_source_transformation(arg_transformation)
	{
		this._source_xform = arg_transformation
		return this
	}

	set_source_timeline_name(arg_source_timeline)
	{
		this._source_timeline = arg_source_timeline
		return this
	}

	set_targets_instances_array(arg_targets)
	{
		this._targets = arg_targets
		return this
	}

	set_target_method_name(arg_target_method)
	{
		this._target_method = arg_target_method
		return this
	}

	set_options(arg_options)
	{
		this._options = arg_options
		return this
	}
	

	
	/**
	 * Build binding.
	 * 
	 * @returns {Promise} 
	 */
	build()
	{
		console.info(context + ':build:loading binding for component ' + this._component.get_name(), this._target_method, this._stream)
		
		this._component.enter_group('build')

		// CHECK ATTRIBUTES
		if ( T.isArray(this._stream) )
		{
			this._stream.forEach(
				(stream, index)=>{
					assert( T.isObject(stream) && stream.is_stream, context + format(':build:component=%s:bad stream object at index=%s', this._component.get_name(), index) )
				}
			)
		} else {
			assert( T.isObject(this._stream) && this._stream.is_stream, context + format(':build:component=%s:bad stream object', this._component.get_name()) )
		}
		assert( T.isArray(this._targets) && this._targets.length > 0, context + format(':build:component=%s,timeline=%s:bad targets',            this._component.get_name(), this._source_timeline, this._source_svc_method) )
		assert( T.isString(this._target_method) || T.isNotEmptyArray(this._target_method), context + format(':build:component=%s,timeline=%s:bad target method=%s',   this._component.get_name(), this._source_timeline, this._target_method) )
		
		const method_cfg = T.isObject(this._options) ? this._options.method  : undefined
		const operands   = T.isObject(method_cfg)    ? method_cfg.operands   : undefined
		const format_cfg = T.isObject(this._options) ? this._options.format  : undefined
		
		const unsubscribes = []
		this._targets.forEach(
			(target, index)=>{
				const stream = T.isArray(this._stream) ? (this._stream.length > index ? this._stream[index] : this._stream[this._stream.length - 1]) : this._stream
				
				let unbind = undefined
				if ( T.isString(this._target_method) )
				{
					// console.info(context + ':build:loading binding for component ' + this._component.get_name() + ' with method=' + this._target_method)
					
					unbind = this.bind_stream(stream, this._source_xform, target, this._target_method, operands, format_cfg, this._starting_value)
					unsubscribes.push(unbind)
				}
				else if ( T.isNotEmptyArray(this._target_method) )
				{
					this._target_method.forEach(
						(method_name)=>{
							if ( T.isString(method_name) )
							{
								// console.info(context + ':build:loading binding for component ' + this._component.get_name() + ' with methods=' + method_name)
								
								unbind = this.bind_stream(stream, this._source_xform, target, method_name, operands, format_cfg, this._starting_value)
								unsubscribes.push(unbind)
							}
						}
					)
				}
			}
		)

		this._unsubscribe =  ()=>{
			unsubscribes.forEach(
				(unsubscribe)=>{
					unsubscribe()
				}
			)
		}

		if ( T.isArray(this._state_path) && this._state_path.length > 0 )
		{
			const stream = T.isArray(this._stream) ? this._stream[0] : this._stream
			if (! T.isObject(stream) || ! stream.is_stream)
			{
				console.warn(context + ':build:bad state path stream for component %s', this._component.get_name(), this._stream, stream)
				return
			}
			this._unsubscribe_state_update = this.bind_stream(stream, this._source_xform, this._component, 'dispatch_update_state_value_action', [this._state_path])
		}

		this._component.leave_group('build:async')
		return Promise.resolve()
	}
	
	
	
	/**
	 * Bind a stream to a target object action.
	 * 
	 * @param {object}    arg_stream           - stream instance.
	 * @param {string|array|function} arg_values_xform - values transformation (optional).
	 * @param {object}    arg_bound_object     - target object instance (optional: this as default).
	 * @param {string}    arg_bound_method     - target object method string.
	 * @param {anything}  arg_method_operands  - target object method operands (optional).
	 * @param {object}    arg_format_object    - formatting settings.
	 * @param {any}       arg_starting_value   - starting value.
	 * @param {object}    arg_options          - binding options.
	 * 
	 * @returns {function} - unsubscribe function.
	 */
	bind_stream(arg_stream, arg_values_xform, arg_bound_object, arg_bound_method, arg_method_operands, arg_format_object, arg_starting_value, arg_options)
	{
		// console.info(context + ':bind_stream:loading binding for component ' + this._component.get_name(), arg_bound_method, arg_method_operands, this._stream, arg_bound_object, this._source_timeline)

		let unbind_cb = undefined

		// SET TARGET OBJECT
		arg_bound_object = arg_bound_object ? arg_bound_object : this._component
		if ( T.isString(arg_bound_object) )
		{
			if ( T.isUndefined(arg_bound_method) )
			{
				arg_bound_method = arg_bound_object
				arg_bound_object = this._component
			} else if (arg_bound_object == 'this')
			{
				arg_bound_object = this._component
			}
			else
			{
				console.error(context + ':bind_stream:bad bound object string:%s', arg_bound_object)
				return undefined
			}
		}

		// TODO: DO NOT USE TYPR TO TEST DOM ELEMENTS
		// TYPR USE toString FUNCTION TO TEST TYPE against [object Object].
		// FOR OBJECT, toString give [object Object]
		// BUT DOM ELEMENTS toString GIVE [object HTMLXXXElement]
		assert( T.isObject(arg_stream) && arg_stream.is_stream, context + ':bind_stream:bad stream object')
		assert( T.isObject(arg_bound_object) || typeof arg_bound_object == 'object', context + ':bind_stream:bad bound object')
		assert( T.isString(arg_bound_method), context + ':bind_stream:bad bound method string')
		
		// CHECK OBJECT METHOD
		if ( ! (arg_bound_method in arg_bound_object) )
		{
			console.error(context + ':bind_stream:bad method [%s] for bound object=', arg_bound_method, arg_bound_object)
			return undefined
		}

		// DEBUG
		arg_stream.get_transformed_stream().onValue(
			(values) => {
				console.log(context + ':bind_stream:on initial stream:%s:bound method=%s, bound object, values', this._component.get_name(), arg_bound_method, arg_bound_object, values)
			}
		)

		// SET FORMATTING FUNCTION
		let formatting_xform = undefined
		if ( T.isObject(arg_format_object) && T.isString(arg_format_object.type) )
		{
			formatting_xform = this.get_format_value_function(arg_format_object)
		}

		// SET XFORM FOR SIMPLE SETTINGS
		if ( T.isString(arg_values_xform) )
		{
			const field_name = arg_values_xform
			arg_values_xform = {
				"result_type":"single",
				"fields":[
					{
						"name":field_name,
						"path":field_name
					}
				]
			}
		}

		if ( T.isUndefined(arg_values_xform) || arg_values_xform == null )
		{
			let xform_stream = T.isFunction(formatting_xform) ? arg_stream.get_transformed_stream().map(formatting_xform) : arg_stream.get_transformed_stream()

			if ( ! T.isUndefined(arg_starting_value) )
			{
				xform_stream = xform_stream.startWith(arg_starting_value)
			}

			if (arg_method_operands)
			{
				if ( T.isArray(arg_method_operands) )
				{
					// console.log(context + ':bind_stream:set a stream handler for method=%s with array operands=', arg_bound_method, arg_method_operands)
					unbind_cb = xform_stream.toProperty().assign(arg_bound_object, arg_bound_method, ...arg_method_operands)
				}
				else
				{ 
					// console.log(context + ':bind_stream:set a stream handler for method=%s with not array operands=', arg_bound_method, arg_method_operands)
					unbind_cb = xform_stream.toProperty().assign(arg_bound_object, arg_bound_method, arg_method_operands)
				}
			}
			else
			{
				unbind_cb = xform_stream.toProperty().assign(arg_bound_object, arg_bound_method)
			}
		}
		
		else if ( T.isFunction(arg_values_xform) )
		{
			let xform_stream = arg_stream.get_transformed_stream().map(arg_values_xform)
			xform_stream = T.isFunction(formatting_xform) ? xform_stream.map(formatting_xform) : xform_stream

			if ( ! T.isUndefined(arg_starting_value) )
			{
				xform_stream = xform_stream.startWith(arg_starting_value)
			}
			
			if (arg_method_operands)
			{
				unbind_cb = xform_stream.toProperty().assign(arg_bound_object, arg_bound_method, ...arg_method_operands)
			}
			else
			{
				unbind_cb = xform_stream.map(arg_values_xform).toProperty().assign(arg_bound_object, arg_bound_method)
			}
		}

		else if ( T.isObject(arg_values_xform) )
		{
			const datas_xform = (arg_stream_record) => {
				if (arg_stream_record.datas)
				{
					return arg_stream_record.datas
				}
				return arg_stream_record
			}

			let xform_stream = arg_stream.get_transformed_stream().map(datas_xform).map( transform(arg_values_xform) )
			
			// DEBUG
			// xform_stream.map(datas_xform).map( transform(arg_values_xform) ).onValue(
			// 	(values) => {
			// 		console.log(context + ':bind_stream:%s:on values_xform stream:bound method=%s, bound object, values', name, arg_bound_method, arg_bound_object, values)
			// 	}
			// )

			xform_stream = T.isFunction(formatting_xform) ? xform_stream.map(formatting_xform) : xform_stream
			
			if ( ! T.isUndefined(arg_starting_value) )
			{
				xform_stream = xform_stream.startWith(arg_starting_value)
			}

			if (arg_options && T.isBoolean(arg_options.dispatch) && arg_options.dispatch && T.isNumber(arg_options.index) && arg_options.index > 0 )
			{
				const at_index_xform = (arg_stream_record) => {
					if ( T.isArray(arg_stream_record) && arg_stream_record.length > arg_options.index )
					{
						return arg_stream_record[arg_options.index]
					}
					return undefined
				}

				xform_stream = xform_stream.map(at_index_xform)
			}

			// DEBUG
			// xform_stream.onValue(
			// 	(values) => {
			// 		console.log(context + ':bind_stream:on transformed stream:%s:bound method=%s, bound object, values', name, arg_bound_method, arg_bound_object, values)
			// 	}
			// )
			if (this._runtime.shoud_log_bindingd_stream())
			{
				const target = arg_bound_object && arg_bound_object.get_name ? arg_bound_object.get_name() : (arg_bound_object && arg_bound_object.attr ? arg_bound_object.attr('id') : (arg_bound_object && arg_bound_object.getAtttribute ? arg_bound_object.getAttribute('id') : 'unknow'))
				xform_stream.onValue(
					(values) => {
						const datas = T.isArray(values) ? 'array of length ' + values.length : (T.isObject(values) ? 'object of keys ' + Object.keys(values).toString() : 'datas')
						this._component.debug('bind_stream:on transformed stream:%s:target=%s,method=%s:values', name, target, arg_bound_method, datas)
					}
				)
			}

			if (arg_method_operands)
			{
				if ( T.isArray(arg_method_operands) )
				{
					unbind_cb = xform_stream.onValue(arg_bound_object, arg_bound_method, ...arg_method_operands)
				} else {
					unbind_cb = xform_stream.onValue(arg_bound_object, arg_bound_method, arg_method_operands)
				}
			}
			else
			{
				unbind_cb = xform_stream.onValue(arg_bound_object, arg_bound_method)
			}
		}
		else
		{
			assert(false, context + ':bind_svc:bad values paths string|array|function')
		}

		return unbind_cb
	}
	
	
	
	/**
	 * Get a formatting function or identity.
	 * Format settings example:
	 * 		{
	 * 			"type":"number",
	 * 			"digits":2
	 * 		}
	 * 
	 * @param {object} arg_format_object - format settings.
	 * 
	 * @returns {Function} - Formatting function or identity.
	 */
	get_format_value_function(arg_format_object)
	{
		if (! T.isObject(arg_format_object) || ! T.isString(arg_format_object.type) )
		{
			return (x) => x
		}

		switch(arg_format_object.type.toLocaleLowerCase()) {
			case 'number': {
				if ( T.isNumber(arg_format_object.digits) )
				{
					return (x) => {
						if (T.isNumber(x))
						{
							return x.toFixed(arg_format_object.digits)
						}
						return undefined
					}
				}
				return (x) => (T.isNumber(x) ? x : undefined)
			}
		}

		return (x) => x
	}
}

