	/**
	 * Get container items count.
	 * 
	 * @returns {integer}
	 */
	ui_items_get_count()
	{
		const dom_elem = this.get_dom_element()
		return dom_elem.children.length
	}
	
	
	
	/**
	 * Erase container items.
	 * 
	 * @returns {nothing}
	 */
	ui_items_clear()
