content:
// NPM IMPORTS
import assert from 'assert'
import Crossroads from 'crossroads'
import Hasher from 'hasher'

// COMMON IMPORTS
import T from '../../../node_modules/devapt-core-common/dist/js/utils/types'

// BROWSER IMPORTS
import RouterState from './router_state'


const context = 'browser/runtime/router'



/**
 * @file Browser navigation router class.
 * 
 * @author Luc BORIES
 * 
 * @license Apache-2.0
 */
export default class Router extends RouterState
{
	/**
	 * Create a Router instance.
	 * @extends RouterState
	 * 
	 * A Router instance manages a navigation history and update the page on navigation change.
	 * Navigation history is an array stored into the Redux store.
	 * Actions are:
	 * 		* display_content(view name, menubar name): update page content with given view and menubar.
	 * 		* go_backward(): update page with history previous content if available.
	 * 		* go_forward(): update page with history next content if available.
	 * 		* clear_history(): reset history array.
	 * 
	 * 	API:
	 * 		->init(arg_home_view_name, arg_home_menubar_name)
	 * 		->add_handler(arg_route, arg_handler)
	 * 		->update_hash_self(arg_view_name, arg_menubar_name)
	 * 		->display_content_self(arg_view_name, arg_menubar_name)
	 * 
	 * @param {string} arg_log_context - context of traces of this instance (optional).
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_log_context)
	{
		const log_context = arg_log_context ? arg_log_context : context
		super(log_context)

		this.is_router = true

		this._router_engine = Crossroads
		this._hasher = Hasher

		// DEBUG
		// log all routes
		// this._router_engine.routed.add( (request, data)=>{ console.log('route found', request, data) } ) 
		
		// log all requests that were bypassed / not matched
		this._router_engine.bypassed.add( (request)=>{ console.warn('route not found', request) } )

		assert( T.isObject(this._state_store), context + ':constructor:bad state_store object')
	}


	
	/**
	 * Init router.
	 * 
	 * @param {string} arg_home_view_name - Home view name.
	 * @param {string} arg_home_menubar_name - Home menubar name.
	 * 
	 * @returns {nothing}
	 */
	init(arg_home_view_name, arg_home_menubar_name)
	{
		// REGISTER HOME PAGE ROUTE
		// this.add_handler('',
		// 	() => {
		// 		return this.do_action_display_content(arg_home_view_name, arg_home_menubar_name)
		// 	}
		// )

		
		// REGISTER IGNORE ROUTE
		this.add_handler('ignore:{path}',
			()=>{
				// NOTHING TODO
			}
		)
		this.add_handler('ignore_{path}',
			()=>{
				// NOTHING TODO
			}
		)

		
		// REGISTER A PAGE ROUTE (view, menubar)
		var route = 'view\={view}\,menubar\={menubar}'
		this.add_handler(route,
			(arg_view, arg_menubar) => {
				return this.do_action_display_content(arg_view, arg_menubar)
			}
		)
		
		
		// SETUP HASHER
		const parseHash = (arg_newHash, arg_oldHash) => {
			// console.log('Hasher parse cb for new [%s] and old [%s]', arg_newHash, arg_oldHash)

			// debugger

			this._router_engine.parse(arg_newHash)
		}
		Hasher.prependHash = ''
		Hasher.setHash('/')
		Hasher.initialized.add(parseHash) //parse initial hash
		Hasher.changed.add(parseHash) //parse hash changes
		Hasher.init() //start listening for history change
	}



	/**
	 * Add a route handler.
	 * 
	 * @param {string|RegExp} arg_route - route.
	 * @param {Function} arg_handler - f(args)
	 * 
	 * @returns {nothing}
	 */
	add_handler(arg_route, arg_handler)
	{
		// console.log('Crossroads add route handler for route:', arg_route)

		this._router_engine.addRoute(arg_route,
			(...args) => {
				// var hash = Hasher.getHash()
				
				// console.log('Crossroads route cb with hash [%s] and args:', hash, args)

				return arg_handler(args)
			}
		)
	}
	
	

	/**
	 * Parse an url and route it.
	 * 
	 * @param {string} arg_url - url to route.
	 * 
	 * @returns {nothing}
	 */
	parse(arg_url)
	{
		const app_url = this._state_store.get_state().get('app_url', undefined)
		arg_url = (app_url.length > 0 && app_url[0] == '/' ? '' : '/') + app_url + arg_url
		
		if ( arg_url.endsWith('/') )
		{
			arg_url = arg_url.substr(0, arg_url.length - 1)
		}

		this._router_engine.parse(arg_url)
	}

	

	/**
	 * Update page url with given view and menubar (update only the hash).
	 * 
	 * @param {string} arg_view_name - view name.
	 * @param {string} arg_menubar_name - menubar name.
	 * 
	 * @returns {nothing}
	 */
	// update_hash_self(arg_view_name, arg_menubar_name)
	// {
	// 	Hasher.changed.active = false
	// 	Hasher.setHash('view=' + arg_view_name + ',menubar=' + arg_menubar_name)
	// 	Hasher.changed.active = true
	// }



	set_hash_if_empty(arg_hash)
	{
		if ( Hasher.getHash() == '' )
		{
			Hasher.changed.active = false
			Hasher.setHash(arg_hash)
			Hasher.changed.active = true
		}
	}
}

// NPM IMPORTS
// import assert from 'assert'
import { fromJS } from 'immutable'

// COMMON IMPORTS
import T         from '../../../node_modules/devapt-core-common/dist/js/utils/types'
import Stateable from '../../../node_modules/devapt-core-common/dist/js/base/stateable'

// BROWSER IMPORTS
import DisplayCommand from '../commands/display_command'


const context = 'browser/runtime/router_state'



/**
 * Initial route state
 */
const initial_state_js = {
	history:{
		items:[],
		index:-1
	}
}



/**
 * @file Browser navigation router class.
 * 
 * @author Luc BORIES
 * 
 * @license Apache-2.0
 */
export default class RouterState extends Stateable
{
	/**
	 * Create a RouterState instance.
	 * @extends Stateable
	 * @abstract
	 * 
	 * A RouterState instance manages a navigation history state and update the page on navigation change.
	 * 
	 * Navigation history is an array stored into the Redux store.
	 * 
	 * Actions are:
	 * 		* display_content(view name, menubar name): update page content with given view and menubar.
	 * 		* go_backward(): update page with history previous content if available.
	 * 		* go_forward(): update page with history next content if available.
	 * 		* update_history(arg_history_values): replace history array.
	 * 		* clear_history(): reset history array.
	 * 
	 * Corresponding method to subclass are:
	 * 		* display_content_self(view name, menubar name):Promise
	 * 		* go_backward_self():Promise
	 * 		* go_forward_self():Promise
	 * 
	 * @param {string} arg_log_context - context of traces of this instance (optional).
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_log_context)
	{
		const runtime = window.devapt().runtime()
		const log_context = arg_log_context ? arg_log_context : context
		const default_settings = {}
		super(default_settings, runtime, initial_state_js, log_context)

		this.is_router_state = true
		this.state_path = ['router']
	}


	/**
	 * Get name.
	 * 
	 * @returns {string}
	 */
	get_name()
	{
		return 'router'
	}



	/**
	 * Load and apply a container component configuration.
	 * 
	 * @param {Immutable.Map|undefined} arg_state - component state to load (optional).
	 * 
	 * @returns {nothing} 
	 */
	load(arg_state)
	{
		const state = arg_state ? arg_state : this.get_state()
		super.load(state)

		// console.info(context + ':load:loading ' + this.get_name())
		
		if (! state)
		{
			this.error('load:no available state')
			return
		}
	}
	
	
	
	/**
	 * Handle state changes.
	 * 
	 * @param {Immutable.Map} arg_previous_state - previous state map.
	 * @param {Immutable.Map} arg_new_state - new state map.
	 * 
	 * @returns {nothing}
	 */
	handle_state_change(arg_previous_state, arg_new_state)
	{
		if (! arg_previous_state)
		{
			// console.info(context + ':handle_state_change:update initial items')
			this.do_action_clear_history()
			return
		}
		
		if ( arg_previous_state && arg_new_state && arg_previous_state.has('history') && arg_new_state.has('history') )
		{
			if ( arg_previous_state.hasIn(['history', 'items']) && arg_new_state.hasIn(['history', 'items'] ) )
			{
				const previous_history_items = arg_previous_state.getIn(['history', 'items'])
				const new_history_items = arg_new_state.getIn(['history', 'items'])
				if ( ! previous_history_items.equals(new_history_items) )
				{
					const new_items = new_history_items.toArray().toJS()
					
					if (new_items.length == 0)
					{
						this.do_action_clear_history()
						return
					}

					this.do_action_update_history(new_items)
				}
			}
			
			if ( arg_previous_state.hasIn(['history', 'index']) && arg_new_state.hasIn(['history', 'index'] ) )
			{
				const previous_history_index = arg_previous_state.getIn(['history', 'index'])
				const new_history_index = arg_new_state.getIn(['history', 'index'])
				// TODO CHECK IMMUTABLE RETURNS A NUMBER
				if ( previous_history_index != new_history_index )
				{
					this.do_action_move_history(new_history_index)
				}
			}
		}
	}


	
	/**
	 * Goto the page content with given view (update only the hash).
	 * 	
	 * @param {string} arg_view_name - view name.
	 * @param {string} arg_menubar_name - menubar name.
	 * 
	 * @returns {nothing}
	 */
	do_action_display_content(arg_view_name, arg_menubar_name)
	{
		this.enter_group('do_action_display_content')
		
		this.debug('arg_view_name:', arg_view_name)
		this.debug('arg_menubar_name:', arg_menubar_name)
		
		if ( T.isString(arg_view_name) || T.isString(arg_menubar_name) )
		{
			this.dispatch_action('display_content', { view:arg_view_name, menubar:arg_menubar_name } )
		}
		
		this.leave_group('do_action_display_content')
	}



	/**
	 * Display history previous content if available.
	 *
	 * @returns {nothing}
	 */
	do_action_go_backward()
	{
		this.enter_group('do_action_go_backward')
		
		this.dispatch_action('go_backward', {} )
		
		this.leave_group('do_action_go_backward')
	}



	/**
	 * Display history next content if available.
	 *
	 * @returns {nothing}
	 */
	do_action_go_forward()
	{
		this.enter_group('do_action_go_forward')
		
		this.dispatch_action('go_forward', {} )
		
		this.leave_group('do_action_go_forward')
	}
	


	/**
	 * Clear history.
	 *
	 * @returns {nothing}
	 */
	do_action_clear_history()
	{
		this.enter_group('do_action_go_forward')
		
		this.dispatch_action('clear_history', {} )
		
		this.leave_group('do_action_go_forward')
	}



	/**
	 * Update history.
	 *
	 * @param {array} arg_new_items - history array items.
	 * 
	 * @returns {nothing}
	 */
	do_action_update_history(arg_new_items)
	{
		this.enter_group('do_action_go_forward')
		
		this.debug('arg_new_items', arg_new_items)

		this.dispatch_action('update_history', { values: arg_new_items } )
		
		this.leave_group('do_action_go_forward')
	}



	/**
	 * Move history current position.
	 *
	 * @param{Number} arg_new_index - history position index.
	 * 
	 * @returns {nothing}
	 */
	do_action_move_history(arg_new_index)
	{
		this.enter_group('do_action_move_history')
		
		this.debug('arg_new_index', arg_new_index)

		this.dispatch_action('update_history', { index: arg_new_index } )
		
		this.leave_group('do_action_move_history')
	}

	
	
	/**
	 * Store actions reducer pure function.
	 * 
	 * @param {object} arg_previous_state - previous state.
	 * @param {object} arg_action - store action: { type:'', component:'', ...}
	 * 
	 * @returns {object} - new state
	 */
	reduce_action(arg_previous_state, arg_action)
	{
		const initial_state_js = {

		}

		if (! arg_previous_state)
		{
			arg_previous_state = fromJS(initial_state_js)
		}

		// console.log(context + ':reduce_action:prev state', arg_previous_state.toJS())
		
		switch(arg_action.type)
		{
			case 'display_content': {
				if ( ! T.isString(arg_action.view) || ! T.isString(arg_action.menubar) )
				{
					return arg_previous_state
				}

				// UPDATE PAGE URL
				this.update_hash(arg_action.view, arg_action.menubar)
				
				// UPDATE ROUTER STORED STATE
				const history = arg_previous_state.get('history')
				let next_state = history.get('items').push( { view:arg_action.view_name, menubar:arg_action.menubar_name } )
				next_state = history.set('index', history.get('index') )
				
				// UPDATE PAGE CONTENT
				this.display_content(arg_action.view, arg_action.menubar)
				
				return next_state
			}

			case 'go_backward': {
				const history = arg_previous_state.get('history')
				const previous_index = history.get('index')
				if (previous_index > 0)
				{
					const new_index = previous_index - 1
					const item = history.get('items').get(new_index, undefined)
					if (item)
					{
						this.display_content(item.view, item.menubar)

						const next_state = history.set('index', new_index)
						return next_state
					}
				}
				
				return arg_previous_state
			}

			case 'go_forward': {
				const history = arg_previous_state.get('history')
				const previous_index = history.get('index')
				if (previous_index + 1 < history.get('items').count() )
				{
					const new_index = previous_index + 1
					const item = history.get('items').get(new_index, undefined)
					if (item)
					{
						this.display_content(item.view, item.menubar)

						const next_state = history.set('index', new_index)
						return next_state
					}
				}

				return arg_previous_state
			}

			case 'clear_history': {
				const next_state = arg_previous_state.set('history', fromJS([]) )
				return next_state
			}

			case 'update_history': {
				let items = arg_previous_state.get('history').concat(arg_action.values)
				const next_state = arg_previous_state.set('history', items)
				return next_state
			}

			case 'move_history': {
				const history = arg_previous_state.get('history')
				const new_index = arg_action.index
				if (new_index > 0 && new_index < history.get('items').count() )
				{
					const item = history.get('items').get(new_index, undefined)
					if (item)
					{
						this.display_content(item.view, item.menubar)

						const next_state = history.set('index', new_index)
						return next_state
					}
				}
			}
		}

		
		return arg_previous_state
	}
	
	
	
	/**
	 * Display the page content with given view and menubar.
	 * 
	 * @param {string} arg_view_name - view name
	 * @param {string} arg_menubar_name - menubar name
	 * 
	 * @returns {Promise} - Resolved result is a boolean: success or failure
	 */
	display_content(arg_view_name, arg_menubar_name, arg_breadcrumbs=undefined)
	{
		this.enter_group('display_content')
		
		this.debug('arg_view_name=' + arg_view_name)
		this.debug('arg_menubar_name=' + arg_menubar_name)
		this.debug('arg_breadcrumbs=' + arg_breadcrumbs)
		

		let promise = null
		try
		{
			const cmd_settings = {
				name:'router.display_content',
				type:'display',
				view:arg_view_name,
				menubar:arg_menubar_name,
				breadcrumbs:arg_breadcrumbs
			}
			const cmd = new DisplayCommand(this._runtime, cmd_settings)

			this.get_runtime().ui().pipe_display_command(cmd)
		}
		catch(e)
		{
			console.error(e, context)
			promise = Promise.resolve(false)
		}
		

		this.leave_group('display_content')
		return promise
	}
	
	

	/**
	 * Update page url with given view and menubar (update only the hash).
	 * 
	 * @param {string} arg_view_name - view name.
	 * @param {string} arg_menubar_name - menubar name.
	 * 
	 * @returns {nothing}
	 */
	update_hash(arg_view_name, arg_menubar_name)
	{
		this.enter_group('update_hash')
		
		this.debug('arg_view_name', arg_view_name)
		this.debug('arg_menubar_name', arg_menubar_name)
		
		try
		{
			if ( T.isFunction(this.update_hash_self) )
			{
				this.update_hash_self(arg_view_name, arg_menubar_name)
			}
		}
		catch(e)
		{
			console.error(e, context)
			this.error(e)
		}
		
		this.leave_group('update_hash')
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T             from '../../../node_modules/devapt-core-common/dist/js/utils/types'
import { transform } from '../../../node_modules/devapt-core-common/dist/js/utils/transform'
import Stream        from '../../../node_modules/devapt-core-common/dist/js/messaging/stream'

// BROWSER IMPORT
import ServiceOperation from './service_operation'
import DEFAULT_OPS      from './service_default_ops'


const context = 'browser/runtime/service'




/**
 * @file client Service class.
 * @author Luc BORIES
 * @license Apache-2.0
 */
export default class Service
{
	/**
	 * Create a service wrapper instance.
	 * 
	 * @param {string} arg_svc_name - service name.
	 * @param {object} arg_svc_settings - service settiings.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_svc_name, arg_svc_settings)
	{
		this.$name = arg_svc_name
		this.is_service = true
		this.execute_on_browser = false
		
		// MAP OF POLLING TIMERS: name => timer id
		this.timers = {}
		
		this.load(arg_svc_settings)
	}



	/**
	 * Get service name.
	 * 
	 * @returns {string}
	 */
	get_name()
	{
		return this.$name
	}
	
	
	
	/**
	 * Load service settings.
	 * 
	 * @param {object} arg_settings - runtime settings.
	 * 
	 * @returns {nothing}
	 */
	load(arg_settings)
	{
		const self = this
		// console.log(context + ':load: name=' + this.$name + ' settings=', arg_settings)
		
		// SERVICE EXECUTION IS BROWSER OR SERVER (default)
		if ('execution' in arg_settings)
		{
			if (arg_settings.execution == 'browser')
			{
				this.execute_on_browser = true
			}
		}
		
		// GET SERVICES OPERATIONS
		let ops = DEFAULT_OPS
		if ('operations' in arg_settings)
		{
			ops = arg_settings['operations']
		}
		// console.log(context + ':load: name=' + this.$name + ' ops=', ops)
		
		// GET POLLERS AND TIMELINE SETTINGS
		const pollers_settings = ('pollers' in arg_settings) ? arg_settings.pollers : undefined
		const timeline_settings = ('timeline' in arg_settings) ? arg_settings.timeline : undefined
		// console.log(context + ':load:settings.pollers=', pollers_settings)

		// CONFIGURE OPERATIONS
		this.$ops = ops
		const svc_path = '/' + this.$name
		const svc_socket = window.io(svc_path)
		self.socket = svc_socket
		
		this.$ops.forEach(
			(operation) => {
				const op_name = operation
				// console.log(context + ':load:svc=%s:op=%s', this.get_name(), op_name)
				
				// OPERATION POLLER: REPEAT EVERY xxx MILLISECONDS FOR GLOBAL SETTINGS
				if (! this.execute_on_browser && pollers_settings && (op_name in pollers_settings))
				{
					const pollers_op_settings = pollers_settings[op_name]
					// console.log('service has poller for operation:' + op_name, pollers_op_settings)

					this.create_poller(pollers_op_settings, op_name, arg_settings.credentials, svc_socket, [])
				}

				// OPERATION EXECUTION
				const svc_operation = new ServiceOperation(op_name, {service:this})
				self[op_name] = (arg_operands) => {
					// console.log(context + ':op:%s:%s:cfg=', this.get_name(), op_name, arg_operands)

					if (this.execute_on_browser)
					{
						return svc_operation.execute_on_browser(arg_operands, arg_settings.credentials)
					}

					return svc_operation.execute_on_server(svc_socket, svc_path, arg_operands, arg_settings.credentials, arg_settings.session_uid)
				}
				self[op_name].operation = svc_operation

				// OPERATION TIMELINE: HAS HISTORY?
				self[op_name].timelines = {}
				if (! this.execute_on_browser && timeline_settings && (op_name in timeline_settings))
				{
					this.create_timeline(op_name, svc_socket, timeline_settings)
				}
			}
		)
	}


	
	/**
	 * Create a timer.
	 * 
	 * @param {string}	arg_timer_name - timer unique name.
	 * @param {function} arg_timer_cb - timer callback function.
	 * @param {integer} arg_delay - timer interval integer in milliseconds.
	 * @param {boolean} arg_force_create - if true delete existing timer and recreate it (default=false).
	 * 
	 * @returns {nothing}
	 */
	create_timer(arg_timer_name, arg_timer_cb, arg_delay, arg_force_create = false)
	{
		assert( T.isString(arg_timer_name), context + ':create_timer:bad timer name string')
		assert( T.isFunction(arg_timer_cb), context + ':create_timer:bad timer callback function')
		assert( T.isNumber(arg_delay), context + ':create_timer:bad timer delay integer')
		assert( T.isBoolean(arg_force_create), context + ':create_timer:bad force create boolean')
		
		// console.log('create_timer', arg_timer_name, this.timers)
		
		if (arg_timer_name in this.timers)
		{
			// console.log(context + ':create_timer:timer exists name=' + arg_timer_name)
			if (! arg_force_create)
			{
				return
			}

			// DELETE EXISTING TIMER
			// console.log(context + ':create_timer:delete existing timer name=' + arg_timer_name)
			this.delete_timer( this.timers[arg_timer_name] )
			delete this.timers[arg_timer_name]
		}
		
		// CREATE TIMER
		// console.log(context + ':create_timer:create timer name=' + arg_timer_name)
		this.timers[arg_timer_name] = setInterval(
			arg_timer_cb,
			arg_delay
		)
	}
	
	

	/**
	 * Create a poller for the given socket operation.
	 * 
	 * @param {object} arg_poller_settings - poller settings { name:'...', interval_seconds|interval_milliseconds:number }.
	 * @param {object} arg_op_name - service operation name.
	 * @param {object} arg_credentials - session credentials.
	 * @param {object} arg_socket - service socket.
	 * @param {array} arg_op_opds - operation operands (optional)(default=[]).
	 * 
	 * @returns {nothing}
	 */
	create_poller(arg_poller_settings, arg_op_name, arg_credentials, arg_socket, arg_op_opds=[])
	{
		const self = this

		if ( T.isObject(arg_poller_settings) )
		{
			const interval_ms = T.isNumber(arg_poller_settings.interval_seconds) ? arg_poller_settings.interval_seconds * 1000 : arg_poller_settings.interval_milliseconds
			if ( T.isNumber(interval_ms) && T.isString(arg_poller_settings.name) )
			{
				console.log('create poller for operation:' + arg_op_name, arg_poller_settings.name, interval_ms)
				
				const request = {
					session_uid:arg_session_uid,
					service:self.get_name(),
					operation:arg_op_name,
					operands:arg_op_opds,
					credentials:arg_credentials
				}

				self.create_timer(
					arg_poller_settings.name,
					() => {
						// console.log('GLOBAL SETTINGS:create_timer svc_socket.emit', svc_path, op_name)
						arg_socket.emit(arg_op_name, request)
					},
					interval_ms,
					false
				)
			}
		}
	}
	


	/**
	 * Delete a timer.
	 * 
	 * @param {any}	arg_timer_id.
	 * 
	 * @returns {nothing}
	 */
	delete_timer(arg_timer_id)
	{
		clearInterval(arg_timer_id)
	}


	create_timeline(op_name, svc_socket, timeline_settings)
	{
		console.log('service has timeline for operation:' + op_name, timeline_settings)

		let timeline_op_settings_array = timeline_settings[op_name]
		if( T.isObject(timeline_op_settings_array) )
		{
			timeline_op_settings_array = [timeline_op_settings_array]
		}
		
		let stream = Stream.from_emitter_event(svc_socket, op_name)

		timeline_op_settings_array.forEach(
			(timeline_op_settings) => {
				if ( T.isObject(timeline_op_settings) && timeline_op_settings.transform && T.isNumber(timeline_op_settings.max) && T.isString(timeline_op_settings.name) && T.isNumber(timeline_op_settings.interval_seconds))
				{
					self[op_name].timelines[timeline_op_settings.name] = {
						values:[],
						previous_ts:undefined,
						stream:new Stream.Bus()
					}
					stream.subscribe(
						(value) => {
							value = value.datas ? value.datas : value

							if ( T.isString( timeline_op_settings.transform ) || T.isNumber( timeline_op_settings.transform ) )
							{
								const field_name = timeline_op_settings.transform
								timeline_op_settings.transform = {
									"result_type":"single",
									"fields":[
										{
											"name":field_name,
											"path":field_name
										}
									]
								}
							}

							const extracted_value = transform(timeline_op_settings.transform)(value)
							// console.log(context + ':load:timeline extracted_value=', extracted_value)
							
							const timeline = self[op_name].timelines[timeline_op_settings.name]
							const ts = Date.now()
							const prev_ts = timeline.previous_ts
							
							if (!prev_ts)
							{
								timeline.previous_ts = ts
								timeline.values = [{ts:ts, value:extracted_value}]
								timeline.stream.push(timeline.values)
							}
							else if ( (ts - prev_ts) > (timeline_op_settings.interval_seconds * 1000) )
							{
								timeline.values.push({ts:ts, value:extracted_value})
								timeline.previous_ts = ts
								
								if (timeline.values.length > timeline_op_settings.max)
								{
									const too_many = timeline.values.length - timeline_op_settings.max
									timeline.values = timeline.values.slice(too_many)
								}

								timeline.stream.push(timeline.values)
							}
						}
					)
				}
			}
		)
	}
}

export default [
	{
		name:'ping',
		operands:[]
	},
	{
		name:'get',
		operands:[]
	},
