// NPM IMPORTS
import assert from 'assert'
import _ from 'lodash'

// COMMON IMPORTS
import T         from '../utils/types'
import Errorable from './errorable'
import Instance  from './instance'


/**
 * Contextual constant for this file logs.
 * @private
 */
const context = 'common/base/collection_base'



/**
 * Base class for all collections classes.
 * @abstract
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
  API:
 * 		->set_all(arg_items):nothing - Set all collection items.
 * 		->get_all(arg_types):array - Get all collection items or filter items with given type.
 * 		->get_all_names(arg_types):array - Get all items names with or without a filter on items types.
 * 		->get_all_ids():array - Get all items ids with or without a filter on items types.
 * 
 * 		->item(arg_name):Instance - Get an item by its name.
 * 
 * 		->get_count():number - Get all items count.
 * 		->get_first():object|undefined - Get first item.
 * 		->get_last():object|undefined - Get last item.
 * 
 * 		->add(arg_item):nothing - Add an item to the collection.
 * 		->add_first(arg_item):nothing - Add an item to the collection at the first position.
 * 		->remove(arg_item):nothing - Remove an item from the collection.
 * 		->has(arg_item):boolean -  Test if an item is inside the collection.
 * 
 * 		->find_by_name(arg_name):Instance|undefined - Find an item by its name into the collection.
 * 		->find_by_id(arg_id):Instance|undefined - Find an item by its id into the collection.
 * 		->find_by_attr(arg_attr_name, arg_attr_value):Instance|undefined - Find an item by one of its attributes into the collection.
 * 		->find_by_filter(arg_filter_function):Instance|undefined - Find an item by a filter function.
 * 
 * 		->filter_by_attr(arg_attr_name, arg_attr_value):array - Filter items by one of theirs attributes into the collection.
 * 		->filter_by_filter(arg_filter_function):array - Filter items by a filter function.
 * 
 * 		->get_accepted_types():array - Get all collection accepted types.
 * 		->set_accepted_types(arg_types):nothing - Set all collection accepted types.
 * 		->add_accepted_type(arg_type):nothing - Add one collection accepted type.
 * 		->has_accepted_type(arg_type):boolean - Test if collection has given accepted type.
 * 
 * 		->forEach(arg_cb):nothing - forEach wrapper on ordered items.
 */
export default class CollectionBase extends Errorable
{
	/**
	 * Create a collection of Instance objects.
	 * 
	 * @returns {nothing}
	 */
	constructor()
	{
		super(context, undefined)

		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_collection_base  = true

		/**
		 * Items array.
		 * @type {array}
		 */
		this._items_array   = []

		/**
		 * Items names map.
		 * @type {object}
		 */
		this._items_by_name = {}

		/**
		 * Items ids map.
		 * @type {object}
		 */
		this._items_by_id   = {}

		/**
		 * Accepted types array.
		 * @type {array}
		 */
		this._accepted_types = ['*']
	}



	/**
	 * Format string dump.
	 * 
	 * @returns{string}
	 */
	toString()
	{
		let str = '['
		_.forEach(this._items_array, (item, index)=>(index > 0 ? ',' : '') + item.get_name() )
		return str + ']'
	}
	
	
	
	/**
	 * Set all collection items.
	 * 
	 * @param {Instance|array} arg_items - collection items: one or many Instance objects.
	 * 
	 * @returns {nothing}
	 */
	set_all(arg_items)
	{
		// DEBUG
		let str = '['
		_.forEach(arg_items, (item, index)=> str += (index > 0 ? ',' : '') + (item.get_name ? item.get_name() : 'bad item of type ' + (typeof item) ) )
		str += ']'
		console.log('set_all', str, typeof arg_items)

		// RESET STORES
		this._items_array   = []
		this._items_by_name = {}
		this._items_by_id   = {}

		// ONE INSTANCE IS GIVEN
		if ( T.isObject(arg_items) && arg_items instanceof Instance )
		{
			this._add(arg_items)
			return
		}
		
		// AN OBJECT OR AN ARRAY IS GIVEN
		if ( T.isObject(arg_items) || T.isArray(arg_items) )
		{
			_.forEach(arg_items,
				(item)=>{
					if ( T.isObject(item) && item instanceof Instance )
					{
						this._add(item)
					}
				}
			)
			return
		}

		console.error(context + '::bad given items type (not an Instance, object, array)')
	}



	/**
	 * Set all collection items.
	 * @private
	 * 
	 * @param {array} arg_items - Instance objects array.
	 * 
	 * @returns {nothing}
	 */
	_set_all(arg_items)
	{
		this._items_array = arg_items
	}



	/**
	 * Get all collection items.
	 * @private
	 * 
	 * @returns {array}
	 */
	_get_all()
	{
		return this._items_array
	}
	
	
	
	/**
	 * Get all collection items or filter items with given type.
	 * 
	 * @param {array|string|nothing} arg_types - type or types for items filtering.
	 * 
	 * @returns {array} - all or filtered items, empty array if not found.
	 */
	get_all(arg_types)
	{
		// NO TYPE FILTER
		if (! arg_types)
		{
			return _.toArray( this._items_array )
		}

		// ONE TYPE FILTER
		if ( T.isString(arg_types) )
		{
			return _.filter(this._items_array, item => item.get_types() == arg_types )
		}

		// MANY TYPES FILTER
		if ( T.isArray(arg_types) )
		{
			return _.filter(this._items_array, item => arg_types.indexOf( item.get_types() ) >= 0 )
		}

		return []
	}
	
	
	
	/**
	 * Get all items names with or without a filter on items types.
	 * 
	 * @param {array|string|nothing} arg_types - type or types for items filtering.
	 * 
	 * @returns {array} - all or filtered items names, empty array if not found.
	 */
	get_all_names(arg_types)
	{
		// NO TYPE FILTER
		if (! arg_types)
		{
			return _.map(this._items_array, (item) => item.get_name() )
		}

		// ONE TYPE FILTER
		if ( T.isString(arg_types) )
		{
			return _.filter( this._items_array, item => item.get_types() == arg_types ).map( (item) => item.get_name() )
		}

		// MANY TYPES FILTER
		if ( T.isArray(arg_types) )
		{
			return _.filter( this._items_array, item => arg_types.indexOf( item.get_types() ) >= 0 ).map( (item) => item.get_name() )
		}

		return []
	}
	
	

	/**
	 * Get all items ids with or without a filter on items types.
	 * 
	 * @returns {array} - all items ids.
	 */
	get_all_ids()
	{
		return _.map(this._items_array, (item) => item.get_id() )
	}
	
	
	
	/**
	 * Get an item by its name.
	 * 
	 * @param {string} arg_name - instance name.
	 * 
	 * @returns {Instance|undefined}
	 */
	item(arg_name)
	{
		return this._items_by_name ? this._items_by_name[arg_name] : undefined
	}
	
	
	
	/**
	 * Get an item by its name.
	 * 
	 * @param {string} arg_name - instance name.
	 * 
	 * @returns {Instance|undefined}
	 */
	get(arg_name)
	{
		return this._items_by_name ? this._items_by_name[arg_name] : undefined
	}
	
	
	
	/**
	 * Default iterator operator.
	 */
	
	// * [Symbol.iterator]() {
	// 	for (let item of this.$items)
	// 	{
	// 		yield item
	// 	}
	// }
	
	// [Symbol.iterator]()
	// {
	// 	let step = 0
	// 	const count = this.$items.length
		
	// 	const iterator = {
	// 		next()
	// 		{
	// 			if (step < count)
	// 			{
	// 				const item = this.$items[step]
	// 				step++
	// 				return { value:item, done:false }
	// 			}
				
	// 			return { value:undefined, done:true }
	// 		}
	// 	}
		
	// 	return iterator
	// }
	
	
	// NOT COMPATIBLE WITH NODE 0.10
	// [Symbol.iterator]()
	// {
	// 	return this.$items.iterator()
	// }
	
	
	
	/**
	 * Get all items count.
	 * 
	 * @returns {number} - all items count.
	 */
	get_count()
	{
		return _.size(this._items_array)
	}
    
    
	
	/**
	 * Get first item.
	 * 
	 * @returns {object|undefined} - first collection items or undefined if collection is empty.
	 */
	get_first()
    {
		return _.first(this._items_array)
	}
	
	
    
	/**
	 * Get last item.
	 * 
	 * @returns {object|undefined} - last collection items or null if collection is empty.
	 */
	get_last()
    {
		return _.last(this._items_array)
	}



	/**
	 * Add an item to the collection.
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	add(arg_item)
	{
		if ( T.isObject(arg_item) && arg_item instanceof Instance )
		{
			if ( this.has_accepted_type('*') || this.has_accepted_type( arg_item.get_type() ) )
			{
				this._add(arg_item)
				return
			}
			
			this.error('not accepted type [' + arg_item.get_type() + '] for instance [' + arg_item.get_name() + ']')
			return
		}
		
		this.error('bad item: not an instance object')
	}
	
	
	
	/**
	 * Add an item to the collection at the first position.
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	add_first(arg_item)
	{
		if ( T.isObject(arg_item) && arg_item instanceof Instance )
		{
			if ( this.has_accepted_type('*') || this.has_accepted_type(arg_item.$type) )
			{
				this._add_first(arg_item)
				return
			}
			
			this.error('not accepted type [' + arg_item.$type + '] for instance [' + arg_item.$name + ']')
			return
		}
		
		this.error('bad item: not an instance object')
	}
	
	
	
	/**
	 * Remove an item from the collection.
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	remove(arg_item)
	{
		if ( T.isObject(arg_item) && arg_item instanceof Instance )
		{
			const name = arg_item.get_name()
			if (name in this._items_by_name)
			{
				this._remove(arg_item)
				return
			}
		}
		
		this.error('bad item: not an instance object or not found')
	}
	
	

	/**
	 * Test if an item is inside the collection.
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {boolean}
	 */
	has(arg_item)
	{
		if ( T.isObject(arg_item) && arg_item instanceof Instance )
		{
			return this._has(arg_item)
		}
		return false
	}
	
	

	/**
	 * Add an item to the collection without type checks (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	_add(arg_item)
	{
		if( this._has(arg_item) )
		{
			return
		}

		const name = arg_item.get_name()
		const id   = arg_item.get_id()

		this._items_array.push(arg_item)
		this._items_by_name[name] = arg_item
		this._items_by_id[id] = arg_item
	}
	
	

	/**
	 * Add an item to the collection without type checks at first position (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	_add_first(arg_item)
	{
		if( this._has(arg_item) )
		{
			return
		}
		
		const name = arg_item.get_name()
		const id   = arg_item.get_id()

		this._items_array = [arg_item].concat(this._items_array)
		this._items_by_name[name] = arg_item
		this._items_by_id[id] = arg_item
	}
	
	

	/**
	 * Remove an item from the collection without type checks (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	_remove(arg_item)
	{
		const name  = arg_item.get_name()
		const id    = arg_item.get_id()
		const index = this._items_array.indexOf(arg_item)

		this._items_array.splice(index, 1)
		delete this._items_by_name[name]
		delete this._items_by_id[id]
	}
	
	

	/**
	 * Test if an item is inside the collection without type checks (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {boolean}
	 */
	_has(arg_item)
	{
		const name = arg_item.get_name()
		return (name in this._items_by_name)
	}
	
	
    
	/**
	 * Find an item by its name into the collection.
	 * 
	 * TODO: optimize with a map index.
	 * 
	 * @param {string} arg_name - instance name.
	 * 
	 * @returns {Instance|undefined}
	 */
	find_by_name(arg_name)
	{
		return this._items_by_name[arg_name]
	}
	
	
	
	/**
	 * Find an item by its id into the collection.
	 * 
	 * @param {string} arg_id - instance id.
	 * 
	 * @returns {Instance|undefined}
	 */
	find_by_id(arg_id)
	{
		return this._items_by_id[arg_id]
	}
	
	
	
	/**
	 * Find an item by one of its attributes into the collection.
	 * 
	 * @param {string} arg_attr_name - instance attribute name.
	 * @param {any} arg_attr_value - instance attribute value.
	 * 
	 * @returns {Instance|undefined}
	 */
	find_by_attr(arg_attr_name, arg_attr_value)
	{
		return _.find(this._items_array, item => (arg_attr_name in item) && item[arg_attr_name] == arg_attr_value)
	}
	
	
	
	/**
	 * Find an item by a filter function.
	 * 
	 * @param {string} arg_filter_function - function to apply on instance, returns a boolean.
	 * 
	 * @returns {Instance|undefined}
	 */
	find_by_filter(arg_filter_function)
	{
		return _.find(this._items_array, item => arg_filter_function(item) )
	}
	
	
	
	/**
	 * Filter items by one of theirs attributes into the collection.
	 * 
	 * @param {string} arg_attr_name - instance attribute name.
	 * @param {any} arg_attr_value - instance attribute value.
	 * 
	 * @returns {array}
	 */
	filter_by_attr(arg_attr_name, arg_attr_value)
	{
		return _.filter(this._items_array, item => (arg_attr_name in item) && item[arg_attr_name] == arg_attr_value)
	}
	
	

	/**
	 * Filter items by a filter function.
	 * 
	 * @param {string} arg_filter_function - function to apply on instance, returns a boolean.
	 * 
	 * @returns {array}
	 */
	filter_by_filter(arg_filter_function)
	{
		return _.filter(this._items_array, item => arg_filter_function(item) )
	}
	
	
	
	/**
	 * Get all collection accepted types.
	 * 
	 * @returns {array} - array of types strings.
	 */
	get_accepted_types()
	{
		this._accepted_types
	}
	
	
	
	/**
	 * Set all collection accepted types.
	 * 
	 * @param {array} arg_types - accepted types strings array.
	 * 
	 * @returns {nothing}
	 */
	set_accepted_types(arg_types)
	{
		assert(T.isArray(arg_types), context + ':bad accepted types array')
		this._accepted_types = arg_types
	}
	
	
	
	/**
	 * Add one collection accepted type.
	 * 
	 * @param {string} arg_type - accepted types string.
	 * 
	 * @returns {nothing}
	 */
	add_accepted_type(arg_type)
	{
		assert(T.isString(arg_type), context + ':bad accepted type string')
		this._accepted_types.push(arg_type)
	}
	
	
	
	/**
	 * Test if collection has given accepted type.
	 * 
	 * @param {string} arg_type - accepted types string.
	 * 
	 * @returns {boolean}
	 */
	has_accepted_type(arg_type)
	{
		return this._accepted_types.indexOf(arg_type) > -1
	}
	
	
	
	/**
	 * forEach wrapper on ordered items.
	 * 
	 * @param {function} arg_cb - callback to call on each item.
	 * 
	 * @returns {nothing}
	 */
	forEach(arg_cb)
	{
		_.forEach(this._items_array, arg_cb)
	}
}

// NPM IMPORTS
// import assert from 'assert'
import _ from 'lodash'
import semver from 'semver'

// COMMON IMPORTS
import T              from '../utils/types'
import Instance       from './instance'
import CollectionBase from './collection_base'


// let context = 'common/base/collection_version'



/**
 * Versionned Instance objects collection
 * 
 * @author Luc BORIES
 * @license Apache-2.0
* 
* @example
* API:
* 		->set_version_getter(arg_getter):nothing - Set version getter.
* 		->get_version(arg_item):string - Get instance version.
* 		->compare_versions(arg_version1, arg_version2):integer - Compare two instance versions: v1 < v2 => -1, v1 = v2 => 0, v1 > v2 => 1.
* 		
* 		->get_latest_item(arg_name):Instance|undefined - Get latest version of item by its name.
* 		->get_latest_items():array - Get latest version of all items.
* 		->get_item_of_version(arg_name, arg_version):Instance|undefined - Get an item by its name and its version.
* 		->has_version(arg_item_name, arg_version):boolean -  Test if an item is inside the collection.
* 
* @example
* INHERITED API:
* 		->set_all(arg_items):nothing - Set all collection items. (INHERITED)
* 		->get_all(arg_types):array - Get all collection items or filter items with given type. (INHERITED)
* 		->get_all_names(arg_types):array - Get all items names with or without a filter on items types. (INHERITED)
* 		->get_all_ids():array - Get all items ids with or without a filter on items types. (INHERITED)
* 
* 		->item(arg_name):Instance - Get an item by its name. (INHERITED)
* 
* 		->get_count():number - Get all items count. (INHERITED)
* 		->get_first():object|undefined - Get first item. (INHERITED)
* 		->get_last():object|undefined - Get last item. (INHERITED)
* 
* 		->add(arg_item):nothing - Add an item to the collection. (OVERWRITTEN)
* 		->add_first(arg_item):nothing - Add an item to the collection at the first position. (INHERITED)
* 		->remove(arg_item):nothing - Remove an item from the collection.
* 		->has(arg_item):boolean -  Test if an item is inside the collection.
* 
* 		->find_by_name(arg_name):Instance|undefined - Find an item by its name into the collection.
* 		->find_by_id(arg_id):Instance|undefined - Find an item by its id into the collection.
* 		->find_by_attr(arg_attr_name, arg_attr_value):Instance|undefined - Find an item by one of its attributes into the collection.
* 		->find_by_filter(arg_filter_function):Instance|undefined - Find an item by a filter function.
* 
* 		->filter_by_attr(arg_attr_name, arg_attr_value):array - Filter items by one of theirs attributes into the collection.
* 		->filter_by_filter(arg_filter_function):array - Filter items by a filter function.
* 
* 		->get_accepted_types():array - Get all collection accepted types.
* 		->set_accepted_types(arg_types):nothing - Set all collection accepted types.
* 		->add_accepted_type(arg_type):nothing - Add one collection accepted type.
* 		->has_accepted_type(arg_type):boolean - Test if collection has given accepted type.
* 
* 		->forEach(arg_cb):nothing - forEach wrapper on ordered items.
 */
export default class CollectionVersion extends CollectionBase
{
	/**
	 * Create a collection of versionned Instance objects.
	 * 
	 * @param {...Instance} args - variadic list of Instance.
	 * 
	 * @returns {nothing}
	 */
	constructor(...args)
	{
		super()

		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_collection_version  = true

		/**
		 * Items versions names map.
		 * @type {object}
		 */
		this._items_by_name_by_version     = {}

		/**
		 * Items last version names map.
		 * @type {object}
		 */
		this._items_by_name_latest_version = {}

		/**
		 * Get version helper.
		 * @type {function}
		 */
		this.$version_getter = (item)=> item && item.get_version ? item.get_version() : undefined

		if ( args.length == 1 && T.isArray(args[0]) )
		{
			args = args[0]
		}
		
		if (args && args.length > 0)
		{
			this.set_all(args)
		}
	}



	/**
	 * Set version getter.
	 * 
	 * @param {Function} arg_getter - instance version getter.
	 * 
	 * @returns {nothing}
	 */
	set_version_getter(arg_getter)
	{
		if ( T.isFunction(arg_getter) )
		{
			this.$version_getter = arg_getter
		}
	}



	/**
	 * Get instance version.
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {string}
	 */
	get_version(arg_item)
	{
		if ( T.isObject(arg_item) && arg_item instanceof Instance )
		{
			return T.isFunction(this.$version_getter) ? this.$version_getter(arg_item) : undefined
		}

		return undefined
	}



	/**
	 * Compare two instance versions: v1 < v2 => -1, v1 = v2 => 0, v1 > v2 => 1.
	 * 
	 * @param {string} arg_version1 - requested instance 1 version.
	 * @param {string} arg_version2 - requested instance 2 version.
	 * 
	 * @returns {integer}
	 */
	compare_versions(arg_version1, arg_version2)
	{
		return semver.compare(arg_version1, arg_version2)
		// return (arg_version1 < arg_version2) ? -1 : ( (arg_version1 == arg_version2) ? 0 : 1) // TODO
	}
	


	/**
	 * Get latest version of item by its name.
	 * 
	 * @param {string} arg_name - instance name.
	 * 
	 * @returns {Instance|undefined}
	 */
	get_latest_item(arg_name)
	{
		// console.log('collection.get_latest_item:latest=' + Object.keys(this._items_by_name_latest_version).toString())
		return this._items_by_name_latest_version ? this._items_by_name_latest_version[arg_name] : undefined
	}
	


	/**
	 * Get latest version of all items.
	 * 
	 * @returns {array}
	 */
	get_latest_items()
	{
		const items = this._items_by_name_latest_version ? _.toArray(this._items_by_name_latest_version) : []
		// console.log(items.length, context + ':get_latest_items:items.length')
		return items
	}
	
	
	
	/**
	 * Get an item by its name and its version.
	 * 
	 * @param {string} arg_name - requested instance name.
	 * @param {string} arg_version - requested instance version.
	 * 
	 * @returns {Instance|undefined}
	 */
	get_item_of_version(arg_name, arg_version)
	{
		const versions = this._items_by_name_by_version ? this._items_by_name_by_version[arg_name] : undefined
		return versions ? versions[arg_version] : undefined
	}
	
	

	/**
	 * Test if an item is inside the collection.
	 * 
	 * @param {string} arg_item_name - Instance item name.
	 * @param {string} arg_version - requested instance version.
	 * 
	 * @returns {boolean}
	 */
	has_version(arg_item_name, arg_version)
	{
		if ( ! this._items_by_name_by_version[arg_item_name] )
		{
			return false
		}
		return arg_version in this._items_by_name_by_version[arg_item_name]
	}
	
	

	/**
	 * Add an item to the collection without type checks (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	_add(arg_item)
	{
		if( this._has(arg_item) )
		{
			return
		}

		const name = arg_item.get_name()
		const id   = arg_item.get_id()
		const version = this.get_version(arg_item)

		this._items_array.push(arg_item)
		this._items_by_name[name] = arg_item
		this._items_by_id[id] = arg_item

		if (! this._items_by_name_by_version[name])
		{
			this._items_by_name_by_version[name] = {}
			this._items_by_name_latest_version[name] = undefined
		}

		this._items_by_name_by_version[name][version] = arg_item
		if ( this._items_by_name_latest_version[name] )
		{
			const prev_latest = this.get_version(this._items_by_name_latest_version[name])
			const comparison = this.compare_versions(version, prev_latest)
			if (comparison == 1)
			{
				this._items_by_name_latest_version[name] = arg_item
			}
		} else {
			this._items_by_name_latest_version[name] = arg_item
		}
	}
	
	

	/**
	 * Add an item to the collection without type checks at first position (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	_add_first(arg_item)
	{
		if( this._has(arg_item) )
		{
			return
		}

		this._add(arg_item)

		const items = this._get_all()
		items.pop()
		this._set_all( [arg_item].concat(items) )
	}
	
	

	/**
	 * Remove an item from the collection without type checks (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {nothing}
	 */
	_remove(arg_item)
	{
		const name = arg_item.get_name()
		const id   = arg_item.get_id()
		const version = this.get_version(arg_item)
		const index = this._items_array.indexOf(arg_item)

		this._items_array.splice(index, 1)
		delete this._items_by_name[name]
		delete this._items_by_id[id]

		if ( this._items_by_name_by_version[name] )
		{
			delete this._items_by_name_by_version[name][version]
		}
		delete this._items_by_name_latest_version[name]
	}
	
	

	/**
	 * Test if an item is inside the collection without type checks (unsafe).
	 * @private
	 * 
	 * @param {Instance} arg_item - Instance item.
	 * 
	 * @returns {boolean}
	 */
	_has(arg_item)
	{
		const name = arg_item.get_name()
		const version = this.get_version(arg_item)
		return (name in this._items_by_name) && this.has_version(name, version)
	}
}

// NPM IMPORTS
import assert   from 'assert'
import fs       from 'fs'
import path     from 'path'
import mustache from 'mustache'

// COMMON IMPORTS
import T            from '../utils/types'
import {is_browser} from '../utils/is_browser'

/**
 * Cryptography library.
 * @private
 */
let  forge = undefined
if ( is_browser() )
{
	forge = require('forge-browser').forge
} else {
	forge = require('node-forge')
}


/**
 * Contextual constant for this file logs.
 * @private
 */
const context = 'common/base/context'



/**
 * Runtime context methods (browser/server, locales, i18n).
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
 * 	API:
 * 		->constructor(arg_runtime):nothing - constructor.
 * 
 *  PATHS API:
 * 		->get_base_dir():string - Get project base directory, the root directory of the project.
 * 		->get_world_dir():string - Get topology  world resources directory, by default: the root directory of the project.
 * 
 * 		->get_absolute_path(arg_relative_path1, arg_relative_path2, arg_relative_path3):string - Get absolute path of given relative path.
 * 				Search strategy:
 * 				1-Joining given ordered paths
 * 				2-Joining base directory and given ordered paths
 * 				3-Joining world resources directory and given ordered paths
 * 
 * 		->get_absolute_plugin_path(arg_relative_plugin, arg_relative_path1, arg_relative_path2):string - Get absolute path of given relative plugin name.
 * 				Call get_absolute_path(arg_relative_plugin, arg_relative_path1, arg_relative_path2)
 * 
 * 		->get_absolute_package_path(arg_relative_pkg, arg_relative_path1, arg_relative_path2):string - Get absolute path of given relative package name.
 * 				Call get_absolute_path(arg_relative_pkg, arg_relative_path1, arg_relative_path2)
 * 				Call get_absolute_path( path.join('../../../node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
 * 				Call get_absolute_path( path.join('../../node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
 * 				Call get_absolute_path( path.join('../node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
 * 				Call get_absolute_path( path.join('node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
 * 
 * 		->get_absolute_public_path(arg_relative_public, arg_relative_path1, arg_relative_path2):string - Get absolute path of given relative public path.
 * 				Call get_absolute_path(arg_relative_public, arg_relative_path1, arg_relative_path2)
 * 				If not found, search with prefixes [base_public, world_public, 'public', '..', '../public', '../..', '../../public', '../../..', '../../../public']
 * 				Call get_absolute_path( join(prefix, arg_relative_public), arg_relative_path1, arg_relative_path2)
 * 
 * 		->get_absolute_resources_path(arg_relative_resource, arg_relative_path1, arg_relative_path2):string - Get absolute path of given relative resource file.
 * 				Call get_absolute_path( path.join('resources', arg_relative_resource), arg_relative_path1, arg_relative_path2)
 * 
 * 	CREDENTIALS API:
 * 		->get_credentials(arg_request):object - Get credentials.
 * 		->get_credentials_string(arg_credentials):string - Get credentials string as 'username=...&password=...&token=...'.
 * 		->get_url_with_credentials(arg_url, arg_request):string - Get given url augmented with credentials string.
 * 
 * 		->render_credentials_template(arg_html, arg_request_or_credentials):string - Render credentials template.
 * 				Call  mustache.render(arg_html, credentials_datas)
 * 				with const credentials_datas = credentials_obj.get_credentials_for_template()
 * 					credentials_datas.credentials_str = credentials_str
 * 					credentials_datas.credentials_url = credentials_url
 * 					credentials_datas.credentials_basic_base64 = base64_encoded
 * 					credentials_datas.url = '{{url}}'
 */
export default class Context
{
    /**
     * Create a context instance.
	 * 
     * @param {object} arg_runtime - current runtime.
	 * 
     * @returns {nothing}
     */
	constructor(arg_runtime)
	{
		assert( T.isObject(arg_runtime) && arg_runtime.is_server_runtime, context + ':bad runtime object')
		
		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_context = true
		
		/**
		 * Runtime instance.
		 * @type {Runtime}
		 */
		this.$runtime = arg_runtime
	}
    
    
	
	// *************************************************** FILE PATH ********************************************************
	
	/**
	 * Get project base directory, the root directory of the project.
	 * 
	 * @returns {string} - absolute root directory path.
	 */
	get_base_dir()
	{
		return this.$runtime ? this.$runtime.get_setting('base_dir') : null
	}



	/**
	 * Get topology  world resources directory, by default: the root directory of the project.
	 * 
	 * @returns {string} - absolute root directory path.
	 */
	get_world_dir()
	{
		return this.$runtime ? this.$runtime.get_setting('world_dir') : this.get_base_dir()
	}
	

	
	/**
	 * Get absolute path of given relative path.
	 * 
	 * @param {string} arg_relative_path1 - relative path 1.
	 * @param {string} arg_relative_path2 - relative path 2.
	 * @param {string} arg_relative_path3 - relative path 3.
	 * 
	 * @returns {string} - absolute path.
	 */
	get_absolute_path(arg_relative_path1, arg_relative_path2, arg_relative_path3)
	{
		assert( T.isString(arg_relative_path1), context + ':get_absolute_path: bad paths strings')
		let base_dir = path.isAbsolute(arg_relative_path1) ? '' : this.get_base_dir()
		assert( T.isString(base_dir), context + ':get_absolute_path: bad base dir string')

		// console.log(this.get_world_dir(), 'world_dir')
		// console.log('get_absolute_path:with ', arg_relative_path1, arg_relative_path2, arg_relative_path3)

		const default_extension = '.js'
		let ext = undefined
		let p0 = undefined
		let p1 = undefined
		let p2 = undefined

		if ( T.isString(arg_relative_path2) )
		{
			if ( T.isString(arg_relative_path3) )
			{
				ext = path.extname(arg_relative_path3)
				p0 = path.join(arg_relative_path1, arg_relative_path2, arg_relative_path3)
				p1 = path.join(this.get_world_dir(), arg_relative_path1, arg_relative_path2, arg_relative_path3)
				p2 = path.join(base_dir, arg_relative_path1, arg_relative_path2, arg_relative_path3)
			}
			else
			{
				ext = path.extname(arg_relative_path2)
				p0 = path.join(arg_relative_path1, arg_relative_path2)
				p1 = path.join(this.get_world_dir(), arg_relative_path1, arg_relative_path2)
				p2 = path.join(base_dir, arg_relative_path1, arg_relative_path2)
			}
		}
		else
		{
			ext = path.extname(arg_relative_path1)
			p0 = arg_relative_path1
			p1 = path.join(this.get_world_dir(), arg_relative_path1)
			p2 = path.join(base_dir, arg_relative_path1)
		}
		// console.log('get_absolute_path:', p0, p1, p2)

		if ( path.isAbsolute(p0) )
		{
			// WITHOUT DEFAULT EXTENSION
			try
			{
				const fs_stat = fs.statSync(p0)
				if ( fs_stat.isFile() || fs_stat.isDirectory() )
				{
					// console.log(context + ':get_absolute_path:path found [%s] for [%s, %s, %s]', p0, arg_relative_path1, arg_relative_path2, arg_relative_path3)
					return p0
				}
			}
			catch(e) {}

			// WITH DEFAULT EXTENSION
			try
			{
				const fs_stat = fs.statSync(p0 + default_extension)
				if ( fs_stat.isFile() || fs_stat.isDirectory() )
				{
					// console.log(context + ':get_absolute_path:path found [%s] for [%s, %s, %s]', p0, arg_relative_path1, arg_relative_path2, arg_relative_path3)
					return p0 + default_extension
				}
			}
			catch(e) {}
		}

		if ( path.isAbsolute(p1) )
		{
			// WITHOUT DEFAULT EXTENSION
			try
			{
				const fs_stat = fs.statSync(p1)
				if ( fs_stat.isFile() || fs_stat.isDirectory() )
				{
					// console.log(context + ':get_absolute_path:path found [%s] for [%s, %s, %s]', p1, arg_relative_path1, arg_relative_path2, arg_relative_path3)
					return p1
				}
			}
			catch(e) {}

			// WITH DEFAULT EXTENSION
			try
			{
				const fs_stat = fs.statSync(p1 + default_extension)
				if ( fs_stat.isFile() || fs_stat.isDirectory() )
				{
					// console.log(context + ':get_absolute_path:path found [%s] for [%s, %s, %s] with .js', p1, arg_relative_path1, arg_relative_path2, arg_relative_path3)
					return p1 + default_extension
				}
			}
			catch(e) {}
		}

		if ( path.isAbsolute(p2) )
		{
			// WITHOUT DEFAULT EXTENSION
			try
			{
				const fs_stat = fs.statSync(p2)
				if ( fs_stat.isFile() || fs_stat.isDirectory() )
				{
					// console.log(context + ':get_absolute_path:path found [%s] for [%s, %s, %s]', p2, arg_relative_path1, arg_relative_path2, arg_relative_path3)
					return p2
				}
			}
			catch(e) {}

			// WITH DEFAULT EXTENSION
			try
			{
				const fs_stat = fs.statSync(p2 + default_extension)
				if ( fs_stat.isFile() || fs_stat.isDirectory() )
				{
					// console.log(context + ':get_absolute_path:path found [%s] for [%s, %s, %s] with .js', p2, arg_relative_path1, arg_relative_path2, arg_relative_path3)
					return p2 + default_extension
				}
			}
			catch(e) {}
		}

		
		// if (ext == '')
		// {
		// 	const path_1_is_directory = path.dirname(arg_relative_path1) == arg_relative_path1
		// 	const path_2_is_directory = path.dirname(arg_relative_path2) == arg_relative_path2
		// 	const path_3_is_directory = path.dirname(arg_relative_path3) == arg_relative_path3

		// 	const part_1 = (! path_1_is_directory && arg_relative_path1 && ! arg_relative_path2) ? arg_relative_path1 + '.js' : arg_relative_path1
		// 	const part_2 = (! path_2_is_directory && arg_relative_path2 && ! arg_relative_path3) ? arg_relative_path2 + '.js' : arg_relative_path2
		// 	const part_3 = arg_relative_path3 ? arg_relative_path3 + (! path_3_is_directory ? '.js' : '') : undefined

		// 	const absolute_path = this.get_absolute_path(part_1, part_2, part_3)
		// 	return absolute_path
		// }

		console.error(context + ':get_absolute_path:path not found [\n%s, \n%s, \n%s\n] for [\n%s, \n%s, \n%s\n]', p0, p1, p2, arg_relative_path1, arg_relative_path2, arg_relative_path3)
		return undefined
	}
	
	

	/**
	 * Get absolute path of given relative plugin name.
	 * 
	 * @param {string} arg_relative_plugin - plugin name.
	 * @param {string} arg_relative_path1 - relative path 1.
	 * @param {string} arg_relative_path2 - relative path 2.
	 * 
	 * @returns {string} - absolute path.
	 */
	get_absolute_plugin_path(arg_relative_plugin, arg_relative_path1, arg_relative_path2)
	{
		assert( T.isString(arg_relative_plugin), context + 'get_absolute_plugin_path: bad plugin name string')
		if ( path.isAbsolute(arg_relative_plugin) )
		{
			return this.get_absolute_path(arg_relative_plugin, arg_relative_path1, arg_relative_path2)
		}
		return this.get_absolute_path('plugins', arg_relative_plugin, arg_relative_path1, arg_relative_path2)
	}
	
	

	/**
	 * Get absolute path of given relative package name.
	 * 
	 * @param {string} arg_relative_pkg - package name.
	 * @param {string} arg_relative_path1 - relative path 1.
	 * @param {string} arg_relative_path2 - relative path 2.
	 * 
	 * @returns {string} - absolute path.
	 */
	get_absolute_package_path(arg_relative_pkg, arg_relative_path1, arg_relative_path2)
	{
		assert( T.isString(arg_relative_pkg), context + 'get_absolute_package_path: bad package name string')
		if ( path.isAbsolute(arg_relative_pkg) )
		{
			return this.get_absolute_path(arg_relative_pkg, arg_relative_path1, arg_relative_path2)
		}
		// debugger
		
		let p = this.get_absolute_path( path.join('../../../node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
		// console.log('get_absolute_package_path:../../../node_modules:', p)
		if (p)
		{
			return p
		}
		
		p = this.get_absolute_path( path.join('../../node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
		// console.log('get_absolute_package_path:../../../node_modules:', p)
		if (p)
		{
			return p
		}
		
		p = this.get_absolute_path( path.join('../node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
		// console.log('get_absolute_package_path:../../../node_modules:', p)
		if (p)
		{
			return p
		}

		return this.get_absolute_path( path.join('node_modules', arg_relative_pkg), arg_relative_path1, arg_relative_path2)
	}
	
	

	/**
	 * Get absolute path of given relative public path.
	 * 
	 * @param {string} arg_relative_public - public path.
	 * @param {string} arg_relative_path1 - relative path 1.
	 * @param {string} arg_relative_path2 - relative path 2.
	 * 
	 * @returns {string} - absolute path.
	 */
	get_absolute_public_path(arg_relative_public, arg_relative_path1, arg_relative_path2)
	{
		assert( T.isString(arg_relative_public), context + 'get_absolute_public_path: bad path string')
		
		if ( path.isAbsolute(arg_relative_public) )
		{
			// console.log('get_absolute_public_path:absolute:', arg_relative_public)
			return this.get_absolute_path(arg_relative_public, arg_relative_path1, arg_relative_path2)
		}
		
		const base_public = path.join(this.get_base_dir(), 'public')
		const world_public = path.join(this.get_world_dir(), 'public')
		const searchs = [base_public, world_public, 'public', '..', '../public', '../..', '../../public', '../../..', '../../../public']
		// console.log('get_absolute_public_path:base_public:', base_public)
		// console.log('get_absolute_public_path:world_public:', world_public)

		let p = undefined
		let index = 0
		let prefix = undefined
		while( ! p && index < searchs.length )
		{
			prefix = searchs[index]
			p = this.get_absolute_path( path.join(prefix, arg_relative_public), arg_relative_path1, arg_relative_path2)
			// console.log('get_absolute_public_path:prefix=%s:', prefix, p)
			++index
		}
		
		return p
	}
	

	
	/**
	 * Get absolute path of given relative resource file.
	 * 
	 * @param {string} arg_relative_resource - resource file name.
	 * @param {string} arg_relative_path1 - relative path 1.
	 * @param {string} arg_relative_path2 - relative path 2.
	 * 
	 * @returns {string} - absolute path.
	 */
	get_absolute_resources_path(arg_relative_resource, arg_relative_path1, arg_relative_path2)
	{
		assert( T.isString(arg_relative_resource), context + 'get_absolute_resources_path: bad resource file string')
		return this.get_absolute_path( path.join('resources', arg_relative_resource), arg_relative_path1, arg_relative_path2)
	}
	
	
	

	// *************************************************** URL ********************************************************
	
    /**
     * Get credentials.
	 * 
     * @param {object} arg_request - request object.
	 * 
     * @returns {object} credentials plain object.
     */
	get_credentials(arg_request)
	{
		// logs.debug('get_credentials')

		const auth_mgr = this.$runtime ? this.$runtime.security().authentication() : null
		if (! auth_mgr)
		{
			return undefined
		}
		
		if ( ! arg_request )
		{
			return undefined
		}
		
		const credentials = auth_mgr.get_credentials(arg_request)
		return credentials
	}
	
	
	
    /**
     * Get credentials string.
	 * 
     * @param {object} arg_credentials - Credetials object.
	 * 
     * @returns {string} credentials string.
     */
	get_credentials_string(arg_credentials)
	{
		if (! arg_credentials)
		{
			return ''
		}
		
		// TODO: use security token
		return 'username=' + arg_credentials.get_user() + '&password=' + arg_credentials.get_pass_digest() + '&token=' + arg_credentials.get_token()
	}
	
	
	
    /**
     * Get given url augmented with credentials string.
	 * 
     * @param {string} arg_url - image asset relative url.
     * @param {object|Credentials} arg_request_or_credentials - request object or Credentials instance.
	 * 
     * @returns {string} absolute url.
     */
	get_url_with_credentials(arg_url, arg_request_or_credentials)
	{
		// logs.debug('get_url_with_credentials')
		
		
		// TODO ARGS = REQUEST OR CRENDETIALS ??

		// NO CREDENTIALS, NO REQUEST
		if ( ! arg_request_or_credentials )
		{
			return arg_url + '?{{credentials_url}}'
		}
	
		// GET CREDENTIALS
		const credentials = arg_request_or_credentials.is_credentials ? arg_request_or_credentials : (arg_request_or_credentials.credentials ? arg_request_or_credentials.credentials : undefined)
		
		// GET CREDENTIALS STRING
		const credentials_str = this.get_credentials_string(credentials)
		if (credentials_str)
		{
			return arg_url + '?' + credentials_str
		}
		
		return arg_url
	}
    
	
	
	/**
     * Render credentials template.
	 * 
     * @param {string} arg_html - template html string.
     * @param {Request|Credentials} arg_request_or_credentials - request object.
	 * 
     * @returns {string} rendered template.
     */
	render_credentials_template(arg_html, arg_request_or_credentials)
	{
		assert( T.isObject(arg_request_or_credentials), context + ':render_credentials_template:bad arg_request_or_credentials object')

		let credentials_obj = arg_request_or_credentials.is_credentials ? arg_request_or_credentials : this.get_credentials(arg_request_or_credentials)
		let credentials_str = this.get_credentials_string(credentials_obj)
		let credentials_url = this.get_credentials_string(credentials_obj)
		// console.log(credentials_str, 'credentials_str')
		
		
		// TODO 2 cases:
		/*
			url:string
			var:JSON.stringiy(object)
		*/
		
		
		
		if (credentials_str)
		{
			const base64_encoded = forge.util.encode64(credentials_obj.username + ':' + credentials_obj.password)

			const credentials_datas = credentials_obj.get_credentials_for_template()
			credentials_datas.credentials_str = credentials_str
			credentials_datas.credentials_url = credentials_url
			credentials_datas.credentials_basic_base64 = base64_encoded
			credentials_datas.url = '{{url}}'

			// 	credentials_token:credentials_obj.token,
			// 	credentials_user_name:credentials_obj.username,
			// 	credentials_pass_digest:credentials_obj.password,
				
			// 	credentials_login:credentials_obj.ts_login,
			// 	credentials_expire:credentials_obj.expire,

			// 	credentials_basic_base64:base64_encoded
			// 	// credentials_obj: `{ \"username\":\"${credentials_obj.username}\", "password":"${credentials_obj.password}" }`
			// }
			return mustache.render(arg_html, credentials_datas)
		}
		
		return arg_html
	}
	
	
    
	// TODO:TO CLEAN OR IMPLEMENT
	// get_relative_url(arg_url)
    // {
	// 	return arg_url
	// }
    
    
	// get_absolute_url(arg_url)
	// {
	// 	return arg_url
	// }


	// get_absolute_plugin_url(arg_plugin)
	// {
	// 	return arg_plugin
	// }
}

// NPM IMPORTS
import assert     from 'assert'
import util       from 'util'
import { fromJS } from 'immutable'

// COMMON IMPORTS
import T              from '../utils/types'
import Digest         from '../utils/digest'
import EncodeDecode   from '../utils/encode_decode'
import EncryptDecrypt from '../utils/encrypt_decrypt'


/**
 * Contextual constant for this file logs.
 * @private
 */
const context = 'common/base/credentials'



/**
 * Default credentials record.
 * @private
 */
const default_credentials = {
	tenant:'default',		// Tenant name (mandatory)
	env:undefined,			// Environment code: dev, test, int, rec, pro (mandatory)
	application:undefined,	// Application name (mandatory)

	token:undefined,
	user_name:undefined,
	user_pass_digest:undefined,

	ts_login:undefined,
	ts_expiration:undefined,

	errors_count:0,
	renew_count:0,

	hash:undefined
}



/**
 * Authentication informations, used on server and on browser.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
* @example
* 	API:
* 		->set_credentials(arg_datas):nothing - check and set crendtials datas.
* 		
* 		->dump():string - dump credentials to a readable string without sensitive datas.
* 		->digest_password(arg_string):string - hash credentials password (static).
* 		
* 		->serialize(arg_app_secret):string - transform credentials to a string.
* 		->deserialize(string, arg_app_secret):boolean - load credentials from a string.string
* 
* 		->encrypt(arg_app_key):string - encrypted string.
* 		->decrypt(arg_string, arg_app_key):boolean - success or failure.
* 
* 		->extract(arg_string):object - extract a record with credentials values from a formatted string.
 */
export default class Credentials
{
    /**
     * Create a Credentials instance. Store credentials values into an immutable Map.
	 * 
     * @param {object} arg_datas - credentials datas object.
	 * 
     * @returns {nothing}
     */
	constructor(arg_datas=default_credentials, update_handler=undefined)
	{
		assert( T.isObject(arg_datas), context + ':bad runtime object')
		
		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_credentials = true

		/**
		 * Credentials settings.
		 * @type {Immutable.Map}
		 */
		this._credentials = fromJS(default_credentials)

		// this._crypt_mode = 'AES-CBC' // (other modes include: CFB, OFB, CTR, and GCM)
		// this._crypt_key_length = 32
		// this._crypt_iv_length = 8
		// this._crypt_num_iterations = 10

		/**
		 * Update handler.
		 * @type {function}
		 */
		this.update_handler = update_handler

		this.set_credentials(arg_datas)
	}
    
    
	
	/**
	 * Get credentials datas.
	 * 
	 * @returns {object}
	 */
	get_credentials()
	{
		const datas = this._credentials.toJS()
		return datas
	}
    
    
	
	/**
	 * Get credentials tenant.
	 * 
	 * @returns {string|undefined}
	 */
	get_tenant()
	{
		return this._credentials.get('tenant', undefined)
	}
    
    
	
	/**
	 * Get credentials environment.
	 * 
	 * @returns {string|undefined}
	 */
	get_env()
	{
		return this._credentials.get('env', undefined)
	}
    
    
	
	/**
	 * Get credentials application.
	 * 
	 * @returns {string|undefined}
	 */
	get_application()
	{
		return this._credentials.get('application', undefined)
	}
    
    
	
	/**
	 * Get credentials token.
	 * 
	 * @returns {string|undefined}
	 */
	get_token()
	{
		return this._credentials.get('token', undefined)
	}
    
    
	
	/**
	 * Get credentials user.
	 * 
	 * @returns {string|undefined}
	 */
	get_user()
	{
		return this._credentials.get('user_name', undefined)
	}
    
    
	
	/**
	 * Get credentials user.
	 * 
	 * @returns {string|undefined}
	 */
	get_pass_digest()
	{
		return this._credentials.get('user_pass_digest', undefined)
	}
    
    
	
	/**
	 * Get credentials url part.
	 * 
	 * @returns {string}
	 */
	get_url_part()
	{
		return 'username=' + this.get_user() + '&password=' + this.get_pass_digest() + '&token=' + this.get_token()
	}
    
    
	
	/**
	 * Get credentials datas.
	 * 
	 * @returns {object}
	 */
	get_credentials_for_template()
	{
		const credentials_obj = this._credentials.toJS()
		const template_datas = {
			credentials_tenant:credentials_obj.tenant,
			credentials_env:credentials_obj.env,
			credentials_application:credentials_obj.application,

			credentials_token:credentials_obj.token,
			credentials_user_name:credentials_obj.user_name,
			credentials_pass_digest:credentials_obj.user_pass_digest,
			
			credentials_login:credentials_obj.ts_login,
			credentials_expire:credentials_obj.ts_expiration,

			credentials_errors_count:credentials_obj.errors_count,
			credentials_renew_count:credentials_obj.renew_count,

			credentials_hash:credentials_obj.hash
		}
		return template_datas
	}
    
	

	/**
	 * Get empty credentials record.
	 * 
	 * @returns {object}
	 */
	static get_empty_credentials()
	{
		return default_credentials
	}
    
    
	
			
	/**
	 * Credentials are valid for access.
	 * 
	 * @returns {boolean}
	 */
	is_valid() // TODO
	{
		return true
	}

    
	
	/**
	 * Check and set credentials datas.
	 * 
     * @param {object} arg_datas - credentials datas object.
	 * 
	 * @returns {boolean}
	 */
	set_credentials(arg_datas)
	{
		assert( T.isObject(arg_datas), context + ':set_credentials:bad arg_datas object')

		assert( T.isString(arg_datas.tenant) && arg_datas.tenant.length > 0, context + ':set_credentials:bad tenant string')
		assert( T.isString(arg_datas.env) && arg_datas.env.length > 0, context + ':set_credentials:bad env string')
		assert( T.isString(arg_datas.application) && arg_datas.application.length > 0, context + ':set_credentials:bad application string')

		assert( T.isString(arg_datas.token), context + ':set_credentials:bad token string')
		assert( T.isString(arg_datas.user_name), context + ':set_credentials:bad user_name string')
		assert( T.isString(arg_datas.user_pass_digest), context + ':set_credentials:bad user_pass_digest string')

		if ( T.isString(arg_datas.ts_login) )
		{
			try{
				arg_datas.ts_login = parseInt(arg_datas.ts_login)
			}
			catch(e)
			{}
		}
		if ( T.isString(arg_datas.ts_expiration) )
		{
			try{
				arg_datas.ts_expiration = parseInt(arg_datas.ts_expiration)
			}
			catch(e)
			{}
		}
		assert( T.isNumber(arg_datas.ts_login), context + ':set_credentials:bad ts_login number')
		assert( T.isNumber(arg_datas.ts_expiration), context + ':set_credentials:bad ts_expiration number')

		if ( T.isString(arg_datas.errors_count) )
		{
			try{
				arg_datas.errors_count = parseInt(arg_datas.errors_count)
			}
			catch(e)
			{}
		}
		if ( T.isString(arg_datas.renew_count) )
		{
			try{
				arg_datas.renew_count = parseInt(arg_datas.renew_count)
			}
			catch(e)
			{}
		}
		assert( T.isNumber(arg_datas.errors_count), context + ':set_credentials:bad errors_count number')
		assert( T.isNumber(arg_datas.renew_count), context + ':set_credentials:bad renew_count number')

		let datas = {}
		datas.tenant = arg_datas.tenant
		datas.env = arg_datas.env
		datas.application = arg_datas.application

		datas.token = arg_datas.token
		datas.user_name = arg_datas.user_name
		datas.user_pass_digest = arg_datas.user_pass_digest

		datas.ts_login = arg_datas.ts_login
		datas.ts_expiration = arg_datas.ts_expiration

		datas.errors_count = arg_datas.errors_count
		datas.renew_count = arg_datas.renew_count

		const head = util.format('tenant:%s,env:%s,app:%s', datas.tenant, datas.env, datas.application)
		const auth = util.format('token:%s,user:%s,pass:%s', datas.token ? datas.token : 'none', datas.user_name ? datas.user_name : 'none', datas.user_pass_digest ? datas.user_pass_digest : 'none')
		const str = util.format('CREDENTIALS:{%s,%s}', head, auth)
		datas.hash = Digest.hash(str, 'sha256', 'hex')

		this._credentials = fromJS(datas)

		if ( T.isFunction(this.update_handler) )
		{
			this.update_handler(this._credentials)
		}

		return true
	}



	/**
	 * Dump credentials datas to a readable string without sensitive datas.
	 * 
	 * @returns {string} - readable string without sensitive datas.
	 */
	dump()
	{
		const datas = this._credentials.toJS()
		const head = util.format('tenant:%s,env:%s,app:%s', datas.tenant, datas.env, datas.application)
		const auth = util.format('token:%s,user:%s,pass:%s', datas.token ? datas.token : 'none', datas.user_name ? datas.user_name : 'none', datas.user_pass_digest ? '***' : 'none')
		const foot = util.format('start:%n,expire:%n,errors:%n,renews:%n', datas.ts_login, datas.ts_expiration, datas.errors_count, datas.renew_count)
		
		return util.format('CREDENTIALS:{%s,%s,%s}', head, auth, foot)
	}



	/**
	 * Transform credentials password with a hash method.
	 * 
	 * @param {string} arg_password - password string.
	 * 
	 * @returns {string} - hashed string.
	 */
	static digest_password(arg_password)
	{
		return Digest.hash(arg_password, 'sha256', 'hex')
	}



	/**
	 * Transform credentials to a string without encryption which could be sent over a network.
	 * 
	 * @returns {string} - serialized string.
	 */
	serialize()
	{
		const datas = this._credentials.toJS()
		const head = util.format('tenant:%s,env:%s,app:%s', datas.tenant, datas.env, datas.application)
		const auth = util.format('token:%s,user:%s,pass:%s', datas.token ? datas.token : 'none', datas.user_name ? datas.user_name : 'none', datas.user_pass_digest ? datas.user_pass_digest : 'none')
		const str = util.format('CREDENTIALS:{%s,%s}', head, auth)

		return EncodeDecode.encode(str, 'base64')
	}



	/**
	 * Deserialize credentials from a string without encryption.
	 * @TODO: use RegExp to split the string into credentials attributes.
	 * 
	 * @param {string} arg_string - serialized string.
	 * 
	 * @returns {boolean} - deserialized string success or failure.
	 */
	deserialize(arg_string)
	{
		const str = EncodeDecode.decode(arg_string, 'base64')
		const record = this.extract(str)

		return this.set_credentials(record)
	}



	/**
	 * Transform credentials to a string with encryption.
	 * 
	 * @param {string} arg_app_key - encryption key.
	 * 
	 * @returns {string} - encrypted string.
	 */
	encrypt(arg_app_key)
	{
		const datas = this._credentials.toJS()
		const head = util.format('tenant:%s,env:%s,app:%s', datas.tenant, datas.env, datas.application)
		const auth = util.format('token:%s,user:%s,pass:%s', datas.token ? datas.token : 'none', datas.user_name ? datas.user_name : 'none', datas.user_pass_digest ? datas.user_pass_digest : 'none')
		const str = util.format('CREDENTIALS:{%s,%s}', head, auth)

		return EncryptDecrypt.encrypt(str, arg_app_key)
	}



	/**
	 * Decrypt credentials encrypted string.
	 * 
	 * @param {string} arg_string - string.
	 * @param {string} arg_app_key - encryption key.
	 * 
	 * @returns {string} - encrypted string.
	 */
	decrypt(arg_string, arg_app_key)
	{
		const str = EncryptDecrypt.decrypt(arg_string, arg_app_key)
		const record = this.extract(str)

		return this.set_credentials(record)
	}



	/**
	 * Extract credentials fields from a string (password should be hashed and encoded, only [a-zA-Z0-9_] chars).
	 * 
	 * @param {string} arg_string - string as "CREDENTIALS:{tenant:%s,env:%s,app:%s,token:%s,user:%s,pass:%s}".
	 * 
	 * @returns {object} - credentials values record.
	 */
	extract(arg_string)
	{
		const regexp = /CREDENTIALS:{([a-zA-Z0-9_:]+),([a-zA-Z0-9_:]+),([a-zA-Z0-9_:]+),([a-zA-Z0-9_:]+),([a-zA-Z0-9_:]+),([a-zA-Z0-9_:]+)}/
		const results = regexp.exec(arg_string)

		if (results.length != 6)
		{
			console.log(context + ':extract:bad format [%s]', arg_string)
			return false
		}
		
		let values = []
		results.forEach(
			(str)=>{
				const parts = str.split(':')
				const value = parts.length == 2 ? parts[1] : undefined
				values.push(value)
			}
		)

		const record = {
			tenant:results[0],
			env:results[1],
			application:results[2],

			token:results[3],
			user_name:results[4],
			user_pass_digest:results[5]
		}

		return record
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T                       from '../utils/types'
import Instance                from '../base/instance'
import DistributedMessage      from '../base/distributed_message'
import DistributedMetrics      from '../base/distributed_metrics'
import DistributedLogs         from '../base/distributed_logs'


/**
 * Contextual constant for this file logs.
 * @private
 */
const context = 'common/base/distributed_instance'



/**
 * Distributed instances base class: enable communication inside a node or between nodes.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* API:
* 		->load():nothing
* 	    ->load_topology_settings(arg_settings):nothing
* 
* 		->send(DistributedMessage|DistributedMetrics|DistributedLogs):boolean
* 
*      ->enable_on_bus(arg_bus):nothing
*      ->disable_on_bus(arg_bus):nothing
* 
* API for messages:
* 		->send_msg(target, payload):boolean
* 		->receive_msg(DistributedMessage):nothing
* 		->enable_msg():nothing
* 		->disable_msg():nothing
* 
* API for metrics:
* 		->send_metrics(type, values):boolean
* 		->receive_metrics(DistributedMetrics):nothing
* 		->enable_metrics():nothing
* 		->disable_metrics():nothing
* 
* API for logs:
* 		->send_logs(ts, level, texts):boolean
* 		->receive_logs(DistributedLogs):nothing
* 		->enable_logs():nothing
* 		->disable_logs():nothing
 * 
 */
export default class DistributedInstance extends Instance
{
	/**
	 * Create a DistributedInstance.
	 * 
	 * @param {string} arg_collection - collection name.
	 * @param {string} arg_name - server name
	 * @param {string} arg_class - server class name
	 * @param {object} arg_settings - plugin settings map
	 * @param {string} arg_log_context - trace context string (optional, default=context).
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_collection, arg_name, arg_class, arg_settings, arg_log_context=context)
	{
		assert( T.isObject(arg_settings.runtime) || (arg_settings.has && arg_settings.has('runtime')), arg_log_context + ':bad runtime instance')
		assert( T.isObject(arg_settings.logger_manager) || (arg_settings.has && arg_settings.has('logger_manager') ), arg_log_context + ':bad logger_manager instance')
		super(arg_collection, arg_class, arg_name, arg_settings, arg_log_context, arg_settings.logger_manager)
		
		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_distributed_instance = true

		/**
		 * Messages bus instance.
		 * @type {MessageBus}
		 */
		this._msg_bus = undefined

		/**
		 * Metrics messages bus instance.
		 * @type {MessageBus}
		 */
		this._metrics_bus = undefined
		
		/**
		 * Logs messages bus instance.
		 * @type {MessageBus}
		 */
		this._logs_bus = undefined
		
		/**
		 * Bus unsubscribes handlers map.
		 * @type {object}
		 */
		this._bus_unsubscribes = {}

		// DEBUG
		// this.enable_trace()
	}


	
	/**
	 * Load instance settings.
	 * 
	 * @returns {nothing}
	 */
	load()
	{
		// console.log(context + ':load:DistributedInstance')

		super.load()

		// REGISTER BUSES
		if (! this.is_client_runtime)
		{
			this._msg_bus     = this.get_runtime().node.get_msg_bus()
			this._metrics_bus = this.get_runtime().node.get_metrics_bus()
			this._logs_bus    = this.get_runtime().node.get_logs_bus()
		}

		// DEBUG
		// console.log(context + ':load:name=%s this._metrics_bus', this.get_name(), this._metrics_bus.get_name())
	}


	
	/**
	 * Load topology settings.
	 * 
	 * @param {object} arg_settings - master node settings.
	 * 
	 * @returns {nothing}
	 */
	load_topology_settings(arg_settings)
	{
		this.enter_group('load_topology_settings')

		arg_settings = undefined

		this.leave_group('load_topology_settings')
		return arg_settings
	}



	/**
	 * Send a message to an other client.
	 * 
	 * @param {DistributedMessage} arg_msg - message object: a DistributedMessage or DistributedMetrics or DistributedLogs instance.
	 * 
	 * @returns {boolean} - message send or not
	 */
	send(arg_msg)
	{
		assert( T.isObject(arg_msg), context + ':send:bad message object')

		if (this._msg_bus && arg_msg.is_distributed_message)
		{
			this._msg_bus.msg_post(arg_msg)
			return true
		}

		if (this._metrics_bus && arg_msg.is_distributed_metrics)
		{
			this._metrics_bus.msg_post(arg_msg)
			return true
		}
		
		if (this._logs_bus && arg_msg.is_distributed_logs)
		{
			this._logs_bus.msg_post(arg_msg)
			return true
		}

		assert(false, context + ':send:bad message type: not msg or metrics or logs')

		return false
	}


	
	/**
	 * Enable distributed messaging.
	 * 
	 * @param {MessageBus} arg_bus - message bus.
	 * @param {string} arg_channel - channel name string (default='default').
	 * @param {string} arg_method  - receiveing method name string (default='receive_msg').
	 * @param {string} arg_alias   - instance alias name string (optional, default undefined).
	 * 
	 * @returns {nothing}
	 */
	enable_on_bus(arg_bus, arg_channel='default', arg_method='receive_msg', arg_alias=undefined)
	{
		const bus_name = arg_bus.get_name() + ':' + arg_channel
		this._bus_unsubscribes[bus_name] = arg_bus.msg_register(this, arg_channel, arg_method, arg_alias)
	}


	
	/**
	 * Disable distributed messaging.
	 * 
	 * @param {MessageBus} arg_bus - message bus.
	 * @param {string} arg_channel - bus channel string (default='default').
	 * 
	 * @returns {nothing}
	 */
	disable_on_bus(arg_bus, arg_channel='default')
	{
		const bus_name = arg_bus.get_name() + ':' + arg_channel
		const unsubscribe = this._bus_unsubscribes[bus_name]
		if ( T.isFunction(unsubscribe) )
		{
			unsubscribe()
		}
	}



	// -------------------------------- MESSAGES -------------------------------------
	
	/**
	 * Create and send a message to an other client.
	 * 
	 * @param {string} arg_target_name - recipient name.
	 * @param {object} arg_payload - message payload plain object.
	 * @param {string} arg_channel - channel name string (default='default').
	 * 
	 * @returns {boolean} - message send or not.
	 */
	send_msg(arg_target_name, arg_payload, arg_channel)
	{
		// DEBUG
		// this.enable_trace()

		this.enter_group('send_msg')

		let msg = new DistributedMessage(this.get_name(), arg_target_name, arg_payload, arg_channel)
		
		if (this._msg_bus && msg.check_msg_format(msg) )
		{
			this._msg_bus.msg_post(msg)
			this.leave_group('send_msg')
			return true
		}

		this.leave_group('send_msg:bad format')
		return false
	}
	
	
	
	/**
	 * Process received message (to override in sub classes).
	 * 
	 * @param {DistributedMessage} arg_msg - message instance.
	 * 
	 * @returns {nothing}
	 */
	receive_msg(arg_msg)
	{
		this.enter_group('receive_msg')
		// console.log(context + ':receive_msg:from=%s', arg_msg.sender, arg_msg.payload)

		// DO NOT PROCESS MESSAGES FROM SELF
		if (arg_msg.sender == this.get_name())
		{
			this.leave_group('receive_msg:ignore message from itself')
			return
		}

		this.leave_group('receive_msg')
	}


	
	/**
	 * Enable distributed messaging.
	 * 
	 * @returns {nothing}
	 */
	enable_msg()
	{
		this.enable_on_bus(this._msg_bus, 'default', 'receive_msg')
	}


	
	/**
	 * Disable distributed messaging.
	 * 
	 * @returns {nothing}
	 */
	disable_msg()
	{
		this.disable_on_bus(this._msg_bus, 'default')
	}
    
    
	
	// -------------------------------- METRICS -------------------------------------

	/**
	 * Send a metrics message.
	 * 
	 * @param {string} arg_target_name - recipient name.
	 * @param {string} arg_metric_type - type of metrics.
	 * @param {array} arg_metrics - metrics values array.
	 * 
	 * @returns {boolean} - message send or not
	 */
	send_metrics(arg_target_name, arg_metric_type, arg_metrics)
	{
		this.enter_group('send_metrics')
		
		assert( T.isObject(this._metrics_bus), context + ':send_metrics:bad metrics bus object')

		// console.log(context + ':send_metrics:from=%s, to=%s, type=%s', this.get_name(), arg_target_name, arg_metric_type)

		let msg = new DistributedMetrics(this.get_name(), arg_target_name, arg_metric_type, arg_metrics)
		
		if (this._metrics_bus && msg.check_msg_format(msg) )
		{
			this._metrics_bus.msg_post(msg)

			// console.log(context + ':send_metrics:from=%s, to=%s, type=%s', this.get_name(), arg_target_name, arg_metric_type)
			
			this.leave_group('send_metrics')
			return true
		}
		
		console.error(context + ':send_metrics:BAD FORMAT:from=%s, to=%s, type=%s, values=', this.get_name(), arg_target_name, arg_metric_type, arg_metrics)

		this.leave_group('send_metrics:bad format')
		return false
	}
	
	
	
	/**
	 * Process received metrics message (to override in sub classes).
	 * 
	 * @param {DistributedMetrics} arg_msg - metrics message instance.
	 * 
	 * @returns {nothing}
	 */
	receive_metrics(arg_msg)
	{
		this.enter_group('receive_metrics')
		// console.log(context + ':receive_metrics:from=%s', arg_msg.sender, arg_msg.payload)

		// DO NOT PROCESS MESSAGES FROM SELF
		if (arg_msg.sender == this.get_name())
		{
			this.leave_group('receive_metrics:ignore message from itself')
			return
		}

		this.leave_group('receive_metrics')
	}


	
	/**
	 * Enable distributed metrics.
	 * 
	 * @returns {nothing}
	 */
	enable_metrics()
	{
		this.enable_on_bus(this._metrics_bus, 'metrics', 'receive_metrics')
	}


	
	/**
	 * Disable distributed metrics.
	 * 
	 * @returns {nothing}
	 */
	disable_metrics()
	{
		this.disable_on_bus(this._metrics_bus, 'metrics')
	}



	// -------------------------------- LOGS -------------------------------------

	/**
	 * Send a logs message.
	 * 
	 * @param {string} arg_target_name - recipient name.
	 * @param {string} arg_timestamp - logs timestamp string.
	 * @param {string} arg_level - logs level string.
	 * @param {array} arg_values - logs values array.
	 * 
	 * @returns {boolean} - message send or not
	 */
	send_logs(arg_target_name, arg_timestamp, arg_level, arg_values)
	{
		this.enter_group('send_logs')

		let msg = new DistributedLogs(this.get_name(), arg_target_name, arg_timestamp, arg_level, arg_values)
		
		if (this._logs_bus && msg.check_msg_format(msg) )
		{
			this._logs_bus.msg_post(msg)
			this.leave_group('send_logs')
			return true
		}

		this.leave_group('send_logs:bad format')
		return false
	}
	
	
	
	/**
	 * Process received logs message (to override in sub classes).
	 * 
	 * @param {DistributedLogs} arg_msg - logs message instance.
	 * 
	 * @returns {nothing}
	 */
	receive_logs(arg_msg)
	{
		this.enter_group('receive_logs')

		// console.log(context + ':receive_logs:from=%s', arg_msg.sender, arg_msg.payload)

		// DO NOT PROCESS MESSAGES FROM SELF
		if (arg_msg.sender == this.get_name())
		{
			this.leave_group('receive_logs:ignore message from itself')
			return
		}

		this.leave_group('receive_logs')
	}


	
	/**
	 * Enable distributed logs.
	 * 
	 * @returns {nothing}
	 */
	enable_logs()
	{
		this.enable_on_bus(this._logs_bus, 'logs', 'receive_logs')
	}


	
	/**
	 * Disable distributed logs.
	 * 
	 * @returns {nothing}
	 */
	disable_logs()
	{
		this.disable_on_bus(this._logs_bus, 'logs')
	}
}

