* 
*		->validate_record(arg_record):Promise(boolean) - test if given datas are valid for collection model.
*		->new_record(arg_record_datas, arg_record_id) - create a new record instance.
* 		->create_record(arg_record):Promise(DataRecord) - create an existing unsaved data record.
* 		->delete_record(arg_record):Promise(boolean) - delete an existing data record.
* 		->update_record(arg_record):Promise(DataRecord) - update an existing data record.
* 		->reload_record(arg_record):Promise(DataRecord) - reload an existing data record.
* 		->has_record(arg_record_id):Promise(boolean) - test if a data record exists with an id.
* 		
* 		->find_one_record(arg_record_id):Promise(DataRecord) - find an existing data record with an id.
* 		->find_records(arg_query):Promise(DataRecordArray) - find existing data records with a query.
* 		->find_all_records():Promise(DataRecordArray) - find all xisting data records.
* 
* 	PRIVATE:
* 		->_emit(arg_event, arg_datas=undefined):nothing
* 		->_trigger(arg_event, arg_datas):nothing
* 		->_has_cached_record_by_id(arg_id):Promise(boolean)
* 		->_get_cached_record_by_id(arg_id):Promise(DataRecord)
* 		->_set_cached_record_by_id(arg_record):Promise(boolean)
* 		->_remove_cached_record_by_id(arg_id):Promise(boolean)
* 
* 
* 	USAGE ON BROWSER:
* 		// ds is a DataStore instance
* 		var cars = ds.get_collection('cars')
* 		var car_12 = cars.find_one_record('12')
* 
* 
 */
export default class DataCollection extends Loggable
{
	/**
	 * DataCollection class is responsible to manage one model records from one adapter:
	 *  all records operations, cached records, model logic (field value validation, triggers).
	 * DataCollection instances are managed by a DataStore instance.
	 * 
	 * @param {CacheManager} arg_cache_manager - cache manager instance.
	 * @param {DataAdapter} arg_data_adapter - collection data adapter.
	 * @param {array} arg_model_schema - topology model schema.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_cache_manager, arg_data_adapter, arg_model_schema)
	{
		assert( T.isObject(arg_cache_manager) && arg_cache_manager.is_cache_manager, context + ':constructor:bad cache manager object')
		assert( T.isObject(arg_data_adapter) && arg_data_adapter.is_data_adapter, context + ':constructor:bad data adapter object')
		assert( T.isObject(arg_model_schema) && arg_model_schema.is_topology_model, context + ':constructor:bad model schema object')
		
		const model_plural_name = arg_model_schema.get_plural_name()
		assert( T.isString(model_plural_name) && model_plural_name.length > 0, context + ':constructor:bad collection name string')

		super(context)

		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_data_collection = true
		
		/**
		 * CacheManager instance.
		 * @type {CacheManager}
		 */
		this._cache_manager = arg_cache_manager
		
		/**
		 * Datas adapter instance.
		 * @type {DataAdapter}
		 */
		this._adapter = arg_data_adapter
		
		/**
		 * Topology model schema instance.
		 * @type {array}
		 */
		this._schema = arg_model_schema
		
		/**
		 * Datas collection prefix.
		 * @type {string}
		 */
		this._cache_prefix = model_plural_name
		
		/**
		 * Datas collection name.
		 * @type {string}
		 */
		this._name = model_plural_name
	}



	/**
	 * Emit on event.
	 * @private
	 * 
	 * @param {string} arg_event - event name.
	 * @param {any} arg_datas - event datas (optional, default:undefined).
	 * 
	 * @returns {nothing}
	 */
	_emit(arg_event, arg_datas=undefined) // TODO
	{
		this.debug(context + ':emit:' + arg_event)

		// if ( this._schema.has_rules_agenda() )
		// {
		// 	this._schema.push_on_rule_agenda(arg_event, arg_datas)
		// }
	}



	/**
	 * Call event triggers.
	 * @private
	 * 
	 * @param {string} arg_event - event name.
	 * @param {any} arg_datas - event datas (optional, default:undefined).
	 * 
	 * @returns {nothing}
	 */
	_trigger(arg_event, arg_datas=undefined) // TODO
	{
		this.debug(context + ':trigger:' + arg_event)

		// this._schema._trigger(arg_event, arg_datas)
		this._emit(arg_event, arg_datas)

		// TODO: UPDATE METRICS
	}



	/**
	 * Test if a record is cached.
	 * @private
	 * 
	 * @param {string} arg_id - record id.
	 * 
	 * @returns {Promise} - Promise of boolean value: found (true) or not found (false).
	 */
	_has_cached_record_by_id(arg_id)
	{
		const key = this._cache_prefix + ':' + arg_id
		return this._cache_manager.has(key)
	}



	/**
	 * Test if a record array is cached.
	 * @private
	 * 
	 * @param {DataQuery} arg_query - data query.
	 * 
	 * @returns {Promise} - Promise of boolean value: found (true) or not found (false).
	 */
	_has_cached_record_by_query(arg_query)
	{
		const key = this._cache_prefix + ':query:' + arg_query.hash()
		return this._cache_manager.has(key)
	}



	/**
	 * Get a cached record.
	 * @private
	 * 
	 * @param {string} arg_id - record id.
	 * 
	 * @returns {Promise} - Promise of a DataRecord instance.
	 */
	_get_cached_record_by_id(arg_id)
	{
		const key = this._cache_prefix + ':' + arg_id
		return this._cache_manager.get(key, undefined)
	}



	/**
	 * Get a cached record array.
	 * @private
	 * 
	 * @param {DataQuery} arg_query - data query.
	 * 
	 * @returns {Promise} - Promise of a DataRecordArray instance.
	 */
	_get_cached_record_by_query(arg_query)
	{
		const key = this._cache_prefix + ':query:' + arg_query.hash()
		return this._cache_manager.get(key, undefined)
	}



	/**
	 * Add a record to cache.
	 * @private
	 * 
	 * @param {DataRecord} arg_record - record
	 * 
	 * @returns {Promise} - Promise of boolean value: success (true) or failure (false).
	 */
	_set_cached_record_by_id(arg_record)
	{
		assert( T.isObject(arg_record) && arg_record.is_data_record, context + ':_set_cached_record_by_id:bad data record object')
		
		const key = this._cache_prefix + ':' + arg_record.get_id()
		return this._cache_manager.set(key, arg_record, this._schema.get_ttl())
	}



	/**
	 * Add a record array to cache.
	 * @private
	 * 
	 * @param {DataQuery} arg_query - data query.
	 * @param {DataRecordArray} arg_record_array - record array.
	 * 
	 * @returns {Promise} - Promise of boolean value: success (true) or failure (false).
	 */
	_set_cached_record_by_query(arg_query, arg_record_array)
	{
		assert( T.isObject(arg_query) && arg_query.is_data_query, context + ':_set_cached_record_by_query:bad data query object')
		assert( T.isObject(arg_record_array) && arg_record_array.is_data_record_array, context + ':_set_cached_record_by_query:bad data record object')
		
		const promises = []

		// CACHE RECORD ARRAY
		const key = this._cache_prefix + ':query:' + arg_query.hash()
		promises.push( this._cache_manager.set(key, arg_record_array, this._schema.get_ttl()) )

		// CACHE ALL ARRAY RECORDS
		arg_record_array._records.forEach(
			(record)=>{
				promises.push( this._set_cached_record_by_id(record) )
			}
		)

		return Promise.all(promises)
	}



	/**
	 * Remove a cached record.
	 * @private
	 * 
	 * @param {string} arg_id - record id.
	 * 
	 * @returns {Promise} - Promise of boolean value: success (true) or failure (false).
	 */
	_remove_cached_record_by_id(arg_id)
	{
		const key = this._cache_prefix + ':' + arg_id
		
		return this._cache_manager.remove(key)
	}



	/**
	 * Get collection name.
	 * 
	 * @returns {string}
	 */
	get_name()
	{
		return this._name
	}



	/**
	 * Get cache manager.
	 * 
	 * @returns {DataAdapter}
	 */
	get_cache_manager()
	{
		assert( T.isObject(this._cache_manager) && this._cache_manager.is_cache_manager, context + ':get_cache_manager:bad cache manager object')
		
		return this._cache_manager
	}



	/**
	 * Get data adapter.
	 * 
	 * @returns {DataAdapter}
	 */
	get_adapter()
	{
		assert( T.isObject(this._adapter) && this._adapter.is_data_adapter, context + ':get_adapter:bad data adapter object')
		
		return this._adapter
	}



	/**
	 * Get data model.
	 * 
	 * @returns {DataModel}
	 */
	get_model()
	{
		assert( T.isObject(this._schema) && this._schema.is_topology_model, context + ':get_model:bad data model object')
		
		return this._schema
	}



	/**
	 * Get data model.
	 * 
	 * @returns {DataModel}
	 */
	get_schema()
	{
		assert( T.isObject(this._schema) && this._schema.is_topology_model, context + ':get_model:bad data model object')
		
		return this._schema
	}



	/**
	 * Validate data record values.
	 * 1-Call 'before_validate' triggers.
	 * 2-Check all fields values.
	 * 3-Call 'after_validate_ok' triggers on success.
	 *   Call 'after_validate_ko' triggers on failure.
	 * 4-Return success (true) or failure (false)
	 * 
	 * @param {DataRecord} arg_record - data record instance.
	 * 
	 * @returns {Promise} - Promise of a boolean.
	 */
	validate_record(arg_record) // TODO
	{
		assert( T.isObject(arg_record) && arg_record.is_data_record, context + ':validate_record:bad data record object')
		
		this._trigger('before_validate', { record:arg_record })
		const result = this._schema.validate(arg_record.get_attributes_object())
		this._trigger(result.is_valid ? 'after_validate_ok' : 'after_validate_ko', { record:arg_record })

		return Promise.resolve(result)
	}



	/**
	 * Create a new data record instance, not saved.
	 * 
	 * @param {object} arg_record_datas - new record attributes.
	 * @param {string} arg_record_id - new record unique id (optional).
	 * 
	 * @returns {Promise} - Promise(DataRecord)
	 */
	new_record(arg_record_datas, arg_record_id)
	{
		// const is_cached_promise = this._has_cached_record_by_id(arg_record_id)
		// if (is_cached)
		// {
		// 	console.log('collection:is cached')
		// 	return this._get_cached_record_by_id(arg_record_id)
		// }
		return this._adapter.new_record(this._schema.get_name(), arg_record_datas, arg_record_id)
	}



	/**
	 * Create a data collection record.
	 * 1-Call 'before_create' triggers.
	 * 2-Check existing record id within cached records.
	 * 3-Validate record datas: reject on failure
	 * 4-Create a DataRecord instance with adapter.new_record.
	 * 5-Add record to cache.
	 * 6-Call record.save()
	 * 7-Call 'after_create' triggers.
	 * 
	 * @param {DataRecord} arg_record - data record instance.
	 * 
	 * @returns {Promise} - Promise of a DataRecord object.
	 */
	create_record(arg_record)
	{
		assert( T.isObject(arg_record) && arg_record.is_data_record, context + ':create_record:bad data record object')
		
		this._trigger('before_create', { has_error:false, error_msg:undefined, record:arg_record})
		
		return this._has_cached_record_by_id(arg_record.get_id())
		.then(
			(is_cached)=>{
				if (is_cached)
				{
					this._trigger('after_create', { has_error:true, error_msg:'already_exists', record:arg_record })
					return Promise.reject('already_exists')
				}
			}
		)
		.then(
			()=>{
				const is_valid = this.validate_record(arg_record)
				if ( ! is_valid)
				{
					this._trigger('after_create', { has_error:true, error_msg:'not_valid', record:arg_record })
					return Promise.reject('not_valid')
				}
			}
		)
		.then(
			()=>{
				return this._adapter.create_record(this._schema.get_name(), arg_record.get_attributes_object())
			}
		)
		.then(
			(record)=>{
				this._trigger('after_create', { has_error:false, error_msg:undefined, record:record})
				return record
			}
		)
		.catch(
			(e)=>{
				this._trigger('after_create', { has_error:true, error_msg:e, record:arg_record})
				console.error(context + ':create_record:', e)
				return undefined
			}
		)
	}



	/**
	 * Delete a data collection record.
	 * 1-Call 'before_delete' triggers.
	 * 2-Remove cached record.
	 * 3-Call record.delete()
	 * 4-Call 'after_delete' triggers.
	 * 
	 * @param {DataRecord} arg_record - data record instance.
	 * 
	 * @returns {Promise} - Promise of boolean success.
	 */
	delete_record(arg_record)
	{
		assert( T.isObject(arg_record) && arg_record.is_data_record, context + ':delete_record:bad data record object')

		this._trigger('before_delete', { has_error:false, error_msg:undefined, record:arg_record})
		
		return this._remove_cached_record_by_id(arg_record.get_id())
		.then(
			()=>{
				return this._adapter.delete_record(this._schema.get_name(), arg_record.get_id())
				.then( (record)=> { return record.set_removed() } )
			}
		).then(
			()=>{
				this._trigger('after_delete', { has_error:false, error_msg:undefined, record:arg_record})
				return true
			}
		).catch(
			(e)=>{
				this._trigger('after_delete', { has_error:true, error_msg:e, record:arg_record})
				return false
			}
		)
	}



	/**
	 * Update a data collection record.
	 * 1-Call 'before_update' triggers.
	 * 2-Update cached record.
	 * 3-Call record.update()
	 * 4-Call 'after_update' triggers.
	 * 
	 * @param {DataRecord} arg_record - data record instance.
	 * 
	 * @returns {Promise} - Promise of boolean success.
	 */
	update_record(arg_record)
	{
		assert( T.isObject(arg_record) && arg_record.is_data_record, context + ':update_record:bad data record object')
		
		this._trigger('before_update', { has_error:false, error_msg:undefined, record:arg_record})
		
		return this._adapter.update_record(this._schema.get_name(), arg_record)
		.then(
			(record)=>{
				console.log(context + ':update_record:record', record)
				return this._set_cached_record_by_id(arg_record.get_id(), record)
			}
		)
		.then(
			()=>{
				this._trigger('after_update', { has_error:false, error_msg:undefined, record:arg_record})
				console.log(context + ':update_record:true')
				return true
			}
		)
		.catch(
			(e)=>{
				this._trigger('after_update', { has_error:true, error_msg:e, record:arg_record})
				return false
			}
		)
	}



	/**
	 * Update a data collection record.
	 * 1-Search record into cache
	 * 2-Search record into adapter
	 * 
	 * @param {string} arg_record_id - data record id.
	 * 
	 * @returns {Promise} - Promise of boolean found:true, not found:false.
	 */
	has_record(arg_record_id)
	{
		assert( T.isString(arg_record_id) && arg_record_id.length > 0, context + ':has_record:bad record id string')
		
		return this._has_cached_record_by_id(arg_record_id)
		.then(
			(found)=>{
				if (found)
				{
					return true
				}
				return this._adapter.has_record(this._schema.get_name(), arg_record_id)
			}
		).catch(
			(e)=>{
				this._trigger('has_record', { has_error:true, error_msg:e, id:arg_record_id} )
				return false
			}
		)
	}



	/**
	 * Find an existing data record with an id.
	 * 1-Search record into cache
	 * 2-Search record into adapter
	 * 3-Save record into cache
	 * 
	 * @param {string} arg_record_id - data record id.
	 * 
	 * @returns {Promise} - Promise of DataRecord.
	 */
	find_one_record(arg_record_id)
	{
		assert( T.isString(arg_record_id) && arg_record_id.length > 0, context + ':find_one_record:bad record id string')
		
		return this._get_cached_record_by_id(arg_record_id)
		.then(
			(record)=>{
				if ( T.isObject(record) && record.is_data_record )
				{
					this._trigger('find_one_record', { has_error:false, error_msg:undefined, id:arg_record_id, from:'cache'})
					return record
				}
				return this._adapter.find_one_record(this._schema.get_name(), arg_record_id)
				.then(
					(attributes)=>{
						// console.log(context + ':find_one_record:adapter:attributes', attributes)
						return this.new_record(attributes, arg_record_id)
					}
				)
				.then(
					(record)=>{
						if ( T.isObject(record) && record.is_data_record )
						{
							this._trigger('find_one_record', { has_error:false, error_msg:undefined, id:arg_record_id, record:record, from:'adapter'})
							// console.log(context + ':find_one_record:adapter:good record', record)
							return record
						}

						console.error(context + ':find_one_record:adapter:bad record', record)
						this._trigger('find_one_record', { has_error:false, error_msg:undefined, id:arg_record_id, from:'notfound'})
						return undefined
					}
				)
			}
		)
		.catch(
			(e)=>{
				this._trigger('find_one_record', { has_error:true, error_msg:e, id:arg_record_id} )
				console.error(context + ':find_one_record:error', e)
				return undefined
			}
		)
	}



	/**
	 * Find existing data records with a query.
	 * 1-Search records into cache
	 * 2-Search records into adapter
	 * 3-Save records into cache
	 * 
	 * @param {DataQuery} arg_query - data query.
	 * 
	 * @returns {Promise} - Promise of DataRecordArray.
	 */
	find_records(arg_query)
	{
		assert( T.isObject(arg_query) && arg_query.is_data_query, context + ':find_records:bad query object')
		
		return this._get_cached_record_by_query(arg_query)
		.then(
			(record_array)=>{
				if ( T.isObject(record_array) && record_array.is_data_record_array )
				{
					this._trigger('find_records', { has_error:false, error_msg:undefined, query:arg_query, from:'cache'})
					return record_array
				}
				return this._adapter.find_records(this._schema.get_name(), arg_query)
				.then(
					(attributes_array)=>{
						if ( T.isArray(attributes_array) )
						{
							return this.new_record_array(attributes_array)
						}

						this._trigger('find_records', { has_error:true, error_msg:'bad attributes array', query:arg_query, from:'notfound'})
						return undefined
					}
				)
				.then(
					(record_array)=>{
						if ( T.isObject(record_array) && record_array.is_data_record_array )
						{
							return this._set_cached_record_by_query(arg_query, record_array).then(
								()=>{
									this._trigger('find_records', { has_error:false, error_msg:undefined, query:arg_query, records:record_array, from:'adapter'})
									return record_array
								}
							)
						}

						this._trigger('find_records', { has_error:false, error_msg:'bad DataRecordArray', query:arg_query, from:'notfound'})
						return undefined
					}
				)
			}
		).catch(
			(e)=>{
				this._trigger('find_one_record', { has_error:true, error_msg:e, query:arg_query} )
				return undefined
			}
		)
	}



	/**
	 * Find all existing data records from adapter.
	 * 
	 * @returns {Promise} - Promise of DataRecordArray.
	 */
	find_all_records() // TODO use a query and cache: calling find_records(query) with query.select_all()
	{
		return this._adapter.find_all_records(this._schema.get_name())
		.then(
			(record_array)=>{
				if ( T.isObject(record_array) && record_array.is_data_record_array )
				{
					this._trigger('find_all_records', { has_error:false, error_msg:undefined, from:'adapter'})
					return record_array
				}

				this._trigger('find_all_records', { has_error:true, error_msg:'bad DataRecordArray'} )
				return undefined
			}
		)
		.catch(
			(e)=>{
				this._trigger('find_all_records', { has_error:true, error_msg:e } )
				return undefined
			}
		)
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T from '../utils/types'
import DataRecord from './data_record'


/**
 * Contextual constant for this file logs.
 * @private
 */
let context = 'common/data/data_model'



/**
 * DataModel class.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* 	API:
* 		->constructor(arg_attributes)
* 
* 		->get_collection():DataCollection
* 		
* 		->set(arg_attribute_name, arg_attribute_value):boolean - set an attribute value.
* 		->get(arg_attribute_name):any - get an attribute value.
* 		->get_id():string - get id attribute value.
* 
* 		->save():Promise - save all changed attributes to the collection store.
* 		->rollback():boolean - disguard any unsaved changed attributes.
* 		->remove():Promise - remove record from collection store.
* 		->reload():Promise - restore attributes from collection store.
* 
* 
* 	USAGE ON BROWSER:
* 		class Cars extends DataModel {
* 			constructor(arg_attributes) {
* 				const ds = ... // get data store
* 				super(ds.get_collection('cars'), undefined, arg_attributes)
* 			}
* 		}
* 		var cars = new Cars( { engine:"fuel", color:"blue"} )
* 
* 
 */
export default class DataModel extends DataRecord
{
	/**
	 * DataModel class inherits from DataRecord and aims to provide a high level API for sub-classes.
	 * 
	 * @param {DataCollection} arg_data_collection - data collection: contains schema (mandatory).
	 * @param {string|undefined} arg_id - data id, unique in collection (optional).
	 * @param {object|Immutable.Map|undefined} arg_attributes - initial datas values (optional).
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_collection, arg_id, arg_attributes)
	{
		assert( T.isUndefined(arg_attributes) || T.isObject(arg_attributes), context + ':constructor:bad attributes object or undefined')

		if( T.isObject(arg_id) && T.isUndefined(arg_attributes) )
		{
			arg_attributes = arg_id
			arg_id = undefined
		}

		if ( T.isUndefined(arg_id) && T.isObject(arg_attributes) )
		{
			const id_field_name = arg_collection.get_id_field_name()
			if (id_field_name in arg_attributes)
			{
				arg_id = arg_attributes[id_field_name]
			}
		}

		super(arg_collection, arg_id, arg_attributes)

		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_data_model = true
	}
}

// NPM IMPORTS
import assert from 'assert'
import {Map} from 'immutable'

// COMMON IMPORTS
import T from '../utils/types'


/**
 * Contextual constant for this file logs.
 * @private
 */
let context = 'common/data/data_record'



/**
 * DataRecord class.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* 
* 	API:
* 		->constructor(arg_collection, arg_id, arg_attributes)
* 
* 		->get_collection():DataCollection - get record collection.
* 
* 		->get_type():string - get record type.
* 		->get_id():string - get record id.
* 		->get_id_field_name():string - get record id.
* 		->get_attributes():Immutable.Map - get record attributes.
* 
* 		->set(arg_attribute_name, arg_attribute_value):boolean - set an attribute value.
* 		->get(arg_attribute_name):any - get an attribute value.
* 
* 		->save():Promise - save all changed attributes to the collection store.
* 		->rollback():boolean - disguard any unsaved changed attributes.
* 		->remove():Promise - remove record from collection store.
* 		->reload():Promise - restore attributes from collection store.
* 
* 		->serialize():string - transform attributes map to a string.
* 		->deserialize(string):boolean - load attributes from a string.
* 
* 		->fill(arg_datas):boolean - fill attributes with given datas.
* 		->clear():boolean - fill attributes with default datas.
* 
* 
 */
export default class DataRecord
{
	/**
	 * Data record class, contains one collection item attributes.
	 * 
	 * @param {DataCollection} arg_data_collection - data collection: contains schema.
	 * @param {string} arg_id - data id, unique in collection.
	 * @param {object|Immutable.Map} arg_attributes - initial datas values.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_data_collection, arg_id, arg_attributes)
	{
		assert( T.isObject(arg_data_collection) && arg_data_collection.is_data_collection, context + ':constructor:bad model object')
		assert( T.isString(arg_id) && arg_id.length > 0, context + ':constructor:bad id string')
		assert( T.isObject(arg_attributes), context + ':constructor:bad attributes object')
		
		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_data_record = true

		/**
		 * Datas collection instance.
		 * @type {DataCollection}
		 */
		this._collection = arg_data_collection

		/**
		 * Record is a new created record flag.
		 * @type {boolean}
		 */
		this._is_new = false

		/**
		 * Record unique identifier.
		 * @type {string}
		 */
		this._id = arg_id
		
		/**
		 * Record identifier field name.
		 * @type {string}
		 */
		this._id_field_name = this._collection.get_schema().get_id_field_name()

		/**
		 * Record attributes map.
		 * @type {object}
		 */
		this._attributes = new Map()

		this.set_attributes(arg_attributes)
		if (this._id)
		{
			this.set(this._id_field_name, this._id)
		}


		/**
		 * Record has dirty attributes values flag.
		 * @type {boolean}
		 */
		this._has_dirty_attributes = false

		/**
		 * Record dirty attributes names.
		 * @type {array}
		 */
		this._dirty_attributes = []
		
		/**
		 * Record dirty attributes map.
		 * @type {Map}
		 */
		this._previous_attributes = this._attributes
	}



	/**
	 * Emit on event.
	 * @private
	 * 
	 * @param {string} arg_event - event name.
	 * @param {any} arg_datas - event datas (optional, default:undefined).
	 * 
	 * @returns {nothing}
	 */
	/* eslint no-unused-vars: "off" */
	_emit(arg_event, arg_datas=undefined) // TODO
	{
	}



	/**
	 * Check attribute name and value against model schema.
	 * 
	 * @param {string} arg_attribute_name - attribute name.
	 * @param {any} arg_attribute_value - attribute value.
	 * 
	 * @returns {boolean}
	 */
	_check_attribute(arg_attribute_name, arg_attribute_value) // TODO
	{
		return true
	}



	/**
	 * Get record model.
	 * 
	 * @returns {DataModel}
	 */
	get_model()
	{
		return this._model
	}



	/**
	 * Get record collection.
	 * 
	 * @returns {DataCollection}
	 */
	get_collection()
	{
		return this._collection
	}



	/**
	 * Get record type.
	 * 
	 * @returns {string}
	 */
	get_type()
	{
		return this._model.get_name()
	}



	/**
	 * Get record id.
	 * 
	 * @returns {string}
	 */
	get_id()
	{
		return this._id
	}



	/**
	 * Get record id field name.
	 * 
	 * @returns {string}
	 */
	get_id_field_name()
	{
		return this.get_collection().get_schema().get_id_field_name()
	}



	/**
	 * Get record fields names.
	 * 
	 * @returns {array} - array of all schema fields names strings.
	 */
	get_fields_names()
	{
		return this.get_collection().get_schema().get_fields_names()
	}



	/**
	 * Get record attributes.
	 * 
	 * @returns {Immutable.Map}
	 */
	get_attributes()
	{
		return this._attributes
	}


	/**
	 * Get record attributes.
	 * 
	 * @returns {object}
	 */
	get_attributes_object()
	{
		return this._attributes.toJS()
	}



	/**
	 * Set record attributes and normalize with schema.
	 * 
	 * @param {object|Immutable.Map} arg_attributes - attributes map.
	 * 
	 * @returns {nothing}
	 */
	set_attributes(arg_attributes)
	{
		this._previous_attributes = this._attributes
		this._attributes = (arg_attributes.has && arg_attributes.get && arg_attributes.getIn) ? arg_attributes : new Map(arg_attributes)
		// console.log('record._attributes 1', this._attributes)

		const fields_names = this.get_fields_names()
		const defaults_values = this.get_collection().get_schema().get_defaults()

		fields_names.forEach(
			(field_name)=>{
				if(this._attributes.has(field_name) || this._id_field_name == field_name)
				{
					return
				}
				
				this._attributes.set(field_name, defaults_values[field_name])
			}
		)
		// console.log('record._attributes 2', this._attributes)

		this._has_dirty_attributes = true
		this._dirty_attributes = Object.keys( this._attributes.toJS() )
	}
	



	/**
	 * Set an attribute value.
	 * 
	 * @param {string|array} arg_attribute_path - attribute name or path.
	 * @param {any} arg_attribute_value - attribute value.
	 * 
	 * @returns {boolean}
	 */
	set(arg_attribute_path, arg_attribute_value)
	{
		const check = this._check_attribute(arg_attribute_path, arg_attribute_value)
		if (!check)
		{
			return false
		}

		this._has_dirty_attributes = true
		if ( this._dirty_attributes.indexOf(arg_attribute_path) == -1 )
		{
			this._dirty_attributes.push(arg_attribute_path)
		}
		this._attributes = this._attributes.set(arg_attribute_path, arg_attribute_value)
		this._emit('changed', {path:arg_attribute_path, value:arg_attribute_value})

		return true
	}



	/**
	 * Get an attribute value.
	 * 
	 * @param {string|array} arg_attribute_path - attribute name or path.
	 * 
	 * @returns {any|undefined}
	 */
	get(arg_attribute_path)
	{
		return this._attributes.get(arg_attribute_path, undefined)
	}



	/**
	 * Save all attributes values to the collection store.
	 * 
	 * @returns {Promise} - promise of success (boolean)
	 */
	save()
	{
		let promise = undefined
		if (this._is_new)
		{
			promise = this.get_collection().create_record(this)
		} else {
			promise = this.get_collection().update_record(this)
		}
		
		return promise.then(
			(success)=>{
				if (success)
				{
					this._is_new = false
					this._emit('saved')
					this._has_dirty_attributes = false
					this._dirty_attributes = []
					this._previous_attributes = this._attributes
				}
				console.log(context + ':save:success', success)
				return success
			}
		)
	}



	/**
	 * Disguard all dirty attributes values.
	 * 
	 * @returns {Promise} - promise of success (boolean)
	 */
	rollback()
	{
		this._emit('rollbacked')
		this._has_dirty_attributes = false
		this._dirty_attributes = []
		this._attributes = this._previous_attributes
		return Promise.resolve(true)
	}



	/**
	 * Delete record from collection store.
	 * 
	 * @returns {Promise} - promise of success (boolean)
	 */
	remove()
	{
		return this.get_collection().delete_record(this).then(
			(success)=>{
				if (success)
				{
					this._emit('removed')
					this._has_dirty_attributes = false
					this._dirty_attributes = []
					this._previous_attributes = this._attributes = new Map()
				}
			}
		)
	}



	/**
	 * Set record as removed.
	 * 
	 * @return {nothing}
	 */
	set_removed()
	{
		// TODO
	}



	/**
	 * Reload record from collection store.
	 * 
	 * @returns {Promise} - promise of success (boolean)
	 */
	reload()
	{
		return this.get_collection().reload_record(this).then(
			(success)=>{
				if (success)
				{
					this._emit('reloaded')
					this._has_dirty_attributes = false
					this._dirty_attributes = []
					this._previous_attributes = this._attributes
				}
			}
		)
	}



	/**
	 * Transform attributes to a string.
	 * 
	 * @returns {string} - serialized attributes.
	 */
	serialize() // TODO
	{
		return ''
	}



	/**
	 * Transform attributes string to an object.
	 * 
	 * @param {stirng} arg_serialized - serialized attributes.
	 *  
	 * @returns {boolean} - success.
	 */
	/* eslint no-unused-vars: "off" */
	deserialize(arg_serialized) // TODO
	{
		return false
	}



	/**
	 * Fill all attributes values with given datas.
	 * 
	 * @param {arg_datas} - new attribute values.
	 * 
	 * @returns {boolean} - success.
	 */
	fill(arg_datas)
	{
		if ( ! T.isObject(arg_datas) )
		{
			return false
		}

		const backup = this._attributes
		let result = true
		Object.keys(arg_datas).forEach(
			(attribute_name)=>{
				const value = arg_datas[attribute_name]
				if ( ! this.set(attribute_name, value) )
				{
					result = false
					return
				}
				if ( this._dirty_attributes.indexOf(attribute_name) == -1 )
				{
					this._dirty_attributes.push(attribute_name)
				}
			}
		)

		if (! result)
		{
			this._attributes = backup
			return false
		}

		this._emit('filled')
		this._has_dirty_attributes = true
		return true
	}



	/**
	 * Clear all attributes values with default values.
	 * 
	 * @returns {boolean} - success.
	 */
	clear()
	{
		const backup = this._attributes
		let result = true
		this._dirty_attributes = []
		this._model.get_fields().forEach(
			(field)=>{
				result = result && this.set(field.get_name(), field.get_default())
				this._dirty_attributes.push(field.get_name())
			}
		)

		if (!result)
		{
			this._attributes = backup
			return false
		}

		this._has_dirty_attributes = true
		this._emit('cleared')
		return true
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T from '../utils/types'
import Loggable from '../base/loggable'


/**
 * Contextual constant for this file logs.
 * @private
 */
const context = 'common/data/data_store'

/*
createRecord is used for creating new records on the client side. This will return a new record in the created.uncommitted state.
In order to persist this record to the backend you will need to call record.save().

push is used to notify Ember Data's store of new or updated records that exist in the backend. This will return a record in the loaded.saved state. The primary use-case for store#push is to notify Ember Data about record updates (full or partial) that happen outside of the normal adapter methods (for example SSE or Web Sockets).

pushPayload is a convenience wrapper for store#push that will deserialize payloads if the Serializer implements a pushPayload method.



METHODS

adapterFor
modelFor
normalize
peekAll
peekRecord
push
pushPayload
query
recordIsLoaded
serializerFor
unloadAll
unloadRecord
*/

/**
 * DataStore class.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* 	API:
* 		->constructor()
* 		
* 		// DATA ADAPTERS MANAGEMENT
* 		->add_adapter(arg_adapter):boolean
* 		->remove_adapter(arg_adapter):boolean
* 		->suspend_adapter(arg_adapter):boolean
* 		->resume_adapter(arg_adapter):boolean
* 		
* 		// DATA COLLECTIONS MANAGEMENT
* 		->get_collection(arg_model_name):DataCollection - get a data collection for a model.
* 		->add_collection(arg_collection):boolean - add a data collection.
* 		->remove_collection(arg_collection):boolean - remove a data collection.
* 
* 		// WRAPPERS FOR DATA COLLECTIONS OPERATIONS
* 		->create_record(arg_collection_name, arg_record_id, arg_record_datas):Promise(DataRecord) - create a new data record.
* 		->delete_record(arg_collection_name, arg_record_id):Promise(boolean) - delete an existing data record.
* 		->update_record(arg_collection_name, arg_record_id, arg_record_datas):Promise(DataRecord) - update an existing data record.
* 		->has_record(arg_collection_name, arg_record_id):Promise(boolean) - test if a data record exists with an id.
* 		
* 		->find_one_record(arg_collection_name, arg_record_id):Promise(DataRecord) - find an existing data record with an id.
* 		->find_records(arg_collection_name, arg_query):Promise(DataRecordArray) - find existing data records with a query.
* 		->find_all_records(arg_collection_name):Promise(DataRecordArray) - find all xisting data records.
* 
* 
* 	USAGE ON BROWSER:
* 		var memory_cache = new CacheAdapterNodeCache( {ttl:5000, check_period:2000} )
* 		var cache_manager = new CacheManager([memory_cache])
* 
* 		var rest_settings = {...}
* 		var models_settings = {...}
* 		var rest_adapter = new DataAdapterRest(rest_settings, models_settings)
* 		var ds = new DataStore(cache_manager, [rest_adapter])
* 		ds.set_tenant('my tenant')
* 		ds.set_credentials('*', app.get_credentials())
* 		ds.set_credentials('cars', {token:'...'} )
* 		
* 		var cars = ds.get_collection('cars')
* 		var car_12 = cars.find_one_record('12')
* 
* 		or
* 
* 		var car_13 = ds.find_one_record('cars', '13')
* 		
* 		var car_14 = ds.create_record('cars', '14', { engine:'gazol', color:'red' } )
* Each application on the browser has credentials for one tenant.
 * 
 */
export default class DataStore extends Loggable
{
	/**
	 * Data store class is the main interface between datas consumers (views) and producers (adapters).
	 * 
	 * @param {CacheManager} arg_cache_manager - cache manager instance.
	 * @param {array} arg_data_adapters - data adapters array.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_cache_manager, arg_data_adapters=[])
	{
		assert( T.isObject(arg_cache_manager) && arg_cache_manager.is_cache_manager, context + ':constructor:bad cache manager object')
		assert( T.isArray(arg_data_adapters), context + ':constructor:bad data adapters array')

		super(context)

		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_data_store = true
		
		/**
		 * CacheManager instance.
		 * @type {CacheManager}
		 */
		this._cache_manager = arg_cache_manager
		
		/**
		 * Datas adapters array.
		 * @type {array}
		 */
		this._adapters = arg_data_adapters
		
		/**
		 * Adapter collections map.
		 * @type {object}
		 */
		this._collections = {}
	}



	/**
	 * Emit on event.
	 * @private
	 * 
	 * @param {string} arg_event - event name.
	 * @param {any} arg_datas - event datas (optional, default:undefined).
	 * 
	 * @returns {nothing}
	 */
	/* eslint no-unused-vars: "off" */
	_emit(arg_event, arg_datas=undefined) // TODO
	{
		this.debug(context + ':emit:' + arg_event)
	}



	/**
	 * Add a data adapter.
	 * 
	 * @param {DataAdapter} arg_adapter - data adapter.
	 * 
	 * @returns {boolean}
	 */
	add_adapter(arg_adapter)
	{
		assert( T.isObject(arg_adapter) && arg_adapter.is_data_adapter, context + ':add_adapter:bad data adapter object')
		
		const adapter_name = arg_adapter.get_name()
		if (adapter_name in this._adapters)
		{
			this.warn('add_adapter:adapter exist [' + adapter_name + ']')
			return false
		}

		this._adapters[adapter_name] = arg_adapter

		this._emit('adapter.added', adapter_name)
		return true
	}



	/**
	 * Remove a data adapter.
	 * 
	 * @param {DataAdapter} arg_adapter - data adapter.
	 * 
	 * @returns {boolean}
	 */
	remove_adapter(arg_adapter)
	{
		assert( T.isObject(arg_adapter) && arg_adapter.is_data_adapter, context + ':remove_adapter:bad data adapter object')
		
		const adapter_name = arg_adapter.get_name()
		if ( ! (adapter_name in this._adapters) )
		{
			this.warn('remove_adapter:adapter does\'t exist [' + adapter_name + ']')
			return false
		}

		arg_adapter.destroy()
		delete this._adapters[adapter_name]
		this._emit('adapter.removed', adapter_name)
		return true
	}



	/**
	 * Suspend a data adapter.
	 * 
	 * @param {DataAdapter} arg_adapter - data adapter.
	 * 
	 * @returns {boolean}
	 */
	suspend_adapter(arg_adapter)
	{
		assert( T.isObject(arg_adapter) && arg_adapter.is_data_adapter, context + ':suspend_adapter:bad data adapter object')
		
		arg_adapter.suspend()
	}



	/**
	 * Resume a data adapter.
	 * 
	 * @param {DataAdapter} arg_adapter - data adapter.
	 * 
	 * @returns {boolean}
	 */
	resume_adapter(arg_adapter)
	{
		assert( T.isObject(arg_adapter) && arg_adapter.is_data_adapter, context + ':resume_adapter:bad data adapter object')
		
		arg_adapter.resume()
	}



	/**
	 * Get a data collection for a model.
	 * 
	 * @param {string} arg_model_name - data collection model name.
	 * 
	 * @returns {DataCollection|undefined}
	 */
	get_collection(arg_model_name)
	{
		assert( T.isOString(arg_model_name), context + ':get_collection:bad data collection model name string')
		
		if (arg_model_name in this._collections)
		{
			
			return this._collections[arg_model_name]
		}

		this.warn('get_collection:collection doesn\'t exist')
		return undefined
	}



	/**
	 * Add a data collection.
	 * 
	 * @param {DataCollection} arg_collection - data collection.
	 * 
	 * @returns {boolean}
	 */
	add_collection(arg_collection)
	{
		assert( T.isObject(arg_collection) && arg_collection.is_data_collection, context + ':add_collection:bad data collection object')
		
		const collection_name = arg_collection.get_name()
		if (collection_name in this._collections)
		{
			this.warn('add_collection:collection exist')
			return false
		}

		this._collections[collection_name] = arg_collection
		this._emit('collection.added', collection_name)
		return true
	}



	/**
	 * Remove a data collection.
	 * 
	 * @param {DataCollection} arg_collection - data collection.
	 * 
	 * @returns {boolean}
	 */
	remove_collection(arg_collection)
	{
		assert( T.isObject(arg_collection) && arg_collection.is_data_collection, context + ':remove_collection:bad data collection object')
		
		const collection_name = arg_collection.get_name()
		if ( !(collection_name in this._collections) )
		{
			this.warn('remove_collection:collection doesn\'t exist [' + collection_name + ']')
			return false
		}

		arg_collection.destroy()

		delete this._collections[collection_name]
		this._emit('collection.removed', collection_name)
		return true
	}



	/**
	 * Create a data collection record.
	 * 
	 * @param {string} arg_collection_name - data collection name.
	 * @param {string} arg_record_id - data record id.
	 * @param {object} arg_record_datas - data record datas.
	 * 
	 * @returns {Promise} - Promise of a DataRecord object.
	 */
	create_record(arg_collection_name, arg_record_id, arg_record_datas)
	{
		assert( T.isString(arg_collection_name), context + ':create_record:bad data collection string')
		assert( T.isString(arg_record_id) && arg_record_id.length > 0, context + ':create_record:bad record id string')
		assert( T.isObject(arg_record_datas), context + ':create_record:bad record datas object')
		
		// CHECK COLLECTION NAME
		if (! (arg_collection_name in this._collections) )
		{
			this.warn('create_record:collection doesn\'t exist [' + arg_collection_name + ']')
			return Promise.reject('create_record:bad collection name [' + arg_collection_name + ']')
		}

		// GET COLLECTION
		const collection = this._collections[arg_collection_name]
		assert( T.isObject(collection) && collection.is_data_collection, context + ':create_record:bad data collection object')
		
		// CHECK EXISTING RECORD
		const check_record_promise = collection.has_record_by_id(arg_record_id)
		return check_record_promise.then(
			(found)=>{
				if (! found)
				{
					this.warn('create_record:collection record id already exists collection=[' + arg_collection_name + '] id=[' + arg_record_id + ']')
					return Promise.reject('create_record:bad collection [' + arg_collection_name + '] id [' + arg_record_id + ']')
				}
				return collection.create_record(arg_record_id, arg_record_datas)
			}
		)
	}



	/**
	 * Delete a data collection record.
	 * 
	 * @param {string} arg_collection_name - data collection name.
	 * @param {string} arg_record_id - data record id.
	 * 
	 * @returns {Promise} - Promise of boolean success.
	 */
	delete_record(arg_collection_name, arg_record_id)
	{
		assert( T.isString(arg_collection_name), context + ':delete_record:bad data collection string')
		assert( T.isString(arg_record_id) && arg_record_id.length > 0, context + ':delete_record:bad record id string')
		
		// CHECK COLLECTION NAME
		if (! (arg_collection_name in this._collections) )
		{
			this.warn('delete_record:collection doesn\'t exist [' + arg_collection_name + ']')
			return Promise.reject('delete_record:bad collection name [' + arg_collection_name + ']')
		}

		// GET COLLECTION
		const collection = this._collections[arg_collection_name]
		assert( T.isObject(collection) && collection.is_data_collection, context + ':delete_record:bad data collection object')
		
		return collection.delete_record(arg_record_id)
	}



	/**
	 * Update a data collection record.
	 * 
	 * @param {string} arg_collection_name - data collection name.
	 * @param {string} arg_record_id - data record id.
	 * @param {object} arg_record_datas - data record datas.
	 * 
	 * @returns {Promise} - Promise of boolean success.
	 */
	update_record(arg_collection_name, arg_record_id, arg_record_datas)
	{
		assert( T.isString(arg_collection_name), context + ':update_record:bad data collection string')
		assert( T.isString(arg_record_id) && arg_record_id.length > 0, context + ':update_record:bad record id string')
		assert( T.isObject(arg_record_datas), context + ':update_record:bad record datas object')
		
		// CHECK COLLECTION NAME
		if (! (arg_collection_name in this._collections) )
		{
			this.warn('update_record:collection doesn\'t exist [' + arg_collection_name + ']')
			return Promise.reject('update_record:bad collection name [' + arg_collection_name + ']')
		}

		// GET COLLECTION
		const collection = this._collections[arg_collection_name]
		assert( T.isObject(collection) && collection.is_data_collection, context + ':update_record:bad data collection object')
		
		return collection.update_record(arg_record_id, arg_record_datas)
	}



	/**
	 * Update a data collection record.
	 * 
	 * @param {string} arg_collection_name - data collection name.
	 * @param {string} arg_record_id - data record id.
	 * 
	 * @returns {Promise} - Promise of boolean found:true, not found:false.
	 */
	has_record(arg_collection_name, arg_record_id)
	{
		assert( T.isString(arg_collection_name), context + ':has_record:bad data collection string')
		assert( T.isString(arg_record_id) && arg_record_id.length > 0, context + ':has_record:bad record id string')
		
		// CHECK COLLECTION NAME
		if (! (arg_collection_name in this._collections) )
		{
			this.warn('update_record:collection doesn\'t exist [' + arg_collection_name + ']')
			return Promise.reject('update_record:bad collection name [' + arg_collection_name + ']')
		}

		// GET COLLECTION
		const collection = this._collections[arg_collection_name]
		assert( T.isObject(collection) && collection.is_data_collection, context + ':update_record:bad data collection object')
		
		// CHECK EXISTING RECORD
		return collection.has_record_by_id(arg_record_id)
	}



	/**
	 * Find an existing data record with an id.
	 * 
	 * @param {string} arg_collection_name - data collection name.
	 * @param {string} arg_record_id - data record id.
	 * 
	 * @returns {Promise} - Promise of DataRecord.
	 */
	find_one_record(arg_collection_name, arg_record_id)
	{
		assert( T.isString(arg_collection_name), context + ':find_one_record:bad data collection string')
		assert( T.isString(arg_record_id) && arg_record_id.length > 0, context + ':find_one_record:bad record id string')
		
		// CHECK COLLECTION NAME
		if (! (arg_collection_name in this._collections) )
		{
			this.warn('find_one_record:collection doesn\'t exist [' + arg_collection_name + ']')
			return Promise.reject('find_one_record:bad collection name [' + arg_collection_name + ']')
		}

		// GET COLLECTION
		const collection = this._collections[arg_collection_name]
		assert( T.isObject(collection) && collection.is_data_collection, context + ':find_one_record:bad data collection object')
		
		return collection.find_one_record(arg_record_id)
	}



	/**
	 * Find existing data records with a query.
	 * 
	 * @param {string} arg_collection_name - data collection name.
	 * @param {DataQuery} arg_query - data query.
	 * 
	 * @returns {Promise} - Promise of DataRecordArray.
	 */
	find_records(arg_collection_name, arg_query)
	{
		assert( T.isString(arg_collection_name), context + ':find_records:bad data collection string')
		assert( T.isObject(arg_query) && arg_query.is_data_query, context + ':find_records:bad query object')
		
		// CHECK COLLECTION NAME
		if (! (arg_collection_name in this._collections) )
		{
			this.warn('find_records:collection doesn\'t exist [' + arg_collection_name + ']')
			return Promise.reject('find_records:bad collection name [' + arg_collection_name + ']')
		}

		// GET COLLECTION
		const collection = this._collections[arg_collection_name]
		assert( T.isObject(collection) && collection.is_data_collection, context + ':find_records:bad data collection object')
		
		return collection.find_records(arg_query)
	}



	/**
	 * Find all existing data records.
	 * 
	 * @param {string} arg_collection_name - data collection name.
	 * 
	 * @returns {Promise} - Promise of DataRecordArray.
	 */
	find_all_records(arg_collection_name)
	{
		assert( T.isString(arg_collection_name), context + ':find_all_records:bad data collection string')
		
		// CHECK COLLECTION NAME
		if (! (arg_collection_name in this._collections) )
		{
			this.warn('find_all_records:collection doesn\'t exist [' + arg_collection_name + ']')
			return Promise.reject('find_all_records:bad collection name [' + arg_collection_name + ']')
		}

		// GET COLLECTION
		const collection = this._collections[arg_collection_name]
		assert( T.isObject(collection) && collection.is_data_collection, context + ':find_all_records:bad data collection object')
		
		return collection.find_all_records()
	}
}

