// NPM IMPORTS
import assert from 'assert'
import lowdb from 'lowdb'
import FileAsync from 'lowdb/adapters/FileSync'

// COMMON IMPORTS
import T              from '../../utils/types'
import RecordProvider from './record_provider'
import runtime        from '../../base/runtime'


/**
 * Contextual constant for this file logs.
 * @private
 */
const context = 'common/datas/providers/jsonfile_record_provider'



/**
 * Json File record provider class.
 * @author Luc BORIES
 * @license Apache-2.0
 */
export default class JsonFileRecordProvider extends RecordProvider
{
    /**
     * Create a record provider instance
     */
	constructor(arg_settings)
	{
		super(arg_settings)

		// GET FILE NAME
		assert( T.isString(arg_settings.filename), context + ':bad settings.filename object')
		/**
		 * Json file provider file name.
		 * @type {string}
		 */
		this.filename = arg_settings.filename

		// GET MODEL NAME
		assert( T.isString(arg_settings.modelname), context + ':bad settings.modelname object')
		/**
		 * Json file provider model name.
		 * @type {string}
		 */
		this.modelname = arg_settings.modelname

		// GET PRIMARY KEY NAME
		assert( T.isString(arg_settings.pkname), context + ':bad settings.pkname object')
		/**
		 * Json file provider primary key name.
		 * @type {string}
		 */
		this.pkname = arg_settings.pkname


		// LOAD FILE DB

		/**
		 * Json file provider low db instance.
		 * @type {object}
		 */
		this.file_db = null

		/**
		 * Json file provider is ready flag.
		 * @type {boolean}
		 */
		this.is_ready = false

		if ( T.isString(this.filename) )
		{
			assert(runtime, context + ':bad runtime instance')
			const json_full_path = runtime.get_absolute_resources_path(this.filename)
			assert( T.isString(json_full_path), context + ':constructor:bad file path string')

			// OPEN DATABASE
			const db_settings = {
				autosave:true
			}
			this.file_adapter = new FileAsync(json_full_path, db_settings)
			this.file_db = lowdb(this.file_adapter)

			this.is_ready = T.isFunction(this.file_db)
		}
	}
    
    
    /**
     * Build a query to fetch datas.
     * @param {object|undefined} arg_query - optional query context
     * @returns {Promise} datas record promise
     */
	build_query(arg_query)
	{
		// logs.debug(context, 'build_query:enter')
		assert(this.is_ready, context + ':build_query:db not ready')


		// logs.debug(context, 'build_query:leave')
		return arg_query
	}


    /**
     * Provide all datas records
     * @param {object|undefined} arg_query - optional query context
     * @returns {Promise} datas record promise
     */
	find_all_records(arg_query)
	{
		// logs.debug(context, 'find_all_records:enter')
		assert(this.is_ready, context + ':find_all_records:db not ready')

		const query = this.build_query(arg_query)

			// EXECUTE QUERY
		try{
			const records = this.file_db(this.modelname).find(query)
			if (records)
			{
				// logs.debug(context, 'find_all_records:leave')
				return Promise.resolve(records)
			}
		}
		catch(e)
		{
			//  console.log('authenticate user error', e)
		}

		// logs.debug(context, 'find_all_records:leave not found')
		return Promise.resolve(null)
	}
    
    
    /**
     * Find a record by its id.
     * @param {string|number} arg_id - record id
     * @returns {Promise} - promise of found record or null
     */
	find_records_by_id(arg_id)
	{
		// logs.debug(context, 'find_record_by_id:enter')
		assert(this.is_ready, context + ':find_records_by_id:db not ready')
		assert( T.isString(arg_id) || T.isNumber(arg_id), context + ':find_record_by_id:bad id string or number')

		let query = {}
		query[this.pkname] = arg_id
		const records_promise = this.find_record_by_values(query)

		// logs.debug(context, 'find_record_by_id:leave')
		return records_promise
	}
    
    
    /**
     * Find a record with a set of values.
     * @param {object} arg_values_map - values map
     * @param {object|undefined} arg_query - optional query context
     * @returns {Promise} - promise of found records or null value
     */
	find_records_by_values(arg_values_map, arg_query)
	{
		// logs.debug(context, 'find_records_by_values:enter')
		assert(this.is_ready, context + ':build_query:db not ready')
		assert( T.isObject(arg_values_map), context + ':find_record_by_values:bad values object')

		// UPDATE QUERY WITH VALUES
		arg_query = T.isObject(arg_query) ? arg_query : {}
		Object.keys(arg_values_map).forEach(
			(value) => {
				arg_query[value] = arg_values_map[value]
			}
		)
		const query = this.build_query(arg_query)

		// EXECUTE QUERY
		try{
			const records = this.file_db(this.modelname).find(query)
			if (records)
			{
				// logs.debug(context, 'find_record_by_values:leave')
				// console.debug(context, 'find_record_by_values:leave')
				return Promise.resolve(records)
			}
			
			console.error( this.file_db(this.modelname) )
		}
		catch(e)
		{
			//  console.log('authenticate user error', e)
		}

		// logs.debug(context, 'find_record_by_values:leave not found')
		// console.debug(context, 'find_record_by_values:leave not found', arg_values_map)
		return Promise.resolve(null)
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T                       from '../../utils/types'
import {is_browser, is_server} from '../../utils/is_browser'


/**
 * Contextual constant for this file logs.
 * @private
 */
const context = 'common/datas/providers/provider'



/**
 * Local file source.
 * @private
 */
export const SOURCE_LOCAL_FILE = 'local_file'

/**
 * master node source.
 * @private
 */
export const SOURCE_MASTER = 'master'

/**
 * Message bus source.
 * @private
 */
export const SOURCE_MSG_BUS = 'message_bus'

/**
 * Remote url source.
 * @private
 */
export const SOURCE_REMOTE_URL = 'remote_url'

/**
 * Sql database source.
 * @private
 */
export const SOURCE_SQL_DATABASE = 'sql_database'

/**
 * NoSql database source.
 * @private
 */
export const SOURCE_NOSQL_DATABASE = 'nosql_database'

/**
 * All sources.
 * @private
 */
const SOURCES = [SOURCE_LOCAL_FILE, SOURCE_MASTER, SOURCE_MSG_BUS, SOURCE_REMOTE_URL, SOURCE_SQL_DATABASE, SOURCE_NOSQL_DATABASE]



// GET RUNTIME
/**
 * Runtime source file on server.
 * @private
 */
const server_runtime_file = '../../base/runtime'

/**
 * Runtime instance.
 * @private
 */
let runtime = undefined

if (is_server())
{
	runtime = require(server_runtime_file).default
}

else if (is_browser())
{
	runtime = window.devapt().runtime()
}



/**
 * Json providier class.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 */
export default class JsonProvider
{
    /**
     * Create a Provider instance.
	 * 
	 * @param {object} arg_settings - provider config.
	 * 
	 * @returns {nothing}
     */
	constructor(arg_settings)
    {
		// CHECK SETTINGS
		assert( T.isObject(arg_settings), context + ':bad settings object')
		/**
		 * Json provider settings.
		 * @type {object}
		 */
		this.$settings = arg_settings.toJS()

		// GET PROVIDER SOURCE
		assert( T.isString(this.$settings.source), context + ':bad settings.source string')
		/**
		 * Json provider source.
		 * @type {string}
		 */
		this.source = this.$settings.source
		assert( SOURCES.indexOf(this.source) > -1, context + ':bad source string, should be part of [' + SOURCES + ']')
	}

    
    
    /**
     * Provide JSON datas.
	 * 
     * @returns {object} JSON datas Promise.
     */
	provide_json()
	{
		const self = this
		// logs.debug(context, 'enter')

		let promise = new Promise(
			function(resolve, reject)
			{
				self.provide_json_self(resolve, reject)
			}
		)

		// logs.debug(context, 'leave')
		return promise
	}

    
    
    /**
     * Provide JSON datas inside a promise.
	 * 
     * @param {function} resolve - a promise should be resolved.
     * @param {function} reject - a promise should be rejected.
	 * 
     * @returns {nothing}
     */
	provide_json_self(resolve, reject)
	{
		// logs.debug(context + ':provide_json_self', this.source)

		switch(this.source)
		{
			case SOURCE_LOCAL_FILE: {
				assert( T.isString(this.$settings.relative_path), context + ':bad settings.relative_path string')

				const file_path = this.$settings.relative_path
				// logs.debug('file_path', file_path)

				if ( T.isString(file_path) )
				{
					// logs.debug('Node is master: load settings file', file_path)

					const absolute_file_path = runtime.context.get_absolute_path(file_path)
					const json = require(absolute_file_path)

					// console.log(context + '.json', json)

					// logs.debug(context, 'leave')
					resolve(json)
					return
				}

				// logs.debug('file_path', file_path)
				// logs.error(context + ':bad file path string')
				break
			}
			
			case SOURCE_MASTER: {
				console.log(context + ':provide_json_self:SOURCE_MASTER begin')
				const node = runtime.node
				const master_name = node.master_name
				assert( T.isString(master_name), context + ':provide_json_self:bad master name string')
				const delay = T.isNumber(this.$settings.delay) ? this.$settings.delay : 0

				// WAIT FOR BUS GATEWAY IS STARTED AND CONNECTED TO THE LOCAL BUS
				const do_cb = () => {
					node.on_registering_callback = resolve
					node.register_to_master()
					console.log(context + ':provide_json_self:SOURCE_MASTER end')
				}
				setTimeout(do_cb, delay)

				return
			}
			
			case SOURCE_MSG_BUS: {
				// TODO
				break
			}

			case SOURCE_REMOTE_URL: {
				// TODO
				break
			}

			case SOURCE_SQL_DATABASE: {
				// TODO
				break
			}

			case SOURCE_NOSQL_DATABASE: {
				// TODO
				break
			}

			default:{
				console.error(context + ':bad provider source string [' + this.source + ']')
			}
		}

		reject('bad source')
		// logs.debug(context + ':leave')
	}
}

