// NPM IMPORTS
import winston from 'winston'

// COMMON IMPORTS
import T from '../utils/types'
import Logger from './logger'


// const context = 'common/loggers/logger_winston'



const customLevels = {
	levels: {
		debug: 3,
		info: 2,
		warn: 1,
		error: 0
	},
	colors: {
		debug: 'blue',
		info: 'green',
		warn: 'yellow',
		error: 'red'
	}
}



/**
 * @file Winston logger class.
 * @author Luc BORIES
 * @license Apache-2.0
 */
export default class LoggerWinston extends Logger
{
	/**
	 * Create a Winston Logger instance.
	 * @param {string} arg_context - trace context.
	 * @returns {nothing}
	 */
	constructor(arg_enabled, arg_settings)
	{
		super(arg_enabled)
		
		this.is_logger_winston = true

		let transports = []
		
		// console.log(arg_settings, context + ':' + arg_settings)
		if ( T.isObject(arg_settings) )
		{
			if ( T.isObject(arg_settings.transports) )
			{
				// console.log(context + ':transport config found', arg_settings.transports)
				const transports_names = Object.keys(arg_settings.transports)
				transports_names.forEach(
					(transport_name) => {
						const transport_cfg = arg_settings.transports[transport_name]
						
						transport_cfg.name = transport_name

						if ( ! T.isString(transport_cfg.type) )
						{
							return
						}
						
						switch(transport_cfg.type)
						{
							case 'file': {
								const transport = this.create_file_transport(transport_cfg)
								transports.push(transport)
								break
							}
							case 'console': {
								const transport = this.create_console_transport(transport_cfg)
								transports.push(transport)
								break
							}
						}
					}
				)
			}
		}
		
		if ( transports.length == 0 )
		{
			// console.log(context + ':no transport config found')
			const console_transport = this.create_console_transport(undefined)
			const file_transport = this.create_file_transport(undefined)
			transports.push(console_transport)
			transports.push(file_transport)
		}
		
		const logger_cfg = {
			levels: customLevels.levels,
			colors: customLevels.colors,
			transports: transports
		}
		
		this.logger = new (winston.Logger)(logger_cfg)

		winston.addColors(customLevels.colors)
	}
	
	
	
	/**
	 * Create a console transport for Winston logger.
	 * @param {object} arg_transport_cfg - console transport settings.
	 * @returns {winston.transports.Console}
	 */
	create_console_transport(arg_transport_cfg)
	{
		arg_transport_cfg = T.isObject(arg_transport_cfg) ? arg_transport_cfg : {}

		// DEFAULT CONFIGURATION
		const default_transport_cfg = {
			level: 'debug',
			
			timestamp: false,
			
			formatter: function(options)
			{
				const msg = undefined !== options.message ? options.message : ''
				const meta = options.meta && Object.keys(options.meta).length ? options.meta : undefined
				// const payload = meta ? '\n\t' + JSON.stringify(meta) : '' 
				return /*options.timestamp().toString().substr(-6) +*/' pid='+ process.pid + ' '+ winston.config.colorize( options.level, options.level.toUpperCase() )
					+ msg
					+ '\n\t' + meta[0] // TS
					+ ' : ' + meta[1] // CONTEXT
					+ ' : ' + meta[2] // NAME
					+ ' : ' + meta[4] // ACTION
					+ ' : ' + meta[3] // ?
					+ '\n\t text = ' + meta[5] // TEXT
			},
			
			colorize:true
		}
		
		// CHECK CONFIGURATION
		if ( ! T.isString(arg_transport_cfg.level) || ! (arg_transport_cfg.level in customLevels.levels) )
		{
			arg_transport_cfg.level = default_transport_cfg.level
		}
		if ( ! T.isFunction(arg_transport_cfg.timestamp) )
		{
			arg_transport_cfg.timestamp = default_transport_cfg.timestamp
		}
		if ( ! T.isFunction(arg_transport_cfg.formatter) )
		{
			arg_transport_cfg.formatter = default_transport_cfg.formatter
		}
		
		return new (winston.transports.Console)(arg_transport_cfg)
	}
	
	
	/**
	 * Create a file transport for Winston logger.
	 * @param {object} arg_transport_cfg - file transport settings.
	 * @returns {winston.transports.File}
	 */
	create_file_transport(arg_transport_cfg)
	{
		arg_transport_cfg = T.isObject(arg_transport_cfg) ? arg_transport_cfg : {}
		
		// DEFAULT CONFIGURATION
		const default_transport_cfg = {
			level: 'debug',
			
			timestamp: function()
			{
				return Date.now()
			},
			
			formatter: function(options)
			{
				return options.timestamp().toString().substr(-6) +' '+ process.pid +' '+ options.level.toUpperCase() +' '+ (undefined !== options.message ? options.message : '') +
					(options.meta && Object.keys(options.meta).length ? '\n\t'+ JSON.stringify(options.meta) : '' )
			},
			
			filename: './tmp/debug.log',
			maxsize:100000,
			maxFiles:2
		}
		const max_size = 100000000
		const max_files = 100
		
		// CHECK CONFIGURATION
		if ( ! T.isString(arg_transport_cfg.level) || ! (arg_transport_cfg.level in customLevels.levels) )
		{
			arg_transport_cfg.level = default_transport_cfg.level
		}
		if ( ! T.isFunction(arg_transport_cfg.timestamp) )
		{
			arg_transport_cfg.timestamp = default_transport_cfg.timestamp
		}
		if ( ! T.isFunction(arg_transport_cfg.formatter) )
		{
			arg_transport_cfg.formatter = default_transport_cfg.formatter
		}
		if ( ! T.isString(arg_transport_cfg.filename) )
		{
			arg_transport_cfg.filename = default_transport_cfg.filename
		}
		if ( ! T.isNumber(arg_transport_cfg.maxsize) )
		{
			arg_transport_cfg.maxsize = default_transport_cfg.maxsize
		}
		if ( ! T.isNumber(arg_transport_cfg.maxFiles) )
		{
			arg_transport_cfg.maxFiles = default_transport_cfg.maxFiles
		}
		arg_transport_cfg.maxsize = Math.min(arg_transport_cfg.maxsize, max_size)
		arg_transport_cfg.maxFiles = Math.min(arg_transport_cfg.maxFiles, max_files)
		
		return new (winston.transports.File)(arg_transport_cfg)
	}
	
	
	
	/**
	 * Logger DEBUG implementation.
	 * 
	 * @param {array} arg_opds - log record array.
	 * 
	 * @returns {nothing}
	 */
	debug_self(arg_opds)
	{
		this.logger.log('debug', arg_opds)
	}
	
	
	/**
	 * Logger INFO implementation.
	 * 
	 * @param {array} arg_opds - log record array.
	 * 
	 * @returns {nothing}
	 */
	info_self(arg_opds)
	{
		this.logger.log('info', arg_opds)
	}
	
	
	/**
	 * Logger WARN implementation.
	 * 
	 * @param {array} arg_opds - log record array.
	 * 
	 * @returns {nothing}
	 */
	warn_self(arg_opds)
	{
		this.logger.log('warn', arg_opds)
	}
	
	
	/**
	 * Logger ERROR implementation.
	 * 
	 * @param {array} arg_opds - log record array.
	 * 
	 * @returns {nothing}
	 */
	error_self(arg_opds)
	{
		this.logger.log('error', arg_opds)
	}
}

// NPM IMPORTS
import assert from 'assert'
import {format} from 'util'

// COMMON IMPORTS
import T                  from '../utils/types'
import Instance           from '../base/instance'
import DistributedMessage from '../base/distributed_message'
import DistributedLogs    from '../base/distributed_logs'
import DistributedMetrics from '../base/distributed_metrics'
import Stream             from './stream'


const context = 'common/messaging/message_bus'
const MAX_PAGE_SIZE = 999999
const MIN_PAGE_SIZE = 9



/**
 * @file Message bus class.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 */
export default class MessageBus extends Instance
{
	/**
	 * Create a bus.
	 * 
	 * API:
	 *   ->constructor(arg_name, arg_settings, arg_log_context).
	 * 
	 * 	Engine API:
	 *   ->get_bus_engine():BusEngine
	 * 
	 *  Stream API:	
	 *   ->get_input_stream():Stream - get input stream to populate the bus.
	 * 
	 * 	Message API:
	 * 	 ->msg_recipients(arg_page_size=999999, arg_page_index=0):array - get paged recipients list.
	 *   ->msg_post(arg_msg:DistributedMessage):boolean - send a DistributedMessage instance.
	 * 
	 *   ->msg_subscribe(arg_channel:string, arg_handler:f(msg), arg_filter:string|object):unsubscribe function - subscribe to messages of the bus.
	 * 
	 *   ->msg_register(arg_distributed_instance,arg_channel,arg_method='receive_msg'):function - register a DistributedInstance recipient.
	 *   ->msg_has_recipient(arg_name):boolean - test if the bus has given named recipient.
	 *   ->msg_add_recipient(arg_name, arg_instance='remote') - add a bus recipient.
	 *   ->msg_remove_recipient(arg_name) - remove a bus recipient.
	 * 
	 *   ->normalize_msg(arg_msg):DistributedMessage|undefined - Normalize given message.
	 * 
	 * @param {string}    arg_name - instance name.
	 * @param {BusEngine} arg_bus_engine - bus engine.
	 * @param {object}    arg_settings - settings.
	 * @param {string}    arg_log_context - trace context.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_name, arg_bus_engine, arg_settings, arg_log_context)
	{
		super('buses', 'MessageBus', arg_name, arg_settings, arg_log_context ? arg_log_context : context)
		
		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_message_bus = true

		this._bus_engine = arg_bus_engine

		this._input_stream = new Stream()

		this._recipients = {}
		this._recipients_handlers = {}
	}
	

	
	/**
	 * Get bus engine.
	 * 
	 * @returns {BusEngine}
	 */
	get_bus_engine()
	{
		assert( T.isObject(this._bus_engine) && this._bus_engine.is_bus_engine, this.get_context() + ':get_bus_engine:bad bus engine instance')
		return this._bus_engine
	}
	
	
	
	/**
	 * Get stream to populate the bus.
	 * 
	 * @returns {Stream} - input bus stream.
	 */
	get_input_stream()
	{
		return this._input_stream
	}



	/**
	 * Get paged recipients list.
	 * 
	 * @param {integer} arg_page_size  - recipients list page size.
	 * @param {integer} arg_page_index - recipients list page index.
	 * 
	 * @returns {object} - paged result { count:recipients count, page_size:..., page_count:..., page_index:..., page_values:[] }
	 */
	msg_recipients(arg_page_size=MAX_PAGE_SIZE, arg_page_index=0)
	{
		const recipients = Object.keys(this._recipients)
		const count = recipients.length
		const page  = T.isNumber(arg_page_size) && arg_page_size > MIN_PAGE_SIZE && arg_page_size < MAX_PAGE_SIZE ? arg_page_size : MAX_PAGE_SIZE
		const index = T.isNumber(arg_page_index) && arg_page_index >= 0 ? arg_page_index : 0

		const values = count <= page ? recipients : recipients.slice(index * page, page)
		return {
			count:count,
			page_size:page,
			page_count:Math.floor(count / page) + ( (count - Math.floor(count / page)) > 0 ? 1 : 0),
			page_index:index,
			page_values:values
		}
	}



	/**
	 * Send a message into a channel.
	 * 
	 * @param {DistributedMessage} arg_msg - distributed message instance.
	 * 
	 * @returns {boolean} - success or failure.
	 */
	msg_post(arg_msg)
	{
		try{
			assert( T.isObject(arg_msg) && arg_msg.is_distributed_message, this.get_context() + ':msg_post:bad message instance')
			assert( T.isObject(this._bus_engine) && this._bus_engine.is_bus_engine, this.get_context() + ':msg_post:bad message instance')
			
			const channel = arg_msg.get_channel()
			assert( T.isString(channel), this.get_context() + ':msg_post:bad channel name')

			// ADD BUS NAME TO MESSAGE PATH
			const step_name = this.get_name()
			if (arg_msg.has_buses_step(step_name))
			{
				// DEBUG
				const payload_str = JSON.stringify(arg_msg.get_payload())
				this.debug( format('msg_post:bus step exists:engine=[%s] step=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), step_name, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str) )
				// console.log(context + ':msg_post:bus step exists:engine=[%s] step=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), step_name, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str)
				
				return false
			}
			arg_msg.add_buses_step(step_name)

			// DEBUG
			const payload_str = JSON.stringify(arg_msg.get_payload())
			this.debug( format('msg_post:new bus step:engine=[%s] step=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), step_name, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str) )
			// console.log(context + ':msg_post:new bus step:engine=[%s] step=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), step_name, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str)
			
			// LOCAL RECIPIENT
			const recipient_is_local = this.msg_has_recipient(arg_msg.get_target())
			if (recipient_is_local)
			{
				const recipient = this._recipients[arg_msg.get_target()]
				if ( T.isObject(recipient) || recipient == 'browser' )
				{
					this.debug( format('msg_post:recipient is local and is an instance or browser:engine=[%s] step=[%s] channel=[%s] sender=[%s] target=[%s]', this.get_name(), step_name, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target() ) )
					const handler = this._recipients_handlers[arg_msg.get_target()]
					if ( T.isFunction(handler) )
					{
						handler(arg_msg)
						return true
					}
					
					console.warn(context + ':msg_post:failure with error:not or bad handler for local recipient')
					return false
				}

				this.debug( format('msg_post:recipient is local but not an instance:engine=[%s] step=[%s] channel=[%s] sender=[%s] target=[%s]', this.get_name(), step_name, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target() ) )
			}

			// REMOTE RECIPIENT
			this._bus_engine.channel_send(channel, arg_msg)

			return true
		} catch(e) {
			console.warn(context + ':msg_post:failure with error:' + e)
			return false
		}
	}
	
	
	
	/**
	 * Subscribe on channel inputs.
	 * 
	 * @param {string} arg_channel - channel name string.
	 * @param {function} arg_handler - f(payload)
	 * @param {string|function} arg_filter - filter string or function.
	 * 
	 * @returns {function} - unsubscribe function.
	 */
	msg_subscribe(arg_channel, arg_handler, arg_filter=undefined)
	{
		assert( T.isFunction(arg_handler), this.get_context() + ':msg_subscribe:bad handler function')
		assert( T.isObject(this._bus_engine) && this._bus_engine.is_bus_engine, this.get_context() + ':msg_subscribe:bad bus engine instance')
		
		// DEBUG
		// debugger
		this.debug( format('msg_subscribe:engine=[%s] filter=[%s] channel=[%s]', this.get_name(), arg_filter, arg_channel) )
		// console.log(context + ':msg_subscribe:same channel:engine=[%s] filter=[%s] channel=[%s]', this.get_name(), arg_filter, arg_channel)

		// REGISTER RECIPIENT
		if ( T.isString(arg_filter) )
		{
			this.msg_add_recipient(arg_filter, undefined)
		}
		
		// MESSAGE HANDLER
		const msg_handler = (arg_msg)=>{
			arg_msg = this.normalize_msg(arg_msg)

			// DEBUG
			// debugger

			// CHANNEL NAME FILTERING
			const channel = arg_msg.get_channel()
			assert( T.isNotEmptyString(channel), this.get_context() + ':msg_subscribe:msg_handler:bad channel name')
			const same_channel = channel == arg_channel

			// DEBUG
			this.debug( format('msg_subscribe:same channel:engine=[%s] filter=[%s] channel=[%s] handler channel=[%s] same channel[%d]', this.get_name(), arg_filter, channel, arg_channel, same_channel) )
			// console.log(context + ':msg_subscribe:same channel:engine=[%s] filter=[%s] channel=[%s] handler channel=[%s] same channel[%d]', this.get_name(), arg_filter, channel, arg_channel, same_channel)
				
			if (! same_channel)
			{
				// DEBUG
				this.debug( format('msg_subscribe:skip channel:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] handler channel=[%s]', this.get_name(), arg_filter, channel, arg_msg.get_sender(), arg_msg.get_target(), arg_channel) )
				// console.log(context + ':msg_subscribe:skip channel:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] handler channel=[%s]', this.get_name(), arg_filter, channel, arg_msg.get_sender(), arg_msg.get_target(), arg_channel)
				return
			}

			// TARGET NAME FILTERING
			if ( T.isNotEmptyString(arg_filter) && arg_filter != arg_msg.get_target() )
			{
				// DEBUG
				this.debug( format('msg_subscribe:skip target:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] handler channel=[%s]', this.get_name(), arg_filter, channel, arg_msg.get_sender(), arg_msg.get_target(), arg_channel) )
				// console.log(context + ':msg_subscribe:skip target:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] handler channel=[%s]', this.get_name(), arg_filter, channel, arg_msg.get_sender(), arg_msg.get_target(), arg_channel)
				return
			}
			if ( this.msg_has_recipient( arg_msg.get_target() ) )
			{
				// DEBUG
				const payload_str = JSON.stringify(arg_msg.get_payload())
				this.debug( format('msg_subscribe:msg handled with filter:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), arg_filter, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str) )
				// console.log(context + ':msg_subscribe:msg handled with filter:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), arg_filter, arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str)
				
				arg_handler(arg_msg)
				return
			}

			// NO FILTERING
			// DEBUG
			const payload_str = JSON.stringify(arg_msg.get_payload())
			this.debug( format('msg_subscribe:msg handled without filter:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), '', arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str) )
			// console.log(context + ':msg_subscribe:msg handled without filter:engine=[%s] filter=[%s] channel=[%s] sender=[%s] target=[%s] data=[%s]', this.get_name(), '', arg_msg.get_channel(), arg_msg.get_sender(), arg_msg.get_target(), payload_str)
			
			arg_handler(arg_msg)
		}
		
		if ( T.isString(arg_filter) && arg_filter in this._recipients_handlers )
		{
			this._recipients_handlers[arg_filter] = msg_handler
		}

		const fn_filter = T.isFunction(arg_filter) ? arg_filter : undefined
		const str_filter = T.isString(arg_filter) ? (msg)=>{ return arg_filter == msg._target } : undefined
		const predicate = fn_filter || str_filter
		return this._bus_engine.channel_on(arg_channel, msg_handler, predicate)
	}



	/**
	 * Register a DistributedInstance recipient.
	 * 
	 * @param {DistributedInstance} arg_distributed_instance - distributed recipient instance.
	 * @param {string} arg_channel - channel name string.
	 * @param {string} arg_method  - message handler method name string.
	 * @param {string} arg_alias   - instance alias name string (optional, default undefined).
	 * 
	 * @returns {function} - unsubscribe function.
	 */
	msg_register(arg_distributed_instance, arg_channel, arg_method='receive_msg', arg_alias=undefined)
	{
		assert( T.isObject(arg_distributed_instance) && arg_distributed_instance.is_distributed_instance, this.get_context() + ':msg_register:bad distributed instance.')
		assert( T.isString(arg_method) && (arg_method in arg_distributed_instance), this.get_context() + ':msg_register:bad method name [' + arg_method + '].')
		
		const name = T.isNotEmptyString(arg_alias) ? arg_alias : arg_distributed_instance.get_name()
		const handler = arg_distributed_instance[arg_method].bind(arg_distributed_instance)

		// DEBUG
		this.debug( format('msg_register:engine=[%s] instance=[%s] channel=[%s] method=[%s]', this.get_name(), name, arg_channel, arg_method) )
		// console.log(context + ':msg_register:engine=[%s] instance=[%s] channel=[%s] method=[%s]', this.get_name(), name, arg_channel, arg_method)

		this.msg_add_recipient(name, arg_distributed_instance)
		
		return this.msg_subscribe(arg_channel, handler, name)
	}



	/**
	 * Test if bus has given named recipient.
	 * 
	 * @param {arg_name} arg_name - recipient name.
	 * 
	 * @returns {boolean}
	 */
	msg_has_recipient(arg_name)
	{
		return T.isString(arg_name) && (arg_name in this._recipients)
	}


	/**
	 * Add a recipient.
	 * 
	 * @param {string} arg_name - recipient name.
	 * @param {DistributedInstance} arg_instance - distributed recipient instance (default:'remote').
	 * 
	 * @returns {nothing}
	 */
	msg_add_recipient(arg_name, arg_instance='remote')
	{
		assert( T.isNotEmptyString(arg_name), this.get_context() + ':msg_add_recipient:bad recipient name [' + arg_name + '].')
		assert( arg_instance=='browser' || arg_instance=='remote' || T.isObject(arg_instance) && arg_instance.is_distributed_instance, this.get_context() + ':msg_add_recipient:bad distributed instance.')
		
		if (arg_name in this._recipients)
		{
			return
		}

		this._recipients[arg_name] = arg_instance
		this._recipients_handlers[arg_name] = ()=>{}
	}


	/**
	 * Remove a recipient.
	 * 
	 * @param {string} arg_name - recipient name.
	 * 
	 * @returns {nothing}
	 */
	msg_remove_recipient(arg_name)
	{
		assert( T.isNotEmptyString(arg_name), this.get_context() + ':msg_add_recipient:bad recipient name [' + arg_name + '].')
		
		if (arg_name in this._recipients)
		{
			delete this._recipients[arg_name]
			delete this._recipients_handlers[arg_name]
		}
	}


	/**
	 * Normalize given message.
	 * 
	 * @param {object} arg_msg - message to normalize.
	 * 
	 * @returns {DistributedMessage|undefined} - normalized message.
	 */
	normalize_msg(arg_msg)
	{
		if (! T.isObject(arg_msg) || ! arg_msg.is_distributed_message)
		{
			return undefined
		}

		if ( T.isFunction(arg_msg.get_channel) )
		{
			return arg_msg
		}

		if ( arg_msg.is_distributed_logs)
		{
			return new DistributedLogs(arg_msg)
		}

		if ( arg_msg.is_distributed_metrics)
		{
			return new DistributedMetrics(arg_msg)
		}

		return new DistributedMessage(arg_msg)
	}
}

// NPM IMPORTS
import assert from 'assert'
import _ from 'lodash'
import {format} from 'util'
import SocketIOServer from 'socket.io'
import SocketIOClient from 'socket.io-client'

// COMMON IMPORTS
import T        from '../utils/types'
import BusEngine from './bus_engine'


const context = 'common/messaging/socketio_bus_engine'



/**
 * SocketIO based bus engine class for message bus client or server.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* API:
*   ->constructor(arg_name,arg_settings,arg_log_context,arg_logger_manager).
* 
*	->channel_list():array                           - List engine channels.
*   ->channel_add(arg_channel):nothing               - Add a channel.
*   ->channel_send(arg_channel, arg_payload):nothing - Send a message into a channel.
*   ->channel_on(arg_channel, arg_handler):nothing   - Subscribe on channel inputs.
*   ->channel_transform(arg_in_channel, arg_out_channel, arg_xform_handler):nothing - Transform payload of input channel to output channel.
* 
 */
export default class SocketIOBusEngine extends BusEngine
{
	/**
	 * Create a bus.
	 * 
	 * @param {string} arg_name - instance name.
	 * @param {object} arg_settings - settings.
	 * @param {string} arg_log_context - trace context.
	 * @param {LoggerManager} arg_logger_manager - logger manager object (optional).
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_name, arg_settings, arg_log_context=context, arg_logger_manager=undefined)
	{
		super(arg_name, arg_settings, arg_log_context, arg_logger_manager)
		
		/**
		 * Class type flag.
		 * @type {boolean}
		 */
		this.is_socketio_bus_engine = true
		
		this._server = undefined

		if (this._engine_type == 'Server')
		{
			// DEBUG
			this.debug( format('constructor:create a bus server on port=[%s]', this._engine_port) )
			// console.log(context + ':constructor:create a bus server on port=[%s]', this._engine_port)

			this._server = new SocketIOServer(this._engine_port)
			this._server.serveClient(false)
		}

		this._channels = {}
		this._subscribers = {}
	}
	

	
	/**
	 * List engine channels.
	 * 
	 * @returns {array}
	 */
	channel_list()
	{
		return Object.keys(this._channels)
	}
	

	
	/**
	 * Add a channel.
	 * 
	 * @param {string} arg_channel - channel name.
	 * 
	 * @returns {nothing}
	 */
	channel_add(arg_channel)
	{
		assert( T.isString(arg_channel), this.get_context() + ':channel_add:bad channel name')
		
		// SERVER
		if (this._engine_type == 'Server')
		{
			// INIT SERVER NSP
			this._channels[arg_channel] = this._server.of('/' + arg_channel)

			this._channels[arg_channel].on('connect',
				(socket)=>{
					// DEBUG
					this.debug( format('channel_add:on connect:engine=[%s] channel=[%s] id=[%s]', this.get_name(), arg_channel, socket.id) )
					// console.log(context + ':channel_add:on connect:engine=[%s] channel=[%s] id=[%s]', this.get_name(), arg_channel, socket.id)
					
					this.on_connect(socket, arg_channel)
					
					socket.on('error',
						(value)=>
						{
							// DEBUG
							this.debug( format('channel_add:on error:engine=[%s] channel=[%s] id=[%s] value=', this.get_name(), arg_channel, socket.id, value) )
							// console.log(context + ':channel_add:on error:engine=[%s] channel=[%s] id=[%s] value=', this.get_name(), arg_channel, socket.id, value)
							
							this.on_error(socket, arg_channel, value)
						}
					)
					
					socket.on('bus',
						(value)=>{
							// DEBUG
							this.debug( format('channel_add:on bus:engine=[%s] channel=[%s] id=[%s] value=', this.get_name(), arg_channel, socket.id, value) )
							// console.log(context + ':channel_add:on bus:engine=[%s] channel=[%s] id=[%s] value=', this.get_name(), arg_channel, socket.id, value)

							this.on_bus(socket, arg_channel, value)
						}
					)
				}
			)
			
			this._channels[arg_channel].on('disconnect',
				(socket)=>{
					// DEBUG
					this.debug( format('channel_add:on disconnect:engine=[%s] channel=[%s] id=[%s]', this.get_name(), arg_channel, socket.id) )
					// console.log(context + ':channel_add:on disconnect:engine=[%s] channel=[%s] id=[%s]', this.get_name(), arg_channel, socket.id)

					this.on_disconnect(socket, arg_channel)
				}
			)

			return
		}


		// CLIENT
		this._channels[arg_channel] = SocketIOClient(this._engine_url + '/' + arg_channel)

		// CLIENT DEBUG 
		// console.log('this._channels[%s]', arg_channel, this._channels[arg_channel])

		this._channels[arg_channel].on('bus',
			(value)=>{
				// DEBUG
				this.debug( format('channel_add:on bus:engine=[%s] channel=[%s] id=[%s]', this.get_name(), arg_channel, this._channels[arg_channel].id) )
				// console.log(context + ':channel_add:on bus:engine=[%s] channel=[%s] id=[%s]', this.get_name(), arg_channel, this._channels[arg_channel].id)

				this.on_bus(this._channels[arg_channel], arg_channel, value)
			}
		)
	}



	/**
	 * Send a message into a channel.
	 * 
	 * @param {string} arg_channel - channel name string.
	 * @param {object} arg_payload - payload data object.
	 * 
	 * @returns {nothing}
	 */
	channel_send(arg_channel, arg_payload)
	{
		assert( T.isString(arg_channel), this.get_context() + ':channel_send:bad channel name')
		assert( arg_payload, this.get_context() + ':channel_send:bad payload data')
		assert( arg_channel in this._channels, this.get_context() + ':channel_send:channel [' + arg_channel + '] stream not found')
		
		this._channels[arg_channel].emit('bus', arg_payload)
	}
	
	
	
	/**
	 * Subscribe on channel inputs.
	 * 
	 * @param {string} arg_channel - channel name string.
	 * @param {function} arg_handler - f(payload):nothing
	 * @param {function} arg_predicate - p(payload):boolean
	 * 
	 * @returns {nothing}
	 */
	channel_on(arg_channel, arg_handler, arg_predicate=undefined)
	{
		assert( T.isString(arg_channel), this.get_context() + ':channel_on:bad channel name on engine [' + this.get_name() + ']')
		assert( T.isFunction(arg_handler), this.get_context() + ':channel_on:bad handler function on engine [' + this.get_name() + '] for channel [' + arg_channel + ']')
		assert( arg_channel in this._channels, this.get_context() + ':channel_on:channel [' + arg_channel + '] stream not found on engine [' + this.get_name() + ']')
		
		// DEBUG
		this.debug( format('channel_on:bus[' + this.get_name() + '] channel [' + arg_channel + '] predicate=[%s]', arg_predicate ? arg_predicate.toString() : '') )
		// console.log(context + ':channel_on:bus[' + this.get_name() + '] channel [' + arg_channel + ']')

		const handler = (value) => {
			// DEBUG
			this.debug( format('channel_on:bus=[' + this.get_name() + '] channel=[' + arg_channel + '] received value=', value) )
			// console.log(context + ':channel_on:bus=[' + this.get_name() + '] channel=[' + arg_channel + '] received value=', value)
			
			// FILTER BY PREDICATE
			if ( T.isFunction(arg_predicate) )
			{
				if ( arg_predicate(value) )
				{
					arg_handler(value)
					return
				}
			}
			
			// NO VALID PREDICATE
			arg_handler(value)
		}
		
		if (! (arg_channel in this._subscribers) )
		{
			this._subscribers[arg_channel] = []
		}
		this._subscribers[arg_channel].push(handler)

	}

	on_connect(arg_socket, arg_channel)
	{
		//
	}

	on_disconnect(arg_socket, arg_channel)
	{
		//
	}

	on_error(arg_socket, arg_channel, arg_value)
	{
		//
	}

	on_bus(arg_socket, arg_channel, arg_value)
	{
		_.forEach(
			this._subscribers[arg_channel],
			(arg_handler)=>{
				arg_handler(arg_value)
			}
		)
	}
}

// NPM IMPORTS
import assert from 'assert'
import Baconjs from 'baconjs'

// COMMON IMPORTS
import T from '../utils/types'

let context = 'common/messaging/stream'



/**
 * Stream class for BaconJS stream wrapping.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 */
export default class Stream
{
	/**
	 * Create a stream.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_stream=undefined)
	{
		this.is_stream = true

		this._source_stream = arg_stream ? arg_stream : new Baconjs.Bus()
		this._transformed_stream = this._source_stream
		
		this.counters = {}
		this.counters.msg_count = 0
		this.counters.msg_size = 0
		this.counters.errors_count = 0
		this.counters.subscribers_count = 0
		
		this._source_stream.onError(
			() => {
				this.counters.errors_count += 1
			}
		)
	}



	/**
	 * Get input stream.
	 * 
	 * @returns {Baconjs.Bus}
	 */
	get_source_stream()
	{
		return this._source_stream
	}



	/**
	 * Create a Stream instance with a DOM event source stream.
	 * 
	 * @param {string} arg_dom_elem - DOM element.
	 * @param {string} arg_event_name - DOM event name.
	 * 
	 * @returns {Stream}
	 */
	static from_dom_event(arg_dom_elem, arg_event_name)
	{
		return new Stream( Baconjs.fromEvent(arg_dom_elem, arg_event_name) )
	}



	/**
	 * Create a Stream instance with an event emitter source stream.
	 * 
	 * @param {object} arg_emitter - event emitter.
	 * @param {string} arg_event_name - event name.
	 * 
	 * @returns {Stream}
	 */
	static from_emitter_event(arg_emitter, arg_event_name)
	{
		return new Stream( Baconjs.fromEvent(arg_emitter, arg_event_name) )
	}



	/**
	 * Get output stream.
	 * 
	 * @returns {Baconjs.Bus}
	 */
	get_transformed_stream()
	{
		return this._transformed_stream
	}



	/**
	 * Set output stream.
	 * 
	 * @param {Baconjs.Bus} arg_stream - transformed stream.
	 * 
	 * @returns {Stream} - this
	 */
	set_transformed_stream(arg_stream)
	{
		this._transformed_stream = arg_stream
		return this
	}



	/**
	 * Set output stream transformation.
	 * 
	 * @param {function} arg_stream_transformation - function (source stream)=>{ return transformed stream }.
	 * 
	 * @returns {Stream} - this
	 */
	set_transformation(arg_stream_transformation)
	{
		assert( T.isFunction(arg_stream_transformation), context + ':transform:bad function')
		const src = this._source_stream
		const tr = this._transformed_stream

		try {
			this._transformed_stream = arg_stream_transformation(src)
		} catch(e) {
			this._transformed_stream = tr
			console.error(context + ':set_transformation', e)
		}
		
		return this
	}
	
	
	
	/**
	 * Get counters snapshot.
	 * 
	 * @returns {object} - counters.
	 */
	get_counters_snapshot()
	{
		const counters = Object.assign({}, this.counters)
		
		return counters
	}
	
	
	
	/**
	 * Get counters snapshot and reset values to 0.
	 */
	get_and_reset_counters_snapshot()
	{
		const counters = Object.assign({}, this.counters)
		
		this.counters.msg_count = 0
		this.counters.msg_size = 0
		this.counters.errors_count = 0
		this.counters.subscribers_count = 0
		
		return counters
	}
	
	
	
	/**
	 * Push a value into the stream.
	 * 
	 * @param {any}
	 * 
	 * @returns {Stream} - this.
	 */
	push(arg_value)
	{
		this.counters.msg_count += 1
		
		// console.log(arg_value,  context + ':push:value')
		this._source_stream.push(arg_value)
		return this
	}
	
	
	
	/**
	 * Subscribe to stream values.
	 * 
	 * @param {Function} arg_handler - value handler f(value) => nothing.
	 * 
	 * @returns {Function} - unsubscribe function
	 */
	subscribe(arg_handler)
	{
		assert( T.isFunction(arg_handler), context + ':subscribe:bad handler function')
		
		this.counters.subscribers_count += 1
		
		const unsubscribe = this._transformed_stream.onValue(arg_handler)
		return  () => {
			this.counters.subscribers_count -= 1
			unsubscribe()
		}
		
		// return this._transformed_stream.onValue(
		// 	(value) => {
		// 		console.log(value,  context + ':subscribe:value')
		// 	}
		// )
	}
	
	
	
	/**
	 * Subscribe to stream errors.
	 * 
	 * @param {Function} arg_handler - value handler f(value) => nothing.
	 * 
	 * @returns {Function} - unsubscribe function
	 */
	on_error(arg_handler)
	{
		assert( T.isFunction(arg_handler), context + ':subscribe:bad handler function')
		
		this.counters.subscribers_count += 1
		
		const unsubscribe = this._transformed_stream.onError(arg_handler)
		return  () => {
			this.counters.subscribers_count -= 1
			unsubscribe()
		}
	}



	/**
	 * Debounce immediate.
	 * 
	 * @param {integer} arg_milliseconds - number of milliseconds. 
	 */
	debounce_immediate(arg_milliseconds)
	{
		this._transformed_stream = this._transformed_stream.debounceImmediate(arg_milliseconds)
		return this
	}
}

// NPM IMPORTS
// import assert from 'assert'
import _ from 'lodash'

// COMMON IMPORTS
import T               from '../utils/types'
import Instance        from '../base/instance'
import Stream          from '../messaging/stream'


const context = 'common/messaging/streams_provider'



/**
 * Service provider base class.
 * @abstract
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
 * 	API:
 * 		->add_stream(arg_stream_name, arg_stream=undefined):nothing - register a new stream.
 * 		->remove_stream(arg_stream_name):nothing - unregister a stream.
 * 		->get_stream(arg_stream_name):Stream - get a registered stream.
 * 		->subscribe(arg_stream_name, arg_subscriber)
 * 		->unsubscribe(arg_stream_name, arg_subscriber)
 * 
 */
export default class SteamsProvider extends Instance
{
	/**
	 * Create a streams provider.
	 * 
	 * @param {string} arg_collection - collection name.
	 * @param {string} arg_class - class name.
	 * @param {string} arg_name - instance name.
	 * @param {Immutable.Map|object} arg_settings - settings plain object
	 * @param {string} arg_log_context - log context.
	 * @param {LoggerManager} arg_logger_manager - logger manager object (optional).
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_collection, arg_class, arg_name, arg_settings, arg_log_context, arg_logger_manager)
	{
		super(arg_collection, arg_class, arg_name, arg_settings, arg_log_context, arg_logger_manager)
		
		this.is_streams_provider = true

		this._streams = {}
	}
	
	

	/**
	 * Register a new stream.
	 * 
	 * @param {string} arg_stream_name - stream name.
	 * @param {Stream} arg_stream - stream instance (optional, default undefined).
	 * 
	 * @returns {nothing}
	 */
	add_stream(arg_stream_name, arg_stream=undefined)
	{
		if (arg_stream_name in this._streams)
		{
			return
		}
		
		// INIT REGISTERING RECORD
		this._streams[arg_stream_name] = {
			subscribers:[],
			stream:undefined,
			own_stream:true,
			unsubscribe:undefined
		}

		// CREATE STREAM IF NEEDED
		if ( ! T.isObject(arg_stream) || ! arg_stream.is_stream )
		{
			arg_stream = new Stream()
			this._streams[arg_stream_name].own_stream = true
		}
		this._streams[arg_stream_name].stream = arg_stream

		// HANDLE POST
		const post_cb = (v) => {
			// console.log(context + ':on stream value for provider %s',  arg_provider_name)
			this.post_to_subscribers(arg_stream_name, v)
		}
		this._streams[arg_stream_name].unsubscribe = this._streams[arg_stream_name].stream.subscribe(post_cb)
	}
	
	

	/**
	 * Unregister a stream.
	 * 
	 * @param {string} arg_stream_name - stream name.
	 * 
	 * @returns {boolean}
	 */
	remove_stream(arg_stream_name)
	{
		if (arg_stream_name in this._streams)
		{
			const record = this._streams[arg_stream_name]

			record.unsubscribe()

			_.forEach(record.subscribers,
				(subscriber)=>{
					this.unsubscribe(arg_stream_name, subscriber)
				}
			)

			if (record.own_stream)
			{
				delete record.stream
			}

			delete this._streams[arg_stream_name]
			return true
		}
		return false
	}
	
	

	/**
	 * Get a stream.
	 * 
	 * @param {string} arg_stream_name - stream name.
	 * 
	 * @returns {Stream}
	 */
	get_stream(arg_stream_name)
	{
		if (arg_stream_name in this._streams)
		{
			const record = this._streams[arg_stream_name]
			return record.stream
		}
		return undefined
	}
	
	

	/**
	 * Test if a stream is registered.
	 * 
	 * @param {string} arg_stream_name - stream name.
	 * 
	 * @returns {boolean}
	 */
	has_stream(arg_stream_name)
	{
		return (arg_stream_name in this._streams)
	}
	
	
	
	/**
	 * Add a subscriber socket.
	 * 
	 * @param {string} arg_stream_name - stream name.
	 * @param {object} arg_subscriber  - subscriber object.
	 * 
	 * @returns {boolean}
	 */
	subscribe(arg_stream_name, arg_subscriber)
	{
		if (arg_stream_name in this._streams)
		{
			const record = this._streams[arg_stream_name]
			record.subscribers.push(arg_subscriber)
			return true
		}

		return false
	}
	
	
	
	/**
	 * Remove a subscriber socket.
	 * 
	 * @param {string} arg_stream_name - stream name.
	 * @param {object} arg_subscriber  - subscriber object.
	 * @param {function} arg_subscriber_equal_fn - test function (default is (a,b)=>(a==b) ).
	 * 
	 * @returns {nothing}
	 */
	unsubscribe(arg_stream_name, arg_subscriber, arg_subscriber_equal_fn=(a,b)=>a==b)
	{
		if (arg_stream_name in this._streams)
		{
			const record = this._streams[arg_stream_name]
			
			_.remove(record.subscribers,
				(subscriber)=>{
					return arg_subscriber_equal_fn(subscriber, arg_subscriber)
				}
			)

			return true
		}

		return false
	}
	
	
	
	/**
	 * Post streams values to subscribers.
	 * 
	 * @param {string} arg_stream_name - stream name.
	 * @param {object} arg_datas - response values.
	 * 
	 * @returns {nothing}
	 */
	post_to_subscribers(arg_stream_name, arg_datas)
	{
		if (arg_stream_name in this._streams)
		{
			const record = this._streams[arg_stream_name]
			
			_.forEach(record.subscribers,
				(subscriber) => {
					this.post_to_subscriber(subscriber, arg_stream_name, arg_datas)
				}
			)
		}
	}
	
	
	
	/**
	 * Post streams values to one subscriber.
	 * @abstract
	 * 
	 * @param {object} arg_subscriber - subscriber object.
	 * @param {string} arg_stream_name - stream name.
	 * @param {object} arg_datas - response values.
	 * 
	 * @returns {nothing}
	 */
	post_to_subscriber(arg_subscriber, arg_stream_name, arg_datas)
	{
		console.log(context + ':post_to_subscriber:stream=[%s] subscriber=', arg_stream_name, arg_subscriber)
		console.log(context + ':post_to_subscriber:stream=[%s] datas=', arg_stream_name, arg_datas)
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T        from '../utils/types'
import BusEngine from './bus_engine'
import Stream   from './stream'


const context = 'common/messaging/stream_bus_engine'



/**
 * Stream based bus engine class for message bus client or server.
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* API:
*   ->constructor(arg_name,arg_settings,arg_log_context,arg_logger_manager).
* 
*	->channel_list():array                           - List engine channels.
*   ->channel_add(arg_channel):nothing               - Add a channel.
*   ->channel_send(arg_channel, arg_payload):nothing - Send a message into a channel.
*   ->channel_on(arg_channel, arg_handler):nothing   - Subscribe on channel inputs.
*   ->channel_transform(arg_in_channel, arg_out_channel, arg_xform_handler):nothing - Transform payload of input channel to output channel.
* 
 */
export default class StreamBusEngine extends BusEngine
{
	/**
	 * Create a bus.
	 * 
	 * @param {string} arg_name - instance name.
	 * @param {object} arg_settings - settings.
	 * @param {string} arg_log_context - trace context.
	 * @param {LoggerManager} arg_logger_manager - logger manager object (optional).
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_name, arg_settings, arg_log_context=context, arg_logger_manager=undefined)
	{
		super(arg_name, arg_settings, arg_log_context, arg_logger_manager)
		
		this.is_stream_bus_engine = true
		
		this._channels = {}
		// this._channels['default'] = new Stream()
		// this.channel_add('default')
	}
	

	
	/**
	 * List engine channels.
	 * 
	 * @returns {array}
	 */
	channel_list()
	{
		return Object.keys(this._channels)
	}
	

	
	/**
	 * Add a channel.
	 * 
	 * @param {string} arg_channel - channel name.
	 * 
	 * @returns {nothing}
	 */
	channel_add(arg_channel)
	{
		assert( T.isString(arg_channel), this.get_context() + ':channel_add:bad channel name')
		this._channels[arg_channel] = new Stream()
	}



	/**
	 * Send a message into a channel.
	 * 
	 * @param {string} arg_channel - channel name string.
	 * @param {object} arg_payload - payload data object.
	 * 
	 * @returns {nothing}
	 */
	channel_send(arg_channel, arg_payload)
	{
		assert( T.isString(arg_channel), this.get_context() + ':channel_send:bad channel name')
		assert( arg_payload, this.get_context() + ':channel_send:bad payload data')
		assert( arg_channel in this._channels, this.get_context() + ':channel_send:channel [' + arg_channel + '] stream not found')
		this._channels[arg_channel].push(arg_payload)
	}
	
	
	
	/**
	 * Subscribe on channel inputs.
	 * 
	 * @param {string} arg_channel - channel name string.
	 * @param {function} arg_handler - f(payload):nothing
	 * @param {function} arg_predicate - p(payload):boolean
	 * 
	 * @returns {nothing}
	 */
	channel_on(arg_channel, arg_handler, arg_predicate=undefined)
	{
		assert( T.isString(arg_channel), this.get_context() + ':channel_on:bad channel name on engine [' + this.get_name() + ']')
		assert( T.isFunction(arg_handler), this.get_context() + ':channel_on:bad handler function on engine [' + this.get_name() + '] for channel [' + arg_channel + ']')
		assert( arg_channel in this._channels, this.get_context() + ':channel_on:channel [' + arg_channel + '] stream not found on engine [' + this.get_name() + ']')
		this._channels[arg_channel].subscribe(
			(value) => {
				// console.log(context + ':subscribe:bus[' + this.get_name() + '] channel [' + arg_channel + '] received value', value)
				
				// FILTER BY PREDICATE
				if ( T.isFunction(arg_predicate) )
				{
					if ( arg_predicate(value) )
					{
						arg_handler(value)
						return
					}
				}
				
				// NO VALID PREDICATE
				arg_handler(value)
			}
		)
	}
}

