// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T        from '../utils/types'
import Instance from '../base/instance'
import { is_browser, is_server } from '../utils/is_browser'


/**
 * Contextual constant for this file logs.
 * @private
 * @type {string}
 */
let context = 'common/services/service_consumer_base'



/**
 * Service consumer base class.
 * @abstract
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* 	API:
* 		->get_service():Service - Get service instance.
* 
* 		->prepare_args(arg_operands):array - Enhance operands if needed (abstract).
* 
* 		->consume(...args):Promise - Consume datas from service producer.
* 
* 		->is_service_on_same_node():boolean - Is requested service on the same Node ?
* 
* 		->consume_from_server(arg_operands):Promise - Consume service on a server.
* 		->consume_from_browser(arg_operands):Promise - Consume a service from a browser.
* 
* 		->consume_local(arg_provider, arg_operands):Promise - Consume a service on the same host (abstract).
* 		->consume_remote(arg_operands):Promise - Consume a service on a remote host (abstract).
* 
 */
export default class ServiceConsumerBase extends Instance
{
	/**
	 * Create a service consumer.
	 * 
	 * @param {string} arg_consumer_name - consumer name.
	 * @param {Service} arg_service_instance - service instance.
	 * @param {string} arg_context - logging context label.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_consumer_name, arg_service_instance, arg_context)
	{
		assert( T.isString(arg_consumer_name), context + ':bad consumer name string')
		assert( T.isObject(arg_service_instance) && arg_service_instance.is_service, context + ':bad service object')
		
		super('svc_consumers', 'ServiceConsumer', arg_consumer_name, arg_service_instance.get_settings(), arg_context ? arg_context : context)
		
		this.is_service_consumer = true
		this.service = arg_service_instance
	}

	
	
	/**
	 * Get service instance.
	 * 
	 * @returns {Service}
	 */
	get_service()
	{
		return (T.isObject(this.service) && this.service.is_service) ? this.service : null
	}

	
	
	/**
	 * Enhance operands if needed.
	 * @abstract
	 * 
	 * @params {array} arg_operands - Variable list of operands.
	 * 
	 * @returns {array} - array of operands.
	 */
	prepare_args(arg_operands)
	{
		return arg_operands
	}

	
	
	/**
	 * Consume datas from service producer.
	 * 
	 * @params {...objects} args - Variable list of operands object.
	 * 
	 * @returns {Promise} - promise of results.
	 */
	consume(...args)
	{
		this.enter_group('consume')
		
		let promise = null
		
		try
		{
			const operands = this.prepare_args(args)

			// SERVER
			if ( is_server() )
			{
				this.info('svc consumer is on a server')

				promise = this.consume_from_server(operands)
				
				this.leave_group('consume')
				return promise
			}


			// BROWSER
			if ( is_browser() )
			{
				this.info('svc consumer is on a browser')

				promise = this.consume_from_browser(args)
				
				this.leave_group('consume')
				return promise
			}
		}
		catch(e)
		{
			this.error(context + ':consume', e)
		}

		promise = Promise.resolve(undefined)
		this.leave_group('consume')
		return promise
	}



	/**
	 * Is requested service on the same Node ?
	 * 
	 * @returns {boolean}
	 */
	is_service_on_same_node()
	{
		return true
	}


	
	/**
	 * Consume service on a server.
	 * 
	 * @params {array} arg_operands - operands object array.
	 * 
	 * @returns {Promise} - promise of results.
	 */
	consume_from_server(arg_operands)
	{
		this.enter_group('consume_on_server')
		

		if ( this.is_service_on_same_node() )
		{
			const service = this.get_service()
			assert( T.isObject(service) && service.is_service, context + ':consume_from_server:bad service object')
			
			const provider = service.get_provider()
			const promise = this.consume_local(provider, arg_operands)

			this.leave_group('consume_on_server:consume on the same Node')
			return promise
		}


		const promise = this.consume_remote(arg_operands)
		this.leave_group('consume_on_server:consume not on the same Node')
		return promise
	}

	
	
	/**
	 * Consume a service from a browser.
	 * 
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	consume_from_browser(arg_operands)
	{
		return this.consume_remote(arg_operands)
	}
	
	

	/**
	 * Consume a service on the same host.
	 * @abstract
	 * 
	 * @param {object} arg_provider - service provider.
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	/* eslint no-unused-vars: "off" */
	consume_local(arg_provider, arg_operands)
	{
		return Promise.resolve(undefined)
	}
	


	/**
	 * Consume a service on a remote host.
	 * @abstract
	 * 
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	/* eslint no-unused-vars: "off" */
	consume_remote(arg_operands)
	{
		return Promise.resolve(undefined)
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T                        from '../utils/types'
import { is_remote, is_locale } from '../utils/is_remote'
import ServiceConsumerBase from './service_consumer_base'


/**
 * Contextual constant for this file logs.
 * @private
 * @type {string}
 */
let context = 'common/services/service_consumer_by_url'



/**
 * Service consumer base class.
 * @abstract
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 */
export default class ServiceConsumerByUrl extends ServiceConsumerBase
{
	/**
	 * Create a service by url consumer.
	 * 
	 * @param {string} arg_consumer_name - consumer name.
	 * @param {Service} arg_service_instance - service instance.
	 * @param {string} arg_context - logging context label.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_consumer_name, arg_service_instance, arg_context)
	{
		super(arg_consumer_name, arg_service_instance, arg_context ? arg_context : context)
		
		this.is_service_consumer_by_url = true
	}

	
	
	/**
	 * Get consumed service server url.
	 * 
	 * @param {object} arg_provider - service provider.
	 * 
	 * @returns {string} - server url.
	 */
	get_server_url_for(arg_provider)
	{
		assert( T.isObject(arg_provider) && arg_provider.is_service_provider, context + ':get_url_for:bad provider object')

		const host = arg_provider.server.server_host
		const port = arg_provider.server.server_port
		const proto = arg_provider.server.server_protocole

		const url = proto + '://' + host + ':' + port
		this.debug('get_server_url_for', url)
		// console.log(context + ':get_url_for', url)

		return url
	}
	

	
	/**
	 * Get consumed service application url.
	 * 
	 * @param {object} arg_provider - service provider.
	 * 
	 * @returns {string} - service url.
	 */
	get_app_url_for(arg_provider)
	{
		let url = this.get_server_url_for(arg_provider)
		const app_url = arg_provider.application.app_url
		
		return url + '/' + app_url
	}
	

	
	/**
	 * Get consumed service url.
	 * 
	 * @param {object} arg_provider - service provider.
	 * @param {string|object} arg_operands - consumer operands as {url:...} (to consume a service with an url).
	 * 
	 * @returns {string} - service url.
	 */
	get_url_for(arg_provider, arg_operands)
	{
		let url = this.get_app_url_for(arg_provider)
		const opds_url = T.isString(arg_operands) ? arg_operands : (T.isObject(arg_operands) ? arg_operands.url : null)
		
		return url + (opds_url ? '/' + opds_url : '')
	}
	
	

	/**
	 * Consume a service on the same host.
	 * @abstract
	 * 
	 * @param {object} arg_provider - service provider.
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	/* eslint no-unused-vars: "off" */
	consume_local(arg_provider, arg_operands)
	{
		return Promise.resolve(undefined)
	}

	
	
	/**
	 * Consume service on a server.
	 * 
	 * @params {array} arg_operands - operands object array.
	 * 
	 * @returns {Promise} - promise of results.
	 */
	consume_from_server(arg_operands)
	{
		this.enter_group('consume_on_server')
		
		let promise = null
		
		const service = this.get_service()
		assert( T.isObject(service) && service.is_service, context + ':consume:bad service object')

		// TODO: QUESTION, should we always use the same producer for each consumer and register it to consumer.producer?
		// YES: less search, we need to work on the same producer during the step
		// NO: what about producer failure or overload
		const strategy = null
		const provider = service.get_a_provider(strategy)
		assert( T.isObject(provider), context + ':consume:bad service provider object')

		const host = provider.get_host()
		const port = provider.get_port()

		// LOCAL SAME SERVER
		if ( is_locale(host, port) )
		{
			this.info('svc consumer is on the same local server (host, port)')

			promise = this.consume_same_local_server(provider, arg_operands)
			
			this.leave_group('consume_on_server')
			return promise
		}

		// LOCAL OTHER SERVER
		if ( is_locale(host) )
		{
			this.info('svc consumer is on an other local server (host, port)')

			promise = this.consume_other_local_server(provider, arg_operands)
			
			this.leave_group('consume_on_server')
			return promise
		}

		// REMOTE SERVER
		if ( is_remote(host) )
		{
			this.info('svc consumer is on a remote server (host, port)')

			promise = this.consume_other_remote_server(provider, arg_operands)
			
			this.leave_group('consume_on_server')
			return promise
		}
		
		promise = Promise.resolve(undefined)
		this.leave_group('consume_on_server: failure')
		return promise
	}
	
	

	/**
	 * Consume a service on the same local server (same host, same port).
	 * 
	 * @param {object} arg_provider - service provider.
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	consume_same_local_server(arg_provider, arg_operands)
	{
		return this.consume_local(arg_operands)
	}
	
	

	/**
	 * Consume a service on an other local server (same host, other port)
	 * @param {object} arg_provider - service provider
	 * @param {array} arg_operands - consumer operands
	 * @returns {Promise} - promise of service results
	 */
	consume_other_local_server(arg_provider, arg_operands)
	{
		return this.consume_local(arg_operands)
	}
	
	

	/**
	 * Consume a service on an other remote server (other host).
	 * 
	 * @param {object} arg_provider - service provider.
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	consume_other_remote_server(arg_provider, arg_operands)
	{
		return this.consume_remote(arg_provider, arg_operands)
	}
	

	
	/**
	 * Consume a service from a browser.
	 * 
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	consume_from_browser(arg_operands)
	{
		return this.consume_remote(arg_operands)
	}


	
	/**
	 * Consume a service on a remote host
	 * @abstract
	 * 
	 * @param {object} arg_provider - service provider.
	 * @param {array} arg_operands - consumer operands.
	 * 
	 * @returns {Promise} - promise of service results.
	 */
	/* eslint no-unused-vars: "off" */
	consume_remote(arg_provider, arg_operands)
	{
		return Promise.resolve(undefined)
	}
}

// NPM IMPORTS
import assert from 'assert'

// COMMON IMPORTS
import T               from '../utils/types'
import StreamsProvider    from '../messaging/streams_provider'
import ServiceResponse from './service_response'


/**
 * Contextual constant for this file logs.
 * @private
 * @type {string}
 */
const context = 'common/services/service_provider'



/**
 * Service provider base class.
 * @abstract
 * 
 * @author Luc BORIES
 * @license Apache-2.0
 * 
 * @example
* 	API:
* 		->load():nothing - Load settings.
* 
* 		->activate(arg_application, arg_server, arg_app_svc_cfg):nothing - Activate a service feature for an application.
* 
* 		->produce():Promise - Produce service datas on request.
* 
* 		->get_host():string - Get host name of service server.
* 		->get_port():number - Get host port of service server.
* 
 */
export default class ServiceProvider extends StreamsProvider
{
	/**
	 * Create a service provider.
	 * 
	 * @param {string} arg_provider_name - consumer name.
	 * @param {Service} arg_service_instance - service instance.
	 * @param {string} arg_context - logging context label.
	 * 
	 * @returns {nothing}
	 */
	constructor(arg_provider_name, arg_service_instance, arg_context)
	{
		assert( T.isString(arg_provider_name), context + ':bad provider name string')
		assert( T.isObject(arg_service_instance) && arg_service_instance.is_service, context + ':bad service object')
		
		super('svc_providers', 'ServiceProvider', arg_provider_name, arg_service_instance.get_settings(), arg_context ? arg_context : context)
		
		this.is_service_provider = true
		
		this.service = arg_service_instance
		this.server = null
		this.application = null
		this.application_server = null

		
		// CREATE A STREAM WHICH RECEIVE VALUES TO SEND TO SUBSCRIBERS
		/*
		this.subscribers_sockets = []
		this.provided_values_stream = new Stream()
		if ( T.isFunction(this.init_provided_values_stream) )
		{
			this.init_provided_values_stream()
		}

		// ENABLE SEND VALUES TO SUBSCRIBERS HANDLER
		const post_cb = (v) => {
			// console.log(context + ':on stream value for provider %s',  arg_provider_name)
			this.post_provided_values_to_subscribers(v)
		}
		this.provided_values_stream.subscribe(post_cb)
		*/

		this.add_stream('default')
	}
	
	

	/**
	 * Load settings.
	 * @abstract
	 * 
	 * @returns {nothing}
	 */
	load()
	{
	}



	/**
	 * Get provider operations names.
	 * @abstract
	 * 
	 * @returns {array}
	 */
	get_operations_names()
	{
		return ['devapt-disconnect', 'devapt-subscribe', 'devapt-unsubscribe', 'devapt-subscription']
	}


	
	/**
	 * Produce service datas on request.
	 * 
	 * @param {ServiceRequest} arg_request - service request instance.
	 * 
	 * @returns {Promise} - promise of ServiceResponse instance.
	 */
	produce(arg_request)
	{
		const operation = arg_request.get_operation()
		const operands = arg_request.get_operands()
		const response = new ServiceResponse(arg_request)
		const opd_1 = operands.length > 0 ? operands[0] : undefined
		const opd_1_str = T.isNotEmptyString(opd_1) ? opd_1 : 'default'

		// SUBSCRIBE TO PROVIDER STREAM DATAS
		if (operation == 'devapt-disconnect')
		{
			const socket = arg_request.get_socket()
			
			if (socket)
			{
				this.unsubscribe(opd_1_str, socket)
				socket.disconnect(0)

				response.set_results( ['done'].concat(operands) )
				return Promise.resolve(response)
			}
			
			response.set_results( [ { error:'bad socket' } ].concat(operands) )
			return Promise.resolve(response)
		}

		// SUBSCRIBE TO PROVIDER STREAM DATAS
		if (operation == 'devapt-subscribe')
		{
			const socket = arg_request.get_socket()
			
			if (socket)
			{
				this.subscribe(opd_1_str, socket)

				response.set_results( ['done'].concat(operands) )
				return Promise.resolve(response)
			}
			
			response.set_results([ { error:'bad socket' } ].concat(operands) )
			return Promise.resolve(response)
		}

		// UNSUBSCRIBE FROM PROVIDER STREAM DATAS
		if (operation == 'devapt-unsubscribe')
		{
			const socket = arg_request.get_socket()
			
			if (socket)
			{
				this.unsubscribe(opd_1_str, socket)
				response.set_results( ['done'].concat(operands) )
				return Promise.resolve(response)
			}

			response.set_results( [ { error:'bad socket' } ].concat(operands) )
			return Promise.resolve(response)
		}

		// SUBSCRIPTION
		if (operation == 'devapt-subscription')
		{
			response.set_results(operands)
			return Promise.resolve(response)
		}

		response.set_has_error(true)
		response.set_error('produce:error:operation failure [' + operation + '] not found')
		response.set_results(undefined)
		return Promise.resolve(response)
	}
	

    
	/**
	 * Get host name of service server.
	 * 
	 * @returns {string} - service host name
	 */
	get_host()
	{
		return this.server.server_host
	}
	

	
	/**
	 * Get host port of service server.
	 * 
	 * @returns {number} - service host port
	 */
	get_port()
	{
		return this.server.server_port
	}
	
	
	
	/**
	 * Post streams values to one subscriber.
	 * 
	 * @param {object} arg_subscriber - subscriber object (Socket...).
	 * @param {string} arg_stream_name - stream name.
	 * @param {object} arg_datas - response values.
	 * 
	 * @returns {nothing}
	 */
	post_to_subscriber(arg_subscriber, arg_stream_name, arg_datas)
	{
		// DEBUG
		// console.log(context + ':post_to_subscriber:stream=[%s] subscriber=', arg_stream_name, arg_subscriber)
		// console.log(context + ':post_to_subscriber:stream=[%s] datas=', arg_stream_name, arg_datas)

		const svc_name = this.service.get_name()
		arg_subscriber.emit('devapt-subscription', { service:svc_name, operation:'devapt-subscription', results:['done', arg_datas] })
	}
}

