import assert from 'assert'

// COMMON IMPORTS
import T from '../utils/types'


const VALUES_OK = ['ok', 'true', '1', 1, 'enabled', 'valid', 'good']
const VALUES_KO = ['ko', 'false', '0', 0, null, 'disabled', 'unvalid', 'notvalid', 'bad']

const STATE_OK = 'OK'
const STATE_KO = 'KO'
const STATE_UNKNOW = 'UNKNOW'

const context = 'common/utils/tri_state'


export default class TriState
{
	constructor(arg_state)
	{
		if (arg_state !== undefined)
		{
			this.set_state(arg_state)
		}
	}
	
	
	set_state(arg_state)
	{
		assert( T.isString(arg_state) || T.isNumber(arg_state), context + ':bad state type' )
		arg_state = T.isString(arg_state) ? arg_state.toLocaleLowerCase() : arg_state
		
		if (arg_state in VALUES_OK)
		{
			this.state = STATE_OK
		}
		
		if (arg_state in VALUES_KO)
		{
			this.state = STATE_KO
		}
		
		this.state = STATE_UNKNOW
	}
	
	
	get_state()
	{
		return this.state
	}
	
	
	ok()
	{
		return this.state === STATE_OK
	}
	
	
	ko()
	{
		return this.state === STATE_KO
	}
	
	
	unknow()
	{
		return this.state === STATE_UNKNOW
	}
}

// Similar to typr module which has a problem with recent nodejs: lib-cov not found error.

var toStr = Object.prototype.toString
const types_fn = {}



const types = ['Function', 'Object', 'Date', 'Number', 'String', 'Boolean', 'RegExp', 'Arguments']
types.forEach(
	(type)=>{
		const expected = '[object ' + type + ']'
		types_fn['is' + type] = (o)=>{
			return toStr.call(o) === expected
		}
	}
)


types_fn.isNotEmptyString = (o)=>{
	return types_fn.isString(o) && o.length > 0
}


types_fn.isStringOrNumber = (o)=>{
	return types_fn.isString(o) || types_fn.isNumber(o)
}


types_fn.isNotEmptyStringOrNumber = (o)=>{
	return types_fn.isNotEmptyString(o) || types_fn.isNumber(o)
}


types_fn.isNotEmptyArray = (o)=>{
	return types_fn.isArray(o) && o.length > 0
}


types_fn.isClass = (o)=>{
	return types_fn.isFunction(o) /*&& types_fn.isFunction(o.constructor)*/ // TODO ENHANCE CLASS CHECKING
}


// DOM ELEMENT TEST
// For a tr element: "[object HTMLTableRowElement]"
types_fn.isElement = (o)=>{
	if (typeof o != 'object')
	{
		return false
	}
	const str = o.toString()
	return str.startsWith('[object HTML') && str.endsWith('Element]')
}


// DOM NODE LIST TEST
// For a tr element: "[object NodeList]"
types_fn.isNodeList = (o)=>{
	if (typeof o != 'object')
	{
		return false
	}
	const str = o.toString()
	return str == '[object NodeList]'
}


types_fn.isArray = Array.isArray
types_fn.isNaN = Number.isNaN
types_fn.isNumeric = Number.isFinite

types_fn.isInfinite = (n)=>{
	return Math.abs(n) === Infinity
}

types_fn.isNull = (o)=>{
	return o === null
}

types_fn.isUndefined = (o)=>{
	var undef
	return o === undef
}

export default types_fn

# Usage

To install a devapt application, follow:
```
cd apps/
git clone git://github.com/lucbories/devapt-app-testbus.git
cd devapt-app-testbus

npm install --save-prod
```

To create an empty application from scratch (not recommended for devapt newbies):

```
cd my_app
npm init
npm install devapt-core-common --save
npm install devapt-core-browser --save
npm install devapt-core-services --save
npm install devapt-core-server --save
```

Create folders tree:
```
-build
-dist
-public
-public/assets
-public/assets/css
-public/assets/html
-public/assets/img
-public/assets/js
-src/js
-src/resources
-tmp
```



